(1, '11111111', 'Refineries', 'assets/img/petrochemical-plants.jpg', '<h4>Hydrocarbons and Inorganic Scale</h4>\n<p>Distillation Condensers, Crude/Coker Preheat Trains, FC/Steam/Hydro-Crackers, Reforming, Hydrotreating, Water/Steam Scale, and much more.</p>', '', 'en', NULL, NULL),
(2, '11111111', '炼油厂', 'assets/img/petrochemical-plants.jpg', '<h4>碳氢化合物和无机垢</h4>\n<p>蒸馏冷凝器，原油/焦化器预热系统，FC /蒸汽/加氢裂化器，重整，加氢处理，水/蒸汽规模等。</p>', '', 'cn', NULL, NULL),
(3, '11111111', 'Koolwaterstoffen en anorganische ketelsteen', 'assets/img/petrochemical-plants.jpg', '<h4>Hydrocarbons and Inorganic Scale</h4>\n<p>Destillatiecondensors, Ruwe / Coker-voorverwarmtreinen, FC / Steam / Hydro-Crackers, Reforming, Hydrotreating, Water / Steam Scale, en nog veel meer.</p>', '', 'de', NULL, NULL),
(4, '11111111', 'Refinerías', 'assets/img/petrochemical-plants.jpg', '<h4>Hidrocarburos e incrustaciones inorgánicas</h4>\n<p>Condensadores de destilación, trenes de precalentamiento de crudo / coque, FC / vapor / hidrocraqueadores, reformado, hidrotratamiento, báscula de agua / vapor y mucho más.</p>', '', 'es-us', NULL, NULL),
(5, '11111111', 'Refinerías', 'assets/img/petrochemical-plants.jpg', '<h4>Hidrocarburos e incrustaciones inorgánicas</h4>\n<p>Condensadores de destilación, trenes de precalentamiento de crudo / coque, FC / vapor / hidrocraqueadores, reformado, hidrotratamiento, báscula de agua / vapor y mucho más.</p>', '', 'es', NULL, NULL),
(6, '11111111', 'Raffineries', 'assets/img/petrochemical-plants.jpg', '<h4>Hydrocarbures et échelle inorganique</h4>\n<p>Condenseurs de distillation, trains de préchauffage brut / cokeur, FC / vapeur / hydro-craquelins, reformage, hydrotraitement, balance eau / vapeur, et bien plus encore.</p>', '', 'fr', NULL, NULL),
(7, '11111111', 'रिफाइनरीज', 'assets/img/petrochemical-plants.jpg', '<h4>हाइड्रोकार्बन और अकार्बनिक स्केल</h4>\n<p>आसवन कंडेनसर, क्रूड / कोकर प्रीहेट ट्रेनें, एफसी / स्टीम / हाइड्रो-क्रैकर्स, रिफॉर्मिंग, हाइड्रोट्रीटिंग, वाटर / स्टीम स्केल, और बहुत कुछ।</p>', '', 'hi-in', NULL, NULL),
(8, '11111111', '製油所', 'assets/img/petrochemical-plants.jpg', '<h4>炭化水素と無機スケール</h4>\n<p>蒸留コンデンサー、原油/コーカー予熱トレイン、FC /蒸気/ハイドロクラッカー、改質、水素化処理、水/蒸気スケールなど。</p>', '', 'jp', NULL, NULL),
(9, '11111111', 'Penyulingan', 'assets/img/petrochemical-plants.jpg', '<h4>Hidrokarbon dan Skala Anorganik</h4>\n<p>Kondensor Penyulingan, Keretapi Panas Mentah / Coker, FC / Steam / Hydro-Crackers, Reforming, Hydrotreating, Water / Steam Scale, dan banyak lagi.</p>', '', 'ml', NULL, NULL),
(10, '11111111', 'Refinerías', 'assets/img/petrochemical-plants.jpg', '<h4>Hidrocarburos e incrustaciones inorgánicas</h4>\n<p>Condensadores de destilación, trenes de precalentamiento de crudo / coque, FC / vapor / hidrocraqueadores, reformado, hidrotratamiento, báscula de agua / vapor y mucho más.</p>', '', 'mx', NULL, NULL),
(11, '11111111', 'ਰਿਫਾਇਨਰੀਆਂ', 'assets/img/petrochemical-plants.jpg', '<h4>ਹਾਈਡਰੋਕਾਰਬਨ ਅਤੇ inorganic ਸਕੇਲ</h4>\n<p>ਡਿਸਟਿਲਲੇਸ਼ਨ ਕੰਡੈਂਸਰ, ਕਰੂਡ / ਕੋਕਰ ਪ੍ਰੀਹੀਟ ਟ੍ਰੇਨਾਂ, ਐਫਸੀ / ਭਾਫ / ਹਾਈਡ੍ਰੋ-ਕਰੈਕਰਸ, ਸੁਧਾਰ, ਹਾਈਡ੍ਰੋਟਰੈਰੇਟਿੰਗ, ਪਾਣੀ / ਭਾਫ ਸਕੇਲ, ਅਤੇ ਹੋਰ ਬਹੁਤ ਕੁਝ.</p>', '', 'pa-pk', NULL, NULL),
(12, '11111111', 'Refinarias', 'assets/img/petrochemical-plants.jpg', '<h4>Hidrocarbonetos e incrustações inorgânicas</h4>\n<p>Condensadores de destilação, trens de pré-aquecimento de petróleo / coque, FC / vapor / hidro-crackers, reforma, hidrotratamento, escala de água / vapor e muito mais.</p>', '', 'pt-br', NULL, NULL),
(13, '11111111', 'НПЗ', 'assets/img/petrochemical-plants.jpg', '<h4>Углеводороды и неорганические отложения</h4>\n<p>Конденсаторы дистилляции, линии предварительного нагрева сырой нефти / коксования, установки FC / пара / гидрокрекинга, риформинг, гидроочистка, водяной / паровой вес и многое другое.</p>', '', 'ru', NULL, NULL),
(14, '11111111', 'โรงกลั่น', 'assets/img/petrochemical-plants.jpg', '<h4>ไฮโดรคาร์บอนและอนินทรีย์</h4>\n<p>คอนเดนเซอร์กลั่น, น้ำมันดิบ / Coker อุ่นรถไฟ, FC / Steam / Hydro-Crackers, Reforming, Hydrotreating, Water / Steam Scale และอื่น ๆ อีกมากมาย</p>', '', 'th', NULL, NULL),
(15, '22222222', 'Petrochemical Plants', 'assets/img/petrochemical-plants.jpg', '<h4>Hydrocarbons and Fouling</h4>\n<p>Crack Gas Quenching, Bottoms, Fractionation Train, Preheat Trains, Water/Steam Scale.</p>', '', 'en', NULL, NULL),
(16, '22222222', '石化厂', 'assets/img/petrochemical-plants.jpg', '<h4>碳氢化合物和结垢</h4>\n<p>裂解气体淬火，底部，分馏塔，预热塔，水/蒸汽秤。</p>', '', 'cn', NULL, NULL),
(17, '22222222', 'Petrochemische fabrieken', 'assets/img/petrochemical-plants.jpg', '<h4>Koolwaterstoffen en vervuiling</h4>\n<p>Scheurgas blussen, bodems, fractioneringstrein, treinen voorverwarmen, water / stoomschaal.</p>', '', 'de', NULL, NULL),
(18, '22222222', 'Plantas Petroquímicas', 'assets/img/petrochemical-plants.jpg', '<h4>Hidrocarburos e incrustaciones</h4>\n<p>Enfriamiento de gas de grietas, fondos, tren de fraccionamiento, trenes de precalentamiento, báscula de agua / vapor.</p>', '', 'es-us', NULL, NULL),
(19, '22222222', 'Plantas Petroquímicas', 'assets/img/petrochemical-plants.jpg', '<h4>Hidrocarburos e incrustaciones</h4>\n<p>Enfriamiento de gas de grietas, fondos, tren de fraccionamiento, trenes de precalentamiento, báscula de agua / vapor.</p>', '', 'es', NULL, NULL),
(20, '22222222', 'Usines pétrochimiques', 'assets/img/petrochemical-plants.jpg', '<h4>Hydrocarbures et encrassement</h4>\n<p>Trempe au gaz de fissure, fonds, train de fractionnement, trains de préchauffage, balance eau / vapeur.</p>', '', 'fr', NULL, NULL),
(21, '22222222', 'पेट्रोकेमिकल पौधे', 'assets/img/petrochemical-plants.jpg', '<h4>हाइड्रोकार्बन और फॉलिंग</h4>\n<p>क्रैक गैस शमन, बॉटम्स, फ्रैक्चरेशन ट्रेन, प्रीहीट ट्रेनें, पानी / स्टीम स्केल।</p>', '', 'hi-in', NULL, NULL),
(22, '22222222', '石油化学プラント', 'assets/img/petrochemical-plants.jpg', '<h4>炭化水素とファウリング</h4>\n<p>クラックガス焼入れ、ボトムス、分留トレイン、予熱トレイン、水/蒸気スケール。</p>', '', 'jp', NULL, NULL),
(23, '22222222', 'Tumbuhan Petrokimia', 'assets/img/petrochemical-plants.jpg', '<h4>Hidrokarbon dan Fouling</h4>\n<p>Pemadaman Gas Retak, Bahagian Bawah, Keretapi Pecahan, Keretapi Panas, Skala Air / Wap.</p>', '', 'ml', NULL, NULL),
(24, '22222222', 'Plantas Petroquímicas', 'assets/img/petrochemical-plants.jpg', '<h4>Hidrocarburos e incrustaciones</h4>\n<p>Enfriamiento de gas de grietas, fondos, tren de fraccionamiento, trenes de precalentamiento, báscula de agua / vapor.</p>', '', 'mx', NULL, NULL),
(25, '22222222', 'ਪੈਟਰੋ ਕੈਮੀਕਲ ਪਲਾਂਟ', 'assets/img/petrochemical-plants.jpg', '<h4>ਹਾਈਡਰੋਕਾਰਬਨ ਅਤੇ ਫੂਲਿੰਗ</h4>\n<p>ਕਰੈਕ ਗੈਸ ਕੁਨੈਚਿੰਗ, ਤਲ਼ੇ, ਫਰੈਕਸ਼ਨਨ ਟ੍ਰੇਨ, ਪ੍ਰੀਹੀਟ ਟ੍ਰੇਨਾਂ, ਪਾਣੀ / ਭਾਫ ਸਕੇਲ.</p>', '', 'pa-pk', NULL, NULL),
