INSERT INTO `about_companies` VALUES ('58', '22222222', 'Company History', 'assets/img/tech-sonic-headquarter.jpg', '<p style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa;\"><span class=\"cangc3\" style=\"box-sizing: border-box; color: #227cbe; font-family: Impact; font-size: 1.15em !important;\">C</span><span class=\"cangc4\" style=\"box-sizing: border-box; color: #227cbe; font-family: Impact; font-size: 0.9em !important;\">LEAN</span>&nbsp;<span class=\"cangc3\" style=\"box-sizing: border-box; color: #227cbe; font-family: Impact; font-size: 1.15em !important;\">A</span><span class=\"cangc4\" style=\"box-sizing: border-box; color: #227cbe; font-family: Impact; font-size: 0.9em !important;\">S</span>&nbsp;<span class=\"cangc3\" style=\"box-sizing: border-box; color: #227cbe; font-family: Impact; font-size: 1.15em !important;\">N</span><span class=\"cangc4\" style=\"box-sizing: border-box; color: #227cbe; font-family: Impact; font-size: 0.9em !important;\">EW</span><span style=\"box-sizing: border-box; position: relative; font-size: 0.65em; line-height: 0; vertical-align: baseline; top: 0px !important; color: #227cbe; font-weight: 900;\">&reg;</span>&nbsp;is a subsidiary joint venture of&nbsp;<a style=\"box-sizing: border-box; color: #037cb3; text-decoration-line: none; background-color: transparent;\" href=\"https://techsonic.com/\" target=\"_blank\" rel=\"noopener\">Tech Sonic LP</a>. Tech Sonic got its start in the oil sands region of Canada as a hydroblasting company nearly 20 years ago, and began experimenting with ultrasonics in the early 2000&rsquo;s.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa;\">We invented the technology to clean heat exchangers using ultrasonics and since 2009, our clients have cleaned thousands of exchangers and many other parts all over the world.</p>\r\n<p style=