INSERT INTO `reviews` VALUES ('29', '33333333', '33333333', 'The Application of Industrial Scale Ultrasonic Cleaning to Heat Exchangers', '<h2>The Application of Industrial Scale Ultrasonic Cleaning to Heat Exchangers</h2>\r\n<p class=\"text-muted\">B. Kieser, R. Phillion and T. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Tube Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" /></a></p>\r\n<p>The cleaning of fouled heat exchangers often presents a significant challenge to the maintenance and operation of chemical, petroleum and food processes.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', 'assets/img/tech-sonic-vessel-sm.jpg', null, 'Referred Journal Publications', 'en', null, '2020-12-26 20:37:56');
INSERT INTO `reviews` VALUES ('30', '33333333', '33333333', '工业规模超声波清洗在热交换器中的应用', '<p>B. Kieser, R. Phillion, T. McCartney</p>\n            <p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><img style=\"height: auto;\" src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" alt=\"Tube Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" /></a></p>\n            <p>积垢的热交换器的清洁通常对化学，石油和食品加工的维护和操作提出了重大挑战。</p>\n            <p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><span style=\"color: #3598db;\">了解详情</span> &rarr;</a></p>', 'assets/img/tech-sonic-vessel-sm.jpg', null, 'Referred Journal Publications', 'cn', null, null);
INSERT INTO `reviews` VALUES ('31', '33333333', '33333333', 'De toepassing van ultrasone reiniging op industriële schaal op warmtewisselaars', '<p>B. Kieser, R. Phillion, T. McCartney</p>\n            <p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><img style=\"height: auto;\" src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" alt=\"Tube Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" /></a></p>\n            <p>Het reinigen van vervuilde warmtewisselaars vormt vaak een aanzienlijke uitdaging voor het onderhoud en de werking van chemische, petroleum- en voedselprocessen.</p>\n            <p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><span style=\"color: #3598db;\">Lees meer</span> &rarr;</a></p>', 'assets/img/tech-sonic-vessel-sm.jpg', null, 'Referred Journal Publications', 'de', null, null);
INSERT INTO `reviews` VALUES ('32', '33333333', '33333333', 'La aplicación de la limpieza ultrasónica a escala industrial a los intercambiadores de calor', '<p>B. Kieser, R. Phillion, T. McCartney</p>\n            <p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><img style=\"height: auto;\" src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" alt=\"Tube Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" /></a></p>\n            <p>La limpieza de intercambiadores de calor sucios a menudo presenta un desafío significativo para el mantenimiento y operación de procesos químicos, petroleros y alimentarios.</p>\n            <p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><span style=\"color: #3598db;\">Leer más</span> &rarr;</a></p>', 'assets/img/tech-sonic-vessel-sm.jpg', null, 'Referred Journal Publications', 'es-us', null, null);
INSERT INTO `reviews` VALUES ('33', '33333333', '33333333', 'La aplicación de la limpieza ultrasónica a escala industrial a los intercambiadores de calor', '<p>B. Kieser, R. Phillion, T. McCartney</p>\n            <p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><img style=\"height: auto;\" src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" alt=\"Tube Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" /></a></p>\n            <p>La limpieza de intercambiadores de calor sucios a menudo presenta un desafío significativo para el mantenimiento y operación de procesos químicos, petroleros y alimentarios.</p>\n            <p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><span style=\"color: #3598db;\">Leer más</span> &rarr;</a></p>', 'assets/img/tech-sonic-vessel-sm.jpg', null, 'Referred Journal Publications', 'es', null, null);
INSERT INTO `reviews` VALUES ('34', '33333333', '33333333', 'L\'application du nettoyage ultrasonique d\'échelle industrielle aux échangeurs de chaleur', '<p>B. Kieser, R. Phillion, T. McCartney</p>\n            <p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><img style=\"height: auto;\" src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" alt=\"Tube Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" /></a></p>\n            <p>Le nettoyage des échangeurs de chaleur encrassés présente souvent un défi important pour l\'entretien et le fonctionnement des procédés chimiques, pétroliers et alimentaires.</p>\n            <p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><span style=\"color: #3598db;\">En savoir plus</span> &rarr;</a></p>', 'assets/img/tech-sonic-vessel-sm.jpg', null, 'Referred Journal Publications', 'fr', null, null);
INSERT INTO `reviews` VALUES ('35', '33333333', '33333333', 'हीट एक्सचेंजर्स को औद्योगिक स्केल अल्ट्रासोनिक सफाई का अनुप्रयोग', '<p>B. Kieser, R. Phillion, T. McCartney</p>\n            <p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><img style=\"height: auto;\" src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" alt=\"Tube Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" /></a></p>\n            <p>फाउल्ड हीट एक्सचेंजर्स की सफाई अक्सर रासायनिक, पेट्रोलियम और खाद्य प्रक्रियाओं के रखरखाव और संचालन के लिए एक महत्वपूर्ण चुनौती प्रस्तुत करती है।</p>\n            <p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><span style=\"color: #3598db;\">और पढ़ें</span> &rarr;</a></p>', 'assets/img/tech-sonic-vessel-sm.jpg', null, 'Referred Journal Publications', 'hi-in', null, null);
INSERT INTO `reviews` VALUES ('36', '33333333', '33333333', '熱交換器への工業規模の超音波洗浄の応用', '<p>B. Kieser, R. Phillion, T. McCartney</p>\n            <p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><img style=\"height: auto;\" src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" alt=\"Tube Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" /></a></p>\n            <p>汚れた熱交換器の洗浄は、化学、石油、食品プロセスの保守と運用に重大な課題をもたらすことがよくあります。</p>\n            <p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><span style=\"color: #3598db;\">続きを読む</span> &rarr;</a></p>', 'assets/img/tech-sonic-vessel-sm.jpg', null, 'Referred Journal Publications', 'jp', null, null);
INSERT INTO `reviews` VALUES ('37', '33333333', '33333333', 'Aplikasi Pembersihan Ultrasonik Skala Industri Untuk Penukar Panas', '<p>B. Kieser, R. Phillion, T. McCartney</p>\n            <p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><img style=\"height: auto;\" src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" alt=\"Tube Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" /></a></p>\n            <p>Pembersihan penukar haba yang tercemar sering kali memberikan cabaran yang besar bagi penyelenggaraan dan pengendalian proses kimia, petroleum dan makanan.</p>\n            <p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><span style=\"color: #3598db;\">Baca lebih lanjut/span> &rarr;</a></p>', 'assets/img/tech-sonic-vessel-sm.jpg', null, 'Referred Journal Publications', 'ml', null, null);
INSERT INTO `reviews` VALUES ('38', '33333333', '33333333', 'La aplicación de la limpieza ultrasónica a escala industrial a los intercambiadores de calor', '<p>B. Kieser, R. Phillion, T. McCartney</p>\n            <p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><img style=\"height: auto;\" src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" alt=\"Tube Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" /></a></p>\n            <p>La limpieza de intercambiadores de calor sucios a menudo presenta un desafío significativo para el mantenimiento y operación de procesos químicos, petroleros y alimentarios.</p>\n            <p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><span style=\"color: #3598db;\">Leer más</span> &rarr;</a></p>', 'assets/img/tech-sonic-vessel-sm.jpg', null, 'Referred Journal Publications', 'mx', null, null);
INSERT INTO `reviews` VALUES ('39', '33333333', '33333333', 'ਉਦਯੋਗਿਕ ਪੈਮਾਨੇ ਦੀ ਅਲਟਰਾਸੋਨਿਕ ਸਫਾਈ ਦੀ ਹੀਟ ਐਕਸਚੇਂਜਰਾਂ ਲਈ ਉਪਯੋਗਤਾ', '<p>B. Kieser, R. Phillion, T. McCartney</p>\n            <p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><img style=\"height: auto;\" src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" alt=\"Tube Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" /></a></p>\n            <p>ਫਿledਲਡ ਹੀਟ ਐਕਸਚੇਂਜਰਾਂ ਦੀ ਸਫਾਈ ਅਕਸਰ ਰਸਾਇਣਕ, ਪੈਟਰੋਲੀਅਮ ਅਤੇ ਭੋਜਨ ਪ੍ਰਕਿਰਿਆਵਾਂ ਦੀ ਦੇਖਭਾਲ ਅਤੇ ਸੰਚਾਲਨ ਲਈ ਮਹੱਤਵਪੂਰਣ ਚੁਣੌਤੀ ਪੇਸ਼ ਕਰਦੀ ਹੈ.</p>\n            <p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><span style=\"color: #3598db;\">ਹੋਰ ਪੜ੍ਹੋ</span> &rarr;</a></p>', 'assets/img/tech-sonic-vessel-sm.jpg', null, 'Referred Journal Publications', 'pa-pk', null, null);
INSERT INTO `reviews` VALUES ('40', '33333333', '33333333', 'A aplicação de limpeza ultrassônica em escala industrial para trocadores de calor', '<p>B. Kieser, R. Phillion, T. McCartney</p>\n            <p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><img style=\"height: auto;\" src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" alt=\"Tube Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" /></a></p>\n            <p>A limpeza de trocadores de calor sujos frequentemente apresenta um desafio significativo para a manutenção e operação de processos químicos, petrolíferos e alimentícios.</p>\n            <p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><span style=\"color: #3598db;\">Leia mais</span> &rarr;</a></p>', 'assets/img/tech-sonic-vessel-sm.jpg', null, 'Referred Journal Publications', 'pt-br', null, null);
INSERT INTO `reviews` VALUES ('41', '33333333', '33333333', 'Применение ультразвуковой очистки в промышленных масштабах для теплообменников', '<p>B. Kieser, R. Phillion, T. McCartney</p>\n            <p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><img style=\"height: auto;\" src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" alt=\"Tube Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" /></a></p>\n            <p>Очистка загрязненных теплообменников часто представляет собой серьезную проблему для обслуживания и эксплуатации химических, нефтяных и пищевых производств.</p>\n            <p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><span style=\"color: #3598db;\">Узнать больше</span> &rarr;</a></p>', 'assets/img/tech-sonic-vessel-sm.jpg', null, 'Referred Journal Publications', 'ru', null, null);
INSERT INTO `reviews` VALUES ('42', '33333333', '33333333', 'การประยุกต์ใช้การทำความสะอาดอัลตราโซนิกในระดับอุตสาหกรรมกับเครื่องแลกเปลี่ยนความร้อน', '<p>B. Kieser, R. Phillion, T. McCartney</p>\n            <p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><img style=\"height: auto;\" src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" alt=\"Tube Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" /></a></p>\n            <p>การทำความสะอาดเครื่องแลกเปลี่ยนความร้อนที่เปรอะเปื้อนมักเป็นความท้าทายที่สำคัญในการบำรุงรักษาและการดำเนินการของกระบวนการทางเคมีปิโตรเลียมและอาหาร</p>\n            <p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><span style=\"color: #3598db;\">อ่านเพิ่มเติม</span> &rarr;</a></p>', 'assets/img/tech-sonic-vessel-sm.jpg', null, 'Referred Journal Publications', 'th', null, null);
INSERT INTO `reviews` VALUES ('51', 'o71Uy3sYSH', '11111111', 'Off-Line Ultrasonic Cleaning – Restoring As Built Performance During Turnarounds', '<p class=\"text-muted\" style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa; color: #6c757d !important;\">Gwen van de Bilt, Field Supervisor, Waste and Cleaning, Shell Moerdijk</p>\r\n<p><img class=\"img-fluid polaroid\" style=\"box-sizing: border-box; vertical-align: middle; border-style: none; max-width: 100%; height: auto; box-shadow: rgba(0, 0, 0, 0.2) 0px 4px 8px 0px, rgba(0, 0, 0, 0.19) 0px 6px 20px 0px; float: left; margin-bottom: 8.48438px; margin-left: 0px; font-family: Oswald, sans-serif; font-size: 16px; background-color: #f8f9fa;\" src=\"https://cleanasnew.com/img/before-and-after-filters-cleaned.jpg\" alt=\"Typical Wedge Wire filter for cleaning, multiple filters can be cleaned at the same time with only a low-pressure rinse.\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa;\">In April 2013 the Shell Moerdijk Chemicals plant conducted the biggest&ndash;ever TA event in the history of the site. One of the successes we had involved employing off-line ultrasonic cleaning.</p>', null, null, 'Case Studies', 'en', '2020-12-26 14:23:34', '2020-12-26 14:23:34');
INSERT INTO `reviews` VALUES ('52', 'c48qWa5AoR', '11111111', 'Tech Sonic Cleaning System Helps ConocoPhillips Neutralize and Clean Valves', '<h2>Tech Sonic Cleaning System Helps ConocoPhillips Neutralize<br />and Clean Valve</h2>\r\n<p class=\"text-muted\">&nbsp;</p>\r\n<p><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Monel Plug valve after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-valves-cleaned.jpg\" /></p>\r\n<p>ConocoPhillips is one of the largest refiners in the United States. Its Ponca City, Oklahoma refiner , with a crude oil processing capacity of 187,000 per day, processes a mixture of light, medium...</p>\r\n<p><a class=\"btn btn-default\" href=\"https://cleanasnew.com/case-studies/ConocoPhllips-Valve-Service.pdf\" target=\"_blank\" rel=\"noopener\">Download Case Study </a></p>', null, null, 'Case Studies', 'en', '2020-12-26 20:14:42', '2020-12-26 20:14:42');
INSERT INTO `reviews` VALUES ('53', 'GynfsMdkKN', '11111111', 'Upgrader Equipment Gets The Spa Treatment', '<h2>Upgrader Equipment Gets The Spa Treatment</h2>\r\n<p class=\"text-muted\">Tanya Ristoff, Communications Manager, Shell Scotford</p>\r\n<p><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Pictured here &ndash;an exchanger bundle is lowered into an ultrasonic bath by crane.\" data-src=\"https://cleanasnew.com/assets/img/scotford-upgrader-turnaround-case-study.jpg\" /></p>\r\n<p>At first glance, it looks like a big equipment soaker tub, but there&rsquo;s much more than meets the eye when it comes to Tech Sonic Services&rsquo; ultrasonic wash technology.</p>\r\n<p><a class=\"btn btn-default\" href=\"https://cleanasnew.com/case-studies/Scotford-Upgrader-Turnaround.pdf\" target=\"_blank\" rel=\"noopener\">Download Case Study </a></p>', null, null, 'Case Studies', 'en', '2020-12-26 20:15:47', '2020-12-26 20:15:47');
INSERT INTO `reviews` VALUES ('54', 'ZdgtQSLRDz', '22222222', 'Alfa Laval', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\" Compabloc heat exchanger before and after cleaning.\" data-src=\"/assets/img/feedback/AlfaLaval.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Alfa Laval, Business Manager:</p>\r\n<p class=\"quotation\">As a test Ulrik had our small test decanter into their shop for a 24 hour turn around. The equipment was immaculately cleaned within the required time requested with very little concern to do so on <span class=\"cangc3\">C</span><span class=\"cangc4\">LEAN</span> <span class=\"cangc3\">A</span><span class=\"cangc4\">S</span> <span class=\"cangc3\">N</span><span class=\"cangc4\">EW</span><sup>&reg;</sup>&rsquo;s process.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'en', '2020-12-26 20:24:58', '2020-12-26 20:24:58');
INSERT INTO `reviews` VALUES ('55', 'RjY7hZ1Vqe', '22222222', 'Exxon Mobil', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Like \'new\' heat exchanger.\" data-src=\"/assets/img/feedback/Exxon-heat-exchanger.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Exxon Mobil, Heat Team Lead:</p>\r\n<p class=\"quotation\">When we want our equipment &ldquo;really clean&rdquo; we send it to <span class=\"cangc3\">C</span><span class=\"cangc4\">LEAN</span> <span class=\"cangc3\">A</span><span class=\"cangc4\">S</span> <span class=\"cangc3\">N</span><span class=\"cangc4\">EW</span><sup>&reg;</sup>.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'en', '2020-12-26 20:25:32', '2020-12-26 20:25:32');
INSERT INTO `reviews` VALUES ('56', '3IHSkFB29u', '22222222', 'Versa Integrity', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Cleans hard to reach areas in a heat exchanger.\" data-src=\"/assets/img/feedback/Versa-integrity.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Versa Integrity, Operations Manager:</p>\r\n<p class=\"quotation\">The exchanger was so clean our guys could not tell that the exchanger had ever been in service.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'en', '2020-12-26 20:26:04', '2020-12-29 21:15:15');
INSERT INTO `reviews` VALUES ('57', 'Pk6FxVC5fj', '22222222', 'Dow Chemical', '<div class=\"col-4 float-left\"><img class=\"imimg-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean As New&reg; offers a faster cleaning, faster repair turnaround, and faster return to service.\" data-src=\"/assets/img/feedback/Dow.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Dow Chemical, Lead Turnaround Planner:</p>\r\n<p class=\"quotation\">Heard nothing but good things and we are in the process of planning our next outage. Promise if we can send it there to be cleaned, we will. Thanks again.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'en', '2020-12-26 20:26:55', '2020-12-26 20:26:55');
INSERT INTO `reviews` VALUES ('58', '5ZaVWOyXNn', '22222222', 'Shell Sarnia', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean As New&reg; cleans compabloc heat exchangers in a fraction of the time required for hydroblasting.\" data-src=\"/assets/img/feedback/shell-sarina-compabloc.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Sarnia, Refinery Manager:</p>\r\n<p class=\"quotation\">The Refinery decided to test clean four Compabloc units using the new process proposed by Tech Sonic. With the clean Compablocs back in operation, the refinery client captured the throughput and heat transfer data rates to reveal an annual (bpd) recovery of US$4.27M.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'en', '2020-12-26 20:27:22', '2020-12-26 20:27:22');
INSERT INTO `reviews` VALUES ('59', 'Tdsr8iqgOW', '22222222', 'Shell Scotford', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\" Cleans Tube Exchanger back to original specs.\" data-src=\"../assets/img/feedback/shell-scotford.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Scotford:</p>\r\n<p class=\"quotation\">We experienced excellent results with cleaning components for offsite repair/overhaul. 3rd party companies have never seen valves as clean as the ones coming from Scotford. Stainless steel bundles were cleaned better than expected and typically achieved IRIS quality. Bundles with other material composition were generally cleaned to Eddy Current quality. To achieve IRIS spec for these non-stainless items, some were rinsed with a 20Kpsi &amp; rotary lance.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'en', '2020-12-26 20:27:53', '2020-12-26 20:27:53');
INSERT INTO `reviews` VALUES ('60', 'ku0J6orG1c', '22222222', 'Shell Moerdijk', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Like \'new\' rotating equipment.\" data-src=\"/assets/img/feedback/shell-moerdijk-rotator.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Moerdijk, Production Cleaning Manager:</p>\r\n<p class=\"quotation\">Tech Sonic Cleaning Systems are twice as fast at half the cost of traditional hydroblasting. We achieve thorough, complete cleaning that is non-destructive compared to conventional applications.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'en', '2020-12-26 20:28:21', '2020-12-26 20:28:21');
INSERT INTO `reviews` VALUES ('61', 'VoiGD6RmEj', '22222222', 'Shell Global', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"A clean Shell Exchanger improves plant productivity.\" data-src=\"/assets/img/feedback/exchanger-bundles.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Global, Operations &amp; Technology Lead:</p>\r\n<p class=\"quotation\">This is a game changing technology in cleaning effectiveness. Exchanger bundles simply come out cleaner than when addressed with conventional technology. There is a better performance of the bundles on start-up, and these results are achieved in less cleaning time per bundle. This will allow us to be more aggressive in our turnaround schedules in the future. We typically see 70-80% recovery of performance with HP water jetting (caused by residual fouling on the shell side of the bundle). Now we see up to 100% of design heat transfer performance after start-up using the Tech Sonic applications.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'en', '2020-12-26 20:29:49', '2020-12-26 20:29:49');
INSERT INTO `reviews` VALUES ('62', 'Ek36HWw0nK', '22222222', 'Suncor Energy', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic technology is efficient and effective at cleaning dirty scaffolding.\" data-src=\"/assets/img/feedback/suncor-scaffolding.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Suncor Energy:</p>\r\n<p class=\"quotation\">For every dollar we spend with Tech Sonic, we save four dollars in operational and cost efficiencies. We used to have to throw out most of the components as they could not be put back into production. Our capital costs have reduced dramatically.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'en', '2020-12-26 20:30:23', '2020-12-26 20:30:23');
INSERT INTO `reviews` VALUES ('63', 'kfvsjpY5DM', '22222222', 'CF Industrie', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic systems can clean most exchangers for less than half of replacement cost.\" data-src=\"/assets/img/feedback/cf-industries-shell-exchanger.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From CF Industries, Production Manager:</p>\r\n<p class=\"quotation\">We achieved a 27% increase in production of Ammonia after Tech Sonic cleaned our C-109 process train heat exchangers.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'en', '2020-12-26 20:30:53', '2020-12-26 20:30:53');
INSERT INTO `reviews` VALUES ('64', '0gpYVfsHub', '22222222', 'Cenovus Energy', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic systems can clean most packing materials for less than half of replacement cost. \" data-src=\"/assets/img/feedback/cenovus-energy-bucket-pool.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Cenovus Energy, Turnaround Manager:</p>\r\n<p class=\"quotation\">Our flow rates improved from 160 to 260 meters cubed per hour after using Tech Sonic&rsquo;s innovative cleaning technologies. Truly amazing.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'en', '2020-12-26 20:31:20', '2020-12-29 21:12:54');
INSERT INTO `reviews` VALUES ('65', 'EWHeGkP4gy', '22222222', 'Nucor', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean heat exchangers require less energy and improves plant efficiencies.\" data-src=\"/assets/img/feedback/nucor.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Nucor:</p>\r\n<p class=\"quotation\">The heat exchanger that we cleaned Friday is operating very well. Overall heat transfer efficiency increased 50%.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'en', '2020-12-26 20:31:54', '2020-12-26 20:31:54');
INSERT INTO `reviews` VALUES ('66', '8QhLp93xAn', '33333333', 'From Practice-To-Theory-To-Practice: Advances in the Cleaning of Heat Exchangers Using Ultrasound', '<h2>From Practice-To-Theory-To-Practice: Advances in the Cleaning of Heat Exchangers Using Ultrasound</h2>\r\n<p class=\"text-muted\">B. Kieser, A. Goode and R. Philion</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/51_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"A typical exchanger being removed from the ultrasonic bath for inspection and rinsing.\" data-src=\"https://cleanasnew.com/assets/img/tech-sonic-cleaning.jpg\" /></a></p>\r\n<p>The cleaning of heat exchangers, and other large process equipment, using large-scale ultrasonic systems has, in the past few years, progressed from a &ldquo;curiosity&rdquo; to a proven, practical tool for many clients.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/51_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'en', '2020-12-26 20:38:46', '2020-12-26 20:38:46');
INSERT INTO `reviews` VALUES ('67', 'Rbw0omqBJ5', '33333333', 'Ultrasonic Cleaning of Badly Fouled Compabloc Exchangers', '<h2>Ultrasonic Cleaning of Badly Fouled Compabloc Exchangers</h2>\r\n<p class=\"text-muted\">R. Philion, B. Kieser and T. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/49_Kieser-PhilionSMCFL_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Compabloc exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-compablocs-cleaned.jpg\" /></a></p>\r\n<p>This paper describes the use of ultrasonic technologies to clean badly fouled Alfa Laval Compabloc&reg; 106 heat exchangers - which could not be effectively cleaned by conventional methods.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/49_Kieser-PhilionSMCFL_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'en', '2020-12-26 20:40:06', '2020-12-26 20:40:06');
INSERT INTO `reviews` VALUES ('68', 'Wczml95w8J', '33333333', 'Application of Stabilized Hydrogen Peroxide for the Removal of Iron Polysulfide Deposits', '<h2>Application of Stabilized Hydrogen Peroxide for the Removal of Iron Polysulfide Deposits</h2>\r\n<p class=\"text-muted\">T.R. McCartney and R. Shank</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/47_McCartney_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-exchangers-cleaned2.jpg\" /></a></p>\r\n<p>Hydrogen Peroxide is a strong oxidizing agent that could be used for the effective removal of the iron sulfide species.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/47_McCartney_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'en', '2020-12-26 20:45:46', '2020-12-26 20:45:46');
INSERT INTO `reviews` VALUES ('69', 'ireQIuqZ6B', '33333333', 'Understanding the Value of Cleaning - Gains From the Use of Ultrasonic Cleaning of Large Industrial Equipment', '<h2>Understanding the Value of Cleaning - Gains From the Use of Ultrasonic Cleaning of Large Industrial Equipment</h2>\r\n<p class=\"text-muted\">C. H. Schulz and B. Kieser</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2019/41_Schulz%20and%20Kieser.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" style=\"float: none;\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Example of soiling on Quench Oil Exchangers.\" data-src=\"https://cleanasnew.com/assets/img/dirty-heat-exchanger.jpg\" /></a></p>\r\n<p>We present the results of a study to determine the efficacy of large-scale ultrasonic cleaning of heat exchangers at Braskem.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2019/41_Schulz%20and%20Kieser.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'en', '2020-12-26 20:46:29', '2020-12-26 20:46:29');
INSERT INTO `reviews` VALUES ('70', '4ZGDROmkBX', '33333333', 'The Effective Removal of Feco3 Deposits From Critical Process Heat Exchangers Used in Ammonia Production Using a Proprietary Ultrasonic Chemical Cleaning Method', '<h2>The Effective Removal of Feco3 Deposits From Critical Process Heat Exchangers Used in Ammonia Production Using a Proprietary Ultrasonic Chemical Cleaning Method</h2>\r\n<p class=\"text-muted\">R. Philion and T.R. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/50_Philion_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-exchangers-cleaned3.jpg\" /></a></p>\r\n<p>This paper examines the problem of ferrous carbonate fouling and removal in lean - rich Diethanol amine exchangers at the CO2 extraction plants associated with ammonia manufacturing.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/50_Philion_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'en', '2020-12-26 20:47:36', '2020-12-27 17:07:13');
INSERT INTO `reviews` VALUES ('72', '11111111', '11111111', 'Removal Of Iron Carbonate Deposits From Heat Exchangers Used In Ammonia Production Using Tech Sonic Cleaning', '<h2><img style=\"height: auto;\" src=\"https://cleanasnew.com/assets/img/fouling.jpg\" alt=\"A sample of the FeCO3fouling removed from the tube side of the heat exchanger.\" data-src=\"https://cleanasnew.com/assets/img/fouling.jpg\" /></h2>\r\n            <p>The use of ultrasonic technology for cleaning refinery and chemical plant components has been undertaken by Tech Sonic since 2003.</p>', '', null, 'Case Studies', 'es-us', null, '2020-12-23 16:23:55');
INSERT INTO `reviews` VALUES ('73', '22222222', '22222222', 'Shell Deer Park', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../../assets/img/1px.png\" alt=\"Tech Sonic technology is efficient and effective at cleaning dirty heat exchanger. \" data-src=\"/assets/img/feedback/Shell-Deer-Park.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Deer Park, Turnaround Manager:</p>\r\n<p class=\"quotation\">I thought everything you guys did was awesome and have already been spreading recommendations to continue using you guys whenever we can!</p>\r\n</blockquote>\r\n</div>', '/assets/img/feedback/Shell-Deer-Park.jpg', 'Turnaround Manager', 'Feedback', 'es-us', null, '2020-12-26 20:24:26');
INSERT INTO `reviews` VALUES ('74', '33333333', '33333333', 'The Application of Industrial Scale Ultrasonic Cleaning to Heat Exchangers', '<h2>The Application of Industrial Scale Ultrasonic Cleaning to Heat Exchangers</h2>\r\n<p class=\"text-muted\">B. Kieser, R. Phillion and T. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Tube Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" /></a></p>\r\n<p>The cleaning of fouled heat exchangers often presents a significant challenge to the maintenance and operation of chemical, petroleum and food processes.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', 'assets/img/tech-sonic-vessel-sm.jpg', null, 'Referred Journal Publications', 'es-us', null, '2020-12-26 20:37:56');
INSERT INTO `reviews` VALUES ('75', 'o71Uy3sYSH', '11111111', 'Off-Line Ultrasonic Cleaning – Restoring As Built Performance During Turnarounds', '<p class=\"text-muted\" style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa; color: #6c757d !important;\">Gwen van de Bilt, Field Supervisor, Waste and Cleaning, Shell Moerdijk</p>\r\n<p><img class=\"img-fluid polaroid\" style=\"box-sizing: border-box; vertical-align: middle; border-style: none; max-width: 100%; height: auto; box-shadow: rgba(0, 0, 0, 0.2) 0px 4px 8px 0px, rgba(0, 0, 0, 0.19) 0px 6px 20px 0px; float: left; margin-bottom: 8.48438px; margin-left: 0px; font-family: Oswald, sans-serif; font-size: 16px; background-color: #f8f9fa;\" src=\"https://cleanasnew.com/img/before-and-after-filters-cleaned.jpg\" alt=\"Typical Wedge Wire filter for cleaning, multiple filters can be cleaned at the same time with only a low-pressure rinse.\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa;\">In April 2013 the Shell Moerdijk Chemicals plant conducted the biggest&ndash;ever TA event in the history of the site. One of the successes we had involved employing off-line ultrasonic cleaning.</p>', null, null, 'Case Studies', 'es-us', '2020-12-26 14:23:34', '2020-12-26 14:23:34');
INSERT INTO `reviews` VALUES ('76', 'c48qWa5AoR', '11111111', 'Tech Sonic Cleaning System Helps ConocoPhillips Neutralize and Clean Valves', '<h2>Tech Sonic Cleaning System Helps ConocoPhillips Neutralize<br />and Clean Valve</h2>\r\n<p class=\"text-muted\">&nbsp;</p>\r\n<p><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Monel Plug valve after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-valves-cleaned.jpg\" /></p>\r\n<p>ConocoPhillips is one of the largest refiners in the United States. Its Ponca City, Oklahoma refiner , with a crude oil processing capacity of 187,000 per day, processes a mixture of light, medium...</p>\r\n<p><a class=\"btn btn-default\" href=\"https://cleanasnew.com/case-studies/ConocoPhllips-Valve-Service.pdf\" target=\"_blank\" rel=\"noopener\">Download Case Study </a></p>', null, null, 'Case Studies', 'es-us', '2020-12-26 20:14:42', '2020-12-26 20:14:42');
INSERT INTO `reviews` VALUES ('77', 'GynfsMdkKN', '11111111', 'Upgrader Equipment Gets The Spa Treatment', '<h2>Upgrader Equipment Gets The Spa Treatment</h2>\r\n<p class=\"text-muted\">Tanya Ristoff, Communications Manager, Shell Scotford</p>\r\n<p><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Pictured here &ndash;an exchanger bundle is lowered into an ultrasonic bath by crane.\" data-src=\"https://cleanasnew.com/assets/img/scotford-upgrader-turnaround-case-study.jpg\" /></p>\r\n<p>At first glance, it looks like a big equipment soaker tub, but there&rsquo;s much more than meets the eye when it comes to Tech Sonic Services&rsquo; ultrasonic wash technology.</p>\r\n<p><a class=\"btn btn-default\" href=\"https://cleanasnew.com/case-studies/Scotford-Upgrader-Turnaround.pdf\" target=\"_blank\" rel=\"noopener\">Download Case Study </a></p>', null, null, 'Case Studies', 'es-us', '2020-12-26 20:15:47', '2020-12-26 20:15:47');
INSERT INTO `reviews` VALUES ('78', 'ZdgtQSLRDz', '22222222', 'Alfa Laval', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\" Compabloc heat exchanger before and after cleaning.\" data-src=\"/assets/img/feedback/AlfaLaval.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Alfa Laval, Business Manager:</p>\r\n<p class=\"quotation\">As a test Ulrik had our small test decanter into their shop for a 24 hour turn around. The equipment was immaculately cleaned within the required time requested with very little concern to do so on <span class=\"cangc3\">C</span><span class=\"cangc4\">LEAN</span> <span class=\"cangc3\">A</span><span class=\"cangc4\">S</span> <span class=\"cangc3\">N</span><span class=\"cangc4\">EW</span><sup>&reg;</sup>&rsquo;s process.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'es-us', '2020-12-26 20:24:58', '2020-12-26 20:24:58');
INSERT INTO `reviews` VALUES ('79', 'RjY7hZ1Vqe', '22222222', 'Exxon Mobil', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Like \'new\' heat exchanger.\" data-src=\"/assets/img/feedback/Exxon-heat-exchanger.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Exxon Mobil, Heat Team Lead:</p>\r\n<p class=\"quotation\">When we want our equipment &ldquo;really clean&rdquo; we send it to <span class=\"cangc3\">C</span><span class=\"cangc4\">LEAN</span> <span class=\"cangc3\">A</span><span class=\"cangc4\">S</span> <span class=\"cangc3\">N</span><span class=\"cangc4\">EW</span><sup>&reg;</sup>.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'es-us', '2020-12-26 20:25:32', '2020-12-26 20:25:32');
INSERT INTO `reviews` VALUES ('80', '3IHSkFB29u', '22222222', 'Versa Integrity', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Cleans hard to reach areas in a heat exchanger.\" data-src=\"/assets/img/feedback/Versa-integrity.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Versa Integrity, Operations Manager:</p>\r\n<p class=\"quotation\">The exchanger was so clean our guys could not tell that the exchanger had ever been in service.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'es-us', '2020-12-26 20:26:04', '2020-12-26 20:26:04');
INSERT INTO `reviews` VALUES ('81', 'Pk6FxVC5fj', '22222222', 'Dow Chemical', '<div class=\"col-4 float-left\"><img class=\"imimg-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean As New&reg; offers a faster cleaning, faster repair turnaround, and faster return to service.\" data-src=\"/assets/img/feedback/Dow.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Dow Chemical, Lead Turnaround Planner:</p>\r\n<p class=\"quotation\">Heard nothing but good things and we are in the process of planning our next outage. Promise if we can send it there to be cleaned, we will. Thanks again.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'es-us', '2020-12-26 20:26:55', '2020-12-26 20:26:55');
INSERT INTO `reviews` VALUES ('82', '5ZaVWOyXNn', '22222222', 'Shell Sarnia', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean As New&reg; cleans compabloc heat exchangers in a fraction of the time required for hydroblasting.\" data-src=\"/assets/img/feedback/shell-sarina-compabloc.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Sarnia, Refinery Manager:</p>\r\n<p class=\"quotation\">The Refinery decided to test clean four Compabloc units using the new process proposed by Tech Sonic. With the clean Compablocs back in operation, the refinery client captured the throughput and heat transfer data rates to reveal an annual (bpd) recovery of US$4.27M.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'es-us', '2020-12-26 20:27:22', '2020-12-26 20:27:22');
INSERT INTO `reviews` VALUES ('83', 'Tdsr8iqgOW', '22222222', 'Shell Scotford', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\" Cleans Tube Exchanger back to original specs.\" data-src=\"../assets/img/feedback/shell-scotford.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Scotford:</p>\r\n<p class=\"quotation\">We experienced excellent results with cleaning components for offsite repair/overhaul. 3rd party companies have never seen valves as clean as the ones coming from Scotford. Stainless steel bundles were cleaned better than expected and typically achieved IRIS quality. Bundles with other material composition were generally cleaned to Eddy Current quality. To achieve IRIS spec for these non-stainless items, some were rinsed with a 20Kpsi &amp; rotary lance.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'es-us', '2020-12-26 20:27:53', '2020-12-26 20:27:53');
INSERT INTO `reviews` VALUES ('84', 'ku0J6orG1c', '22222222', 'Shell Moerdijk', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Like \'new\' rotating equipment.\" data-src=\"/assets/img/feedback/shell-moerdijk-rotator.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Moerdijk, Production Cleaning Manager:</p>\r\n<p class=\"quotation\">Tech Sonic Cleaning Systems are twice as fast at half the cost of traditional hydroblasting. We achieve thorough, complete cleaning that is non-destructive compared to conventional applications.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'es-us', '2020-12-26 20:28:21', '2020-12-26 20:28:21');
INSERT INTO `reviews` VALUES ('85', 'VoiGD6RmEj', '22222222', 'Shell Global', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"A clean Shell Exchanger improves plant productivity.\" data-src=\"/assets/img/feedback/exchanger-bundles.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Global, Operations &amp; Technology Lead:</p>\r\n<p class=\"quotation\">This is a game changing technology in cleaning effectiveness. Exchanger bundles simply come out cleaner than when addressed with conventional technology. There is a better performance of the bundles on start-up, and these results are achieved in less cleaning time per bundle. This will allow us to be more aggressive in our turnaround schedules in the future. We typically see 70-80% recovery of performance with HP water jetting (caused by residual fouling on the shell side of the bundle). Now we see up to 100% of design heat transfer performance after start-up using the Tech Sonic applications.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'es-us', '2020-12-26 20:29:49', '2020-12-26 20:29:49');
INSERT INTO `reviews` VALUES ('86', 'Ek36HWw0nK', '22222222', 'Suncor Energy', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic technology is efficient and effective at cleaning dirty scaffolding.\" data-src=\"/assets/img/feedback/suncor-scaffolding.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Suncor Energy:</p>\r\n<p class=\"quotation\">For every dollar we spend with Tech Sonic, we save four dollars in operational and cost efficiencies. We used to have to throw out most of the components as they could not be put back into production. Our capital costs have reduced dramatically.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'es-us', '2020-12-26 20:30:23', '2020-12-26 20:30:23');
INSERT INTO `reviews` VALUES ('87', 'kfvsjpY5DM', '22222222', 'CF Industrie', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic systems can clean most exchangers for less than half of replacement cost.\" data-src=\"/assets/img/feedback/cf-industries-shell-exchanger.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From CF Industries, Production Manager:</p>\r\n<p class=\"quotation\">We achieved a 27% increase in production of Ammonia after Tech Sonic cleaned our C-109 process train heat exchangers.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'es-us', '2020-12-26 20:30:53', '2020-12-26 20:30:53');
INSERT INTO `reviews` VALUES ('88', '0gpYVfsHub', '22222222', 'Cenovus Energy', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic systems can clean most packing materials for less than half of replacement cost. \" data-src=\"/assets/img/feedback/cenovus-energy-bucket-pool.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Cenovus Energy, Turnaround Manager:</p>\r\n<p class=\"quotation\">Our flow rates improved from 160 to 260 meters cubed per hour after using Tech Sonic&rsquo;s innovative cleaning technologies. Truly amazing.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'es-us', '2020-12-26 20:31:20', '2020-12-26 20:31:20');
INSERT INTO `reviews` VALUES ('89', 'EWHeGkP4gy', '22222222', 'Nucor', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean heat exchangers require less energy and improves plant efficiencies.\" data-src=\"/assets/img/feedback/nucor.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Nucor:</p>\r\n<p class=\"quotation\">The heat exchanger that we cleaned Friday is operating very well. Overall heat transfer efficiency increased 50%.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'es-us', '2020-12-26 20:31:54', '2020-12-26 20:31:54');
INSERT INTO `reviews` VALUES ('90', '8QhLp93xAn', '33333333', 'From Practice-To-Theory-To-Practice: Advances in the Cleaning of Heat Exchangers Using Ultrasound', '<h2>From Practice-To-Theory-To-Practice: Advances in the Cleaning of Heat Exchangers Using Ultrasound</h2>\r\n<p class=\"text-muted\">B. Kieser, A. Goode and R. Philion</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/51_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"A typical exchanger being removed from the ultrasonic bath for inspection and rinsing.\" data-src=\"https://cleanasnew.com/assets/img/tech-sonic-cleaning.jpg\" /></a></p>\r\n<p>The cleaning of heat exchangers, and other large process equipment, using large-scale ultrasonic systems has, in the past few years, progressed from a &ldquo;curiosity&rdquo; to a proven, practical tool for many clients.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/51_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'es-us', '2020-12-26 20:38:46', '2020-12-26 20:38:46');
INSERT INTO `reviews` VALUES ('91', 'Rbw0omqBJ5', '33333333', 'Ultrasonic Cleaning of Badly Fouled Compabloc Exchangers', '<h2>Ultrasonic Cleaning of Badly Fouled Compabloc Exchangers</h2>\r\n<p class=\"text-muted\">R. Philion, B. Kieser and T. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/49_Kieser-PhilionSMCFL_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Compabloc exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-compablocs-cleaned.jpg\" /></a></p>\r\n<p>This paper describes the use of ultrasonic technologies to clean badly fouled Alfa Laval Compabloc&reg; 106 heat exchangers - which could not be effectively cleaned by conventional methods.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/49_Kieser-PhilionSMCFL_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'es-us', '2020-12-26 20:40:06', '2020-12-26 20:40:06');
INSERT INTO `reviews` VALUES ('92', 'Wczml95w8J', '33333333', 'Application of Stabilized Hydrogen Peroxide for the Removal of Iron Polysulfide Deposits', '<h2>Application of Stabilized Hydrogen Peroxide for the Removal of Iron Polysulfide Deposits</h2>\r\n<p class=\"text-muted\">T.R. McCartney and R. Shank</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/47_McCartney_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-exchangers-cleaned2.jpg\" /></a></p>\r\n<p>Hydrogen Peroxide is a strong oxidizing agent that could be used for the effective removal of the iron sulfide species.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/47_McCartney_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'es-us', '2020-12-26 20:45:46', '2020-12-26 20:45:46');
INSERT INTO `reviews` VALUES ('93', 'ireQIuqZ6B', '33333333', 'Understanding the Value of Cleaning - Gains From the Use of Ultrasonic Cleaning of Large Industrial Equipment', '<h2>Understanding the Value of Cleaning - Gains From the Use of Ultrasonic Cleaning of Large Industrial Equipment</h2>\r\n<p class=\"text-muted\">C. H. Schulz and B. Kieser</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2019/41_Schulz%20and%20Kieser.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" style=\"float: none;\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Example of soiling on Quench Oil Exchangers.\" data-src=\"https://cleanasnew.com/assets/img/dirty-heat-exchanger.jpg\" /></a></p>\r\n<p>We present the results of a study to determine the efficacy of large-scale ultrasonic cleaning of heat exchangers at Braskem.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2019/41_Schulz%20and%20Kieser.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'es-us', '2020-12-26 20:46:29', '2020-12-26 20:46:29');
INSERT INTO `reviews` VALUES ('94', '4ZGDROmkBX', '33333333', 'The Effective Removal of Feco3 Deposits From Critical Process Heat Exchangers Used in Ammonia Production Using a Proprietary Ultrasonic Chemical Cleaning Method', '<h2>The Effective Removal of Feco3 Deposits From Critical Process Heat Exchangers Used in Ammonia Production Using a Proprietary Ultrasonic Chemical Cleaning Method</h2>\r\n<p class=\"text-muted\">R. Philion and T.R. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/50_Philion_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-exchangers-cleaned3.jpg\" /></a></p>\r\n<p>This paper examines the problem of ferrous carbonate fouling and removal in lean - rich Diethanol amine exchangers at the CO2 extraction plants associated with ammonia manufacturing.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/50_Philion_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'es-us', '2020-12-26 20:47:36', '2020-12-26 20:47:36');
INSERT INTO `reviews` VALUES ('103', '11111111', '11111111', 'Removal Of Iron Carbonate Deposits From Heat Exchangers Used In Ammonia Production Using Tech Sonic Cleaning', '<h2><img style=\"height: auto;\" src=\"https://cleanasnew.com/assets/img/fouling.jpg\" alt=\"A sample of the FeCO3fouling removed from the tube side of the heat exchanger.\" data-src=\"https://cleanasnew.com/assets/img/fouling.jpg\" /></h2>\r\n            <p>The use of ultrasonic technology for cleaning refinery and chemical plant components has been undertaken by Tech Sonic since 2003.</p>', '', null, 'Case Studies', 'mx', null, '2020-12-23 16:23:55');
INSERT INTO `reviews` VALUES ('104', '22222222', '22222222', 'Shell Deer Park', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../../assets/img/1px.png\" alt=\"Tech Sonic technology is efficient and effective at cleaning dirty heat exchanger. \" data-src=\"/assets/img/feedback/Shell-Deer-Park.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Deer Park, Turnaround Manager:</p>\r\n<p class=\"quotation\">I thought everything you guys did was awesome and have already been spreading recommendations to continue using you guys whenever we can!</p>\r\n</blockquote>\r\n</div>', '/assets/img/feedback/Shell-Deer-Park.jpg', 'Turnaround Manager', 'Feedback', 'mx', null, '2020-12-26 20:24:26');
INSERT INTO `reviews` VALUES ('105', '33333333', '33333333', 'The Application of Industrial Scale Ultrasonic Cleaning to Heat Exchangers', '<h2>The Application of Industrial Scale Ultrasonic Cleaning to Heat Exchangers</h2>\r\n<p class=\"text-muted\">B. Kieser, R. Phillion and T. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Tube Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" /></a></p>\r\n<p>The cleaning of fouled heat exchangers often presents a significant challenge to the maintenance and operation of chemical, petroleum and food processes.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', 'assets/img/tech-sonic-vessel-sm.jpg', null, 'Referred Journal Publications', 'mx', null, '2020-12-26 20:37:56');
INSERT INTO `reviews` VALUES ('106', 'o71Uy3sYSH', '11111111', 'Off-Line Ultrasonic Cleaning – Restoring As Built Performance During Turnarounds', '<p class=\"text-muted\" style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa; color: #6c757d !important;\">Gwen van de Bilt, Field Supervisor, Waste and Cleaning, Shell Moerdijk</p>\r\n<p><img class=\"img-fluid polaroid\" style=\"box-sizing: border-box; vertical-align: middle; border-style: none; max-width: 100%; height: auto; box-shadow: rgba(0, 0, 0, 0.2) 0px 4px 8px 0px, rgba(0, 0, 0, 0.19) 0px 6px 20px 0px; float: left; margin-bottom: 8.48438px; margin-left: 0px; font-family: Oswald, sans-serif; font-size: 16px; background-color: #f8f9fa;\" src=\"https://cleanasnew.com/img/before-and-after-filters-cleaned.jpg\" alt=\"Typical Wedge Wire filter for cleaning, multiple filters can be cleaned at the same time with only a low-pressure rinse.\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa;\">In April 2013 the Shell Moerdijk Chemicals plant conducted the biggest&ndash;ever TA event in the history of the site. One of the successes we had involved employing off-line ultrasonic cleaning.</p>', null, null, 'Case Studies', 'mx', '2020-12-26 14:23:34', '2020-12-26 14:23:34');
INSERT INTO `reviews` VALUES ('107', 'c48qWa5AoR', '11111111', 'Tech Sonic Cleaning System Helps ConocoPhillips Neutralize and Clean Valves', '<h2>Tech Sonic Cleaning System Helps ConocoPhillips Neutralize<br />and Clean Valve</h2>\r\n<p class=\"text-muted\">&nbsp;</p>\r\n<p><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Monel Plug valve after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-valves-cleaned.jpg\" /></p>\r\n<p>ConocoPhillips is one of the largest refiners in the United States. Its Ponca City, Oklahoma refiner , with a crude oil processing capacity of 187,000 per day, processes a mixture of light, medium...</p>\r\n<p><a class=\"btn btn-default\" href=\"https://cleanasnew.com/case-studies/ConocoPhllips-Valve-Service.pdf\" target=\"_blank\" rel=\"noopener\">Download Case Study </a></p>', null, null, 'Case Studies', 'mx', '2020-12-26 20:14:42', '2020-12-26 20:14:42');
INSERT INTO `reviews` VALUES ('108', 'GynfsMdkKN', '11111111', 'Upgrader Equipment Gets The Spa Treatment', '<h2>Upgrader Equipment Gets The Spa Treatment</h2>\r\n<p class=\"text-muted\">Tanya Ristoff, Communications Manager, Shell Scotford</p>\r\n<p><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Pictured here &ndash;an exchanger bundle is lowered into an ultrasonic bath by crane.\" data-src=\"https://cleanasnew.com/assets/img/scotford-upgrader-turnaround-case-study.jpg\" /></p>\r\n<p>At first glance, it looks like a big equipment soaker tub, but there&rsquo;s much more than meets the eye when it comes to Tech Sonic Services&rsquo; ultrasonic wash technology.</p>\r\n<p><a class=\"btn btn-default\" href=\"https://cleanasnew.com/case-studies/Scotford-Upgrader-Turnaround.pdf\" target=\"_blank\" rel=\"noopener\">Download Case Study </a></p>', null, null, 'Case Studies', 'mx', '2020-12-26 20:15:47', '2020-12-26 20:15:47');
INSERT INTO `reviews` VALUES ('109', 'ZdgtQSLRDz', '22222222', 'Alfa Laval', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\" Compabloc heat exchanger before and after cleaning.\" data-src=\"/assets/img/feedback/AlfaLaval.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Alfa Laval, Business Manager:</p>\r\n<p class=\"quotation\">As a test Ulrik had our small test decanter into their shop for a 24 hour turn around. The equipment was immaculately cleaned within the required time requested with very little concern to do so on <span class=\"cangc3\">C</span><span class=\"cangc4\">LEAN</span> <span class=\"cangc3\">A</span><span class=\"cangc4\">S</span> <span class=\"cangc3\">N</span><span class=\"cangc4\">EW</span><sup>&reg;</sup>&rsquo;s process.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'mx', '2020-12-26 20:24:58', '2020-12-26 20:24:58');
INSERT INTO `reviews` VALUES ('110', 'RjY7hZ1Vqe', '22222222', 'Exxon Mobil', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Like \'new\' heat exchanger.\" data-src=\"/assets/img/feedback/Exxon-heat-exchanger.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Exxon Mobil, Heat Team Lead:</p>\r\n<p class=\"quotation\">When we want our equipment &ldquo;really clean&rdquo; we send it to <span class=\"cangc3\">C</span><span class=\"cangc4\">LEAN</span> <span class=\"cangc3\">A</span><span class=\"cangc4\">S</span> <span class=\"cangc3\">N</span><span class=\"cangc4\">EW</span><sup>&reg;</sup>.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'mx', '2020-12-26 20:25:32', '2020-12-26 20:25:32');
INSERT INTO `reviews` VALUES ('111', '3IHSkFB29u', '22222222', 'Versa Integrity', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Cleans hard to reach areas in a heat exchanger.\" data-src=\"/assets/img/feedback/Versa-integrity.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Versa Integrity, Operations Manager:</p>\r\n<p class=\"quotation\">The exchanger was so clean our guys could not tell that the exchanger had ever been in service.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'mx', '2020-12-26 20:26:04', '2020-12-26 20:26:04');
INSERT INTO `reviews` VALUES ('112', 'Pk6FxVC5fj', '22222222', 'Dow Chemical', '<div class=\"col-4 float-left\"><img class=\"imimg-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean As New&reg; offers a faster cleaning, faster repair turnaround, and faster return to service.\" data-src=\"/assets/img/feedback/Dow.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Dow Chemical, Lead Turnaround Planner:</p>\r\n<p class=\"quotation\">Heard nothing but good things and we are in the process of planning our next outage. Promise if we can send it there to be cleaned, we will. Thanks again.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'mx', '2020-12-26 20:26:55', '2020-12-26 20:26:55');
INSERT INTO `reviews` VALUES ('113', '5ZaVWOyXNn', '22222222', 'Shell Sarnia', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean As New&reg; cleans compabloc heat exchangers in a fraction of the time required for hydroblasting.\" data-src=\"/assets/img/feedback/shell-sarina-compabloc.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Sarnia, Refinery Manager:</p>\r\n<p class=\"quotation\">The Refinery decided to test clean four Compabloc units using the new process proposed by Tech Sonic. With the clean Compablocs back in operation, the refinery client captured the throughput and heat transfer data rates to reveal an annual (bpd) recovery of US$4.27M.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'mx', '2020-12-26 20:27:22', '2020-12-26 20:27:22');
INSERT INTO `reviews` VALUES ('114', 'Tdsr8iqgOW', '22222222', 'Shell Scotford', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\" Cleans Tube Exchanger back to original specs.\" data-src=\"../assets/img/feedback/shell-scotford.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Scotford:</p>\r\n<p class=\"quotation\">We experienced excellent results with cleaning components for offsite repair/overhaul. 3rd party companies have never seen valves as clean as the ones coming from Scotford. Stainless steel bundles were cleaned better than expected and typically achieved IRIS quality. Bundles with other material composition were generally cleaned to Eddy Current quality. To achieve IRIS spec for these non-stainless items, some were rinsed with a 20Kpsi &amp; rotary lance.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'mx', '2020-12-26 20:27:53', '2020-12-26 20:27:53');
INSERT INTO `reviews` VALUES ('115', 'ku0J6orG1c', '22222222', 'Shell Moerdijk', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Like \'new\' rotating equipment.\" data-src=\"/assets/img/feedback/shell-moerdijk-rotator.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Moerdijk, Production Cleaning Manager:</p>\r\n<p class=\"quotation\">Tech Sonic Cleaning Systems are twice as fast at half the cost of traditional hydroblasting. We achieve thorough, complete cleaning that is non-destructive compared to conventional applications.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'mx', '2020-12-26 20:28:21', '2020-12-26 20:28:21');
INSERT INTO `reviews` VALUES ('116', 'VoiGD6RmEj', '22222222', 'Shell Global', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"A clean Shell Exchanger improves plant productivity.\" data-src=\"/assets/img/feedback/exchanger-bundles.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Global, Operations &amp; Technology Lead:</p>\r\n<p class=\"quotation\">This is a game changing technology in cleaning effectiveness. Exchanger bundles simply come out cleaner than when addressed with conventional technology. There is a better performance of the bundles on start-up, and these results are achieved in less cleaning time per bundle. This will allow us to be more aggressive in our turnaround schedules in the future. We typically see 70-80% recovery of performance with HP water jetting (caused by residual fouling on the shell side of the bundle). Now we see up to 100% of design heat transfer performance after start-up using the Tech Sonic applications.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'mx', '2020-12-26 20:29:49', '2020-12-26 20:29:49');
INSERT INTO `reviews` VALUES ('117', 'Ek36HWw0nK', '22222222', 'Suncor Energy', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic technology is efficient and effective at cleaning dirty scaffolding.\" data-src=\"/assets/img/feedback/suncor-scaffolding.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Suncor Energy:</p>\r\n<p class=\"quotation\">For every dollar we spend with Tech Sonic, we save four dollars in operational and cost efficiencies. We used to have to throw out most of the components as they could not be put back into production. Our capital costs have reduced dramatically.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'mx', '2020-12-26 20:30:23', '2020-12-26 20:30:23');
INSERT INTO `reviews` VALUES ('118', 'kfvsjpY5DM', '22222222', 'CF Industrie', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic systems can clean most exchangers for less than half of replacement cost.\" data-src=\"/assets/img/feedback/cf-industries-shell-exchanger.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From CF Industries, Production Manager:</p>\r\n<p class=\"quotation\">We achieved a 27% increase in production of Ammonia after Tech Sonic cleaned our C-109 process train heat exchangers.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'mx', '2020-12-26 20:30:53', '2020-12-26 20:30:53');
INSERT INTO `reviews` VALUES ('119', '0gpYVfsHub', '22222222', 'Cenovus Energy', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic systems can clean most packing materials for less than half of replacement cost. \" data-src=\"/assets/img/feedback/cenovus-energy-bucket-pool.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Cenovus Energy, Turnaround Manager:</p>\r\n<p class=\"quotation\">Our flow rates improved from 160 to 260 meters cubed per hour after using Tech Sonic&rsquo;s innovative cleaning technologies. Truly amazing.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'mx', '2020-12-26 20:31:20', '2020-12-26 20:31:20');
INSERT INTO `reviews` VALUES ('120', 'EWHeGkP4gy', '22222222', 'Nucor', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean heat exchangers require less energy and improves plant efficiencies.\" data-src=\"/assets/img/feedback/nucor.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Nucor:</p>\r\n<p class=\"quotation\">The heat exchanger that we cleaned Friday is operating very well. Overall heat transfer efficiency increased 50%.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'mx', '2020-12-26 20:31:54', '2020-12-26 20:31:54');
INSERT INTO `reviews` VALUES ('121', '8QhLp93xAn', '33333333', 'From Practice-To-Theory-To-Practice: Advances in the Cleaning of Heat Exchangers Using Ultrasound', '<h2>From Practice-To-Theory-To-Practice: Advances in the Cleaning of Heat Exchangers Using Ultrasound</h2>\r\n<p class=\"text-muted\">B. Kieser, A. Goode and R. Philion</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/51_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"A typical exchanger being removed from the ultrasonic bath for inspection and rinsing.\" data-src=\"https://cleanasnew.com/assets/img/tech-sonic-cleaning.jpg\" /></a></p>\r\n<p>The cleaning of heat exchangers, and other large process equipment, using large-scale ultrasonic systems has, in the past few years, progressed from a &ldquo;curiosity&rdquo; to a proven, practical tool for many clients.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/51_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'mx', '2020-12-26 20:38:46', '2020-12-26 20:38:46');
INSERT INTO `reviews` VALUES ('122', 'Rbw0omqBJ5', '33333333', 'Ultrasonic Cleaning of Badly Fouled Compabloc Exchangers', '<h2>Ultrasonic Cleaning of Badly Fouled Compabloc Exchangers</h2>\r\n<p class=\"text-muted\">R. Philion, B. Kieser and T. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/49_Kieser-PhilionSMCFL_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Compabloc exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-compablocs-cleaned.jpg\" /></a></p>\r\n<p>This paper describes the use of ultrasonic technologies to clean badly fouled Alfa Laval Compabloc&reg; 106 heat exchangers - which could not be effectively cleaned by conventional methods.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/49_Kieser-PhilionSMCFL_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'mx', '2020-12-26 20:40:06', '2020-12-26 20:40:06');
INSERT INTO `reviews` VALUES ('123', 'Wczml95w8J', '33333333', 'Application of Stabilized Hydrogen Peroxide for the Removal of Iron Polysulfide Deposits', '<h2>Application of Stabilized Hydrogen Peroxide for the Removal of Iron Polysulfide Deposits</h2>\r\n<p class=\"text-muted\">T.R. McCartney and R. Shank</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/47_McCartney_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-exchangers-cleaned2.jpg\" /></a></p>\r\n<p>Hydrogen Peroxide is a strong oxidizing agent that could be used for the effective removal of the iron sulfide species.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/47_McCartney_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'mx', '2020-12-26 20:45:46', '2020-12-26 20:45:46');
INSERT INTO `reviews` VALUES ('124', 'ireQIuqZ6B', '33333333', 'Understanding the Value of Cleaning - Gains From the Use of Ultrasonic Cleaning of Large Industrial Equipment', '<h2>Understanding the Value of Cleaning - Gains From the Use of Ultrasonic Cleaning of Large Industrial Equipment</h2>\r\n<p class=\"text-muted\">C. H. Schulz and B. Kieser</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2019/41_Schulz%20and%20Kieser.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" style=\"float: none;\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Example of soiling on Quench Oil Exchangers.\" data-src=\"https://cleanasnew.com/assets/img/dirty-heat-exchanger.jpg\" /></a></p>\r\n<p>We present the results of a study to determine the efficacy of large-scale ultrasonic cleaning of heat exchangers at Braskem.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2019/41_Schulz%20and%20Kieser.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'mx', '2020-12-26 20:46:29', '2020-12-26 20:46:29');
INSERT INTO `reviews` VALUES ('125', '4ZGDROmkBX', '33333333', 'The Effective Removal of Feco3 Deposits From Critical Process Heat Exchangers Used in Ammonia Production Using a Proprietary Ultrasonic Chemical Cleaning Method', '<h2>The Effective Removal of Feco3 Deposits From Critical Process Heat Exchangers Used in Ammonia Production Using a Proprietary Ultrasonic Chemical Cleaning Method</h2>\r\n<p class=\"text-muted\">R. Philion and T.R. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/50_Philion_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-exchangers-cleaned3.jpg\" /></a></p>\r\n<p>This paper examines the problem of ferrous carbonate fouling and removal in lean - rich Diethanol amine exchangers at the CO2 extraction plants associated with ammonia manufacturing.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/50_Philion_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'mx', '2020-12-26 20:47:36', '2020-12-26 20:47:36');
INSERT INTO `reviews` VALUES ('137', 'o71Uy3sYSH', '11111111', 'Limpieza ultrasónica fuera de línea: restauración del rendimiento construido durante los cambios', '<p class=\"text-muted\" style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa; color: #6c757d !important;\">Gwen van de Bilt, Field Supervisor, Waste and Cleaning, Shell Moerdijk</p>\r\n<p><img class=\"img-fluid polaroid\" style=\"box-sizing: border-box; vertical-align: middle; border-style: none; max-width: 100%; height: auto; box-shadow: rgba(0, 0, 0, 0.2) 0px 4px 8px 0px, rgba(0, 0, 0, 0.19) 0px 6px 20px 0px; float: left; margin-bottom: 8.48438px; margin-left: 0px; font-family: Oswald, sans-serif; font-size: 16px; background-color: #f8f9fa;\" src=\"https://cleanasnew.com/img/before-and-after-filters-cleaned.jpg\" alt=\"Typical Wedge Wire filter for cleaning, multiple filters can be cleaned at the same time with only a low-pressure rinse.\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa;\">In April 2013 the Shell Moerdijk Chemicals plant conducted the biggest&ndash;ever TA event in the history of the site. One of the successes we had involved employing off-line ultrasonic cleaning.</p>', null, null, 'Case Studies', 'es', '2020-12-26 14:23:34', '2020-12-26 14:23:34');
INSERT INTO `reviews` VALUES ('138', 'c48qWa5AoR', '11111111', 'El sistema de limpieza Tech Sonic ayuda a ConocoPhillips a neutralizar y limpiar las válvulas', '<h2>Tech Sonic Cleaning System Helps ConocoPhillips Neutralize<br />and Clean Valve</h2>\r\n<p class=\"text-muted\">&nbsp;</p>\r\n<p><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Monel Plug valve after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-valves-cleaned.jpg\" /></p>\r\n<p>ConocoPhillips is one of the largest refiners in the United States. Its Ponca City, Oklahoma refiner , with a crude oil processing capacity of 187,000 per day, processes a mixture of light, medium...</p>\r\n<p><a class=\"btn btn-default\" href=\"https://cleanasnew.com/case-studies/ConocoPhllips-Valve-Service.pdf\" target=\"_blank\" rel=\"noopener\">Download Case Study </a></p>', null, null, 'Case Studies', 'es', '2020-12-26 20:14:42', '2020-12-26 20:14:42');
INSERT INTO `reviews` VALUES ('139', 'GynfsMdkKN', '11111111', 'El equipo de mejora recibe el tratamiento de spa', '<h2>Upgrader Equipment Gets The Spa Treatment</h2>\r\n<p class=\"text-muted\">Tanya Ristoff, Communications Manager, Shell Scotford</p>\r\n<p><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Pictured here &ndash;an exchanger bundle is lowered into an ultrasonic bath by crane.\" data-src=\"https://cleanasnew.com/assets/img/scotford-upgrader-turnaround-case-study.jpg\" /></p>\r\n<p>At first glance, it looks like a big equipment soaker tub, but there&rsquo;s much more than meets the eye when it comes to Tech Sonic Services&rsquo; ultrasonic wash technology.</p>\r\n<p><a class=\"btn btn-default\" href=\"https://cleanasnew.com/case-studies/Scotford-Upgrader-Turnaround.pdf\" target=\"_blank\" rel=\"noopener\">Download Case Study </a></p>', null, null, 'Case Studies', 'es', '2020-12-26 20:15:47', '2020-12-26 20:15:47');
INSERT INTO `reviews` VALUES ('140', 'ZdgtQSLRDz', '22222222', 'Alfa Laval', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\" Compabloc heat exchanger before and after cleaning.\" data-src=\"/assets/img/feedback/AlfaLaval.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">De Alfa Laval, Gerente Comercial:</p>\r\n<p class=\"quotation\">Como prueba, Ulrik llevó nuestro pequeño decantador de prueba a su taller durante 24 horas. El equipo se limpió inmaculadamente dentro del tiempo requerido solicitado con muy poca preocupación para hacerlo en <span class=\"cangc3\">C</span><span class=\"cangc4\">LEAN</span> <span class=\"cangc3\">A</span><span class=\"cangc4\">S</span> <span class=\"cangc3\">N</span><span class=\"cangc4\">EW</span><sup>&reg;</sup>&rsquo;s process.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'es', '2020-12-26 20:24:58', '2020-12-26 20:24:58');
INSERT INTO `reviews` VALUES ('141', 'RjY7hZ1Vqe', '22222222', 'Exxon Mobil', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Like \'new\' heat exchanger.\" data-src=\"/assets/img/feedback/Exxon-heat-exchanger.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Exxon Mobil, Heat Team Lead:</p>\r\n<p class=\"quotation\">When we want our equipment &ldquo;really clean&rdquo; we send it to <span class=\"cangc3\">C</span><span class=\"cangc4\">LEAN</span> <span class=\"cangc3\">A</span><span class=\"cangc4\">S</span> <span class=\"cangc3\">N</span><span class=\"cangc4\">EW</span><sup>&reg;</sup>.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'es', '2020-12-26 20:25:32', '2020-12-26 20:25:32');
INSERT INTO `reviews` VALUES ('142', '3IHSkFB29u', '22222222', 'Integridad Versa', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Cleans hard to reach areas in a heat exchanger.\" data-src=\"/assets/img/feedback/Versa-integrity.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Versa Integrity, Operations Manager:</p>\r\n<p class=\"quotation\">The exchanger was so clean our guys could not tell that the exchanger had ever been in service.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'es', '2020-12-26 20:26:04', '2020-12-26 20:26:04');
INSERT INTO `reviews` VALUES ('143', 'Pk6FxVC5fj', '22222222', 'Dow Chemical', '<div class=\"col-4 float-left\"><img class=\"imimg-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean As New&reg; offers a faster cleaning, faster repair turnaround, and faster return to service.\" data-src=\"/assets/img/feedback/Dow.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Dow Chemical, Lead Turnaround Planner:</p>\r\n<p class=\"quotation\">Heard nothing but good things and we are in the process of planning our next outage. Promise if we can send it there to be cleaned, we will. Thanks again.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'es', '2020-12-26 20:26:55', '2020-12-26 20:26:55');
INSERT INTO `reviews` VALUES ('144', '5ZaVWOyXNn', '22222222', 'Shell Sarnia', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean As New&reg; cleans compabloc heat exchangers in a fraction of the time required for hydroblasting.\" data-src=\"/assets/img/feedback/shell-sarina-compabloc.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Sarnia, Refinery Manager:</p>\r\n<p class=\"quotation\">The Refinery decided to test clean four Compabloc units using the new process proposed by Tech Sonic. With the clean Compablocs back in operation, the refinery client captured the throughput and heat transfer data rates to reveal an annual (bpd) recovery of US$4.27M.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'es', '2020-12-26 20:27:22', '2020-12-26 20:27:22');
INSERT INTO `reviews` VALUES ('145', 'Tdsr8iqgOW', '22222222', 'Shell Scotford', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\" Cleans Tube Exchanger back to original specs.\" data-src=\"../assets/img/feedback/shell-scotford.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Scotford:</p>\r\n<p class=\"quotation\">We experienced excellent results with cleaning components for offsite repair/overhaul. 3rd party companies have never seen valves as clean as the ones coming from Scotford. Stainless steel bundles were cleaned better than expected and typically achieved IRIS quality. Bundles with other material composition were generally cleaned to Eddy Current quality. To achieve IRIS spec for these non-stainless items, some were rinsed with a 20Kpsi &amp; rotary lance.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'es', '2020-12-26 20:27:53', '2020-12-26 20:27:53');
INSERT INTO `reviews` VALUES ('146', 'ku0J6orG1c', '22222222', 'Shell Moerdijk', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Like \'new\' rotating equipment.\" data-src=\"/assets/img/feedback/shell-moerdijk-rotator.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Moerdijk, Production Cleaning Manager:</p>\r\n<p class=\"quotation\">Tech Sonic Cleaning Systems are twice as fast at half the cost of traditional hydroblasting. We achieve thorough, complete cleaning that is non-destructive compared to conventional applications.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'es', '2020-12-26 20:28:21', '2020-12-26 20:28:21');
INSERT INTO `reviews` VALUES ('147', 'VoiGD6RmEj', '22222222', 'Shell Global', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"A clean Shell Exchanger improves plant productivity.\" data-src=\"/assets/img/feedback/exchanger-bundles.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Global, Operations &amp; Technology Lead:</p>\r\n<p class=\"quotation\">This is a game changing technology in cleaning effectiveness. Exchanger bundles simply come out cleaner than when addressed with conventional technology. There is a better performance of the bundles on start-up, and these results are achieved in less cleaning time per bundle. This will allow us to be more aggressive in our turnaround schedules in the future. We typically see 70-80% recovery of performance with HP water jetting (caused by residual fouling on the shell side of the bundle). Now we see up to 100% of design heat transfer performance after start-up using the Tech Sonic applications.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'es', '2020-12-26 20:29:49', '2020-12-26 20:29:49');
INSERT INTO `reviews` VALUES ('148', 'Ek36HWw0nK', '22222222', 'Energía Suncor', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic technology is efficient and effective at cleaning dirty scaffolding.\" data-src=\"/assets/img/feedback/suncor-scaffolding.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Suncor Energy:</p>\r\n<p class=\"quotation\">For every dollar we spend with Tech Sonic, we save four dollars in operational and cost efficiencies. We used to have to throw out most of the components as they could not be put back into production. Our capital costs have reduced dramatically.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'es', '2020-12-26 20:30:23', '2020-12-26 20:30:23');
INSERT INTO `reviews` VALUES ('149', 'kfvsjpY5DM', '22222222', 'CF Industrie', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic systems can clean most exchangers for less than half of replacement cost.\" data-src=\"/assets/img/feedback/cf-industries-shell-exchanger.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From CF Industries, Production Manager:</p>\r\n<p class=\"quotation\">We achieved a 27% increase in production of Ammonia after Tech Sonic cleaned our C-109 process train heat exchangers.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'es', '2020-12-26 20:30:53', '2020-12-26 20:30:53');
INSERT INTO `reviews` VALUES ('150', '0gpYVfsHub', '22222222', 'Energía Cenovus', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic systems can clean most packing materials for less than half of replacement cost. \" data-src=\"/assets/img/feedback/cenovus-energy-bucket-pool.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Cenovus Energy, Turnaround Manager:</p>\r\n<p class=\"quotation\">Our flow rates improved from 160 to 260 meters cubed per hour after using Tech Sonic&rsquo;s innovative cleaning technologies. Truly amazing.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'es', '2020-12-26 20:31:20', '2020-12-26 20:31:20');
INSERT INTO `reviews` VALUES ('151', 'EWHeGkP4gy', '22222222', 'Nucor', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean heat exchangers require less energy and improves plant efficiencies.\" data-src=\"/assets/img/feedback/nucor.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Nucor:</p>\r\n<p class=\"quotation\">The heat exchanger that we cleaned Friday is operating very well. Overall heat transfer efficiency increased 50%.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'es', '2020-12-26 20:31:54', '2020-12-26 20:31:54');
INSERT INTO `reviews` VALUES ('152', '8QhLp93xAn', '33333333', 'De la práctica a la teoría a la práctica: avances en la limpieza de intercambiadores de calor mediante ultrasonido', '<h2>From Practice-To-Theory-To-Practice: Advances in the Cleaning of Heat Exchangers Using Ultrasound</h2>\r\n<p class=\"text-muted\">B. Kieser, A. Goode and R. Philion</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/51_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"A typical exchanger being removed from the ultrasonic bath for inspection and rinsing.\" data-src=\"https://cleanasnew.com/assets/img/tech-sonic-cleaning.jpg\" /></a></p>\r\n<p>The cleaning of heat exchangers, and other large process equipment, using large-scale ultrasonic systems has, in the past few years, progressed from a &ldquo;curiosity&rdquo; to a proven, practical tool for many clients.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/51_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'es', '2020-12-26 20:38:46', '2020-12-26 20:38:46');
INSERT INTO `reviews` VALUES ('153', 'Rbw0omqBJ5', '33333333', 'Limpieza ultrasónica de intercambiadores Compabloc muy sucios', '<h2>Ultrasonic Cleaning of Badly Fouled Compabloc Exchangers</h2>\r\n<p class=\"text-muted\">R. Philion, B. Kieser and T. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/49_Kieser-PhilionSMCFL_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Compabloc exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-compablocs-cleaned.jpg\" /></a></p>\r\n<p>This paper describes the use of ultrasonic technologies to clean badly fouled Alfa Laval Compabloc&reg; 106 heat exchangers - which could not be effectively cleaned by conventional methods.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/49_Kieser-PhilionSMCFL_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'es', '2020-12-26 20:40:06', '2020-12-26 20:40:06');
INSERT INTO `reviews` VALUES ('154', 'Wczml95w8J', '33333333', 'Aplicación de peróxido de hidrógeno estabilizado para la eliminación de depósitos de polisulfuro de hierro', '<h2>Application of Stabilized Hydrogen Peroxide for the Removal of Iron Polysulfide Deposits</h2>\r\n<p class=\"text-muted\">T.R. McCartney and R. Shank</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/47_McCartney_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-exchangers-cleaned2.jpg\" /></a></p>\r\n<p>Hydrogen Peroxide is a strong oxidizing agent that could be used for the effective removal of the iron sulfide species.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/47_McCartney_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'es', '2020-12-26 20:45:46', '2020-12-26 20:45:46');
INSERT INTO `reviews` VALUES ('155', 'ireQIuqZ6B', '33333333', 'Comprensión del valor de la limpieza: beneficios derivados del uso de la limpieza ultrasónica de grandes equipos industriales', '<h2>Understanding the Value of Cleaning - Gains From the Use of Ultrasonic Cleaning of Large Industrial Equipment</h2>\r\n<p class=\"text-muted\">C. H. Schulz and B. Kieser</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2019/41_Schulz%20and%20Kieser.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" style=\"float: none;\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Example of soiling on Quench Oil Exchangers.\" data-src=\"https://cleanasnew.com/assets/img/dirty-heat-exchanger.jpg\" /></a></p>\r\n<p>We present the results of a study to determine the efficacy of large-scale ultrasonic cleaning of heat exchangers at Braskem.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2019/41_Schulz%20and%20Kieser.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'es', '2020-12-26 20:46:29', '2020-12-26 20:46:29');
INSERT INTO `reviews` VALUES ('156', '4ZGDROmkBX', '33333333', 'La eliminación efectiva de los depósitos de Feco3 de los intercambiadores de calor de procesos críticos utilizados en la producción de amoníaco mediante un método patentado de limpieza química ultrasónica', '<h2>The Effective Removal of Feco3 Deposits From Critical Process Heat Exchangers Used in Ammonia Production Using a Proprietary Ultrasonic Chemical Cleaning Method</h2>\r\n<p class=\"text-muted\">R. Philion and T.R. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/50_Philion_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-exchangers-cleaned3.jpg\" /></a></p>\r\n<p>This paper examines the problem of ferrous carbonate fouling and removal in lean - rich Diethanol amine exchangers at the CO2 extraction plants associated with ammonia manufacturing.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/50_Philion_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'es', '2020-12-26 20:47:36', '2020-12-26 20:47:36');
INSERT INTO `reviews` VALUES ('168', 'o71Uy3sYSH', '11111111', 'Nettoyage par ultrasons hors ligne - Rétablissement des performances telles que construites pendant les délais d\'exécution', '<p class=\"text-muted\" style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa; color: #6c757d !important;\">Gwen van de Bilt, Field Supervisor, Waste and Cleaning, Shell Moerdijk</p>\r\n<p><img class=\"img-fluid polaroid\" style=\"box-sizing: border-box; vertical-align: middle; border-style: none; max-width: 100%; height: auto; box-shadow: rgba(0, 0, 0, 0.2) 0px 4px 8px 0px, rgba(0, 0, 0, 0.19) 0px 6px 20px 0px; float: left; margin-bottom: 8.48438px; margin-left: 0px; font-family: Oswald, sans-serif; font-size: 16px; background-color: #f8f9fa;\" src=\"https://cleanasnew.com/img/before-and-after-filters-cleaned.jpg\" alt=\"Typical Wedge Wire filter for cleaning, multiple filters can be cleaned at the same time with only a low-pressure rinse.\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa;\">In April 2013 the Shell Moerdijk Chemicals plant conducted the biggest&ndash;ever TA event in the history of the site. One of the successes we had involved employing off-line ultrasonic cleaning.</p>', null, null, 'Case Studies', 'fr', '2020-12-26 14:23:34', '2020-12-26 14:23:34');
INSERT INTO `reviews` VALUES ('169', 'c48qWa5AoR', '11111111', 'Le système de nettoyage Tech Sonic aide ConocoPhillips à neutraliser et nettoyer les vannes', '<h2>Tech Sonic Cleaning System Helps ConocoPhillips Neutralize<br />and Clean Valve</h2>\r\n<p class=\"text-muted\">&nbsp;</p>\r\n<p><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Monel Plug valve after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-valves-cleaned.jpg\" /></p>\r\n<p>ConocoPhillips is one of the largest refiners in the United States. Its Ponca City, Oklahoma refiner , with a crude oil processing capacity of 187,000 per day, processes a mixture of light, medium...</p>\r\n<p><a class=\"btn btn-default\" href=\"https://cleanasnew.com/case-studies/ConocoPhllips-Valve-Service.pdf\" target=\"_blank\" rel=\"noopener\">Download Case Study </a></p>', null, null, 'Case Studies', 'fr', '2020-12-26 20:14:42', '2020-12-26 20:14:42');
INSERT INTO `reviews` VALUES ('170', 'GynfsMdkKN', '11111111', 'L\'équipement de valorisation reçoit le traitement du spa', '<h2>Upgrader Equipment Gets The Spa Treatment</h2>\r\n<p class=\"text-muted\">Tanya Ristoff, Communications Manager, Shell Scotford</p>\r\n<p><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Pictured here &ndash;an exchanger bundle is lowered into an ultrasonic bath by crane.\" data-src=\"https://cleanasnew.com/assets/img/scotford-upgrader-turnaround-case-study.jpg\" /></p>\r\n<p>At first glance, it looks like a big equipment soaker tub, but there&rsquo;s much more than meets the eye when it comes to Tech Sonic Services&rsquo; ultrasonic wash technology.</p>\r\n<p><a class=\"btn btn-default\" href=\"https://cleanasnew.com/case-studies/Scotford-Upgrader-Turnaround.pdf\" target=\"_blank\" rel=\"noopener\">Download Case Study </a></p>', null, null, 'Case Studies', 'fr', '2020-12-26 20:15:47', '2020-12-26 20:15:47');
INSERT INTO `reviews` VALUES ('171', 'ZdgtQSLRDz', '22222222', 'Alfa Laval', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\" Compabloc heat exchanger before and after cleaning.\" data-src=\"/assets/img/feedback/AlfaLaval.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">De Alfa Laval, directeur commercial:</p>\r\n<p class=\"quotation\">En guise de test, Ulrik a mis notre petite carafe de test dans son magasin pendant 24 heures. L\'équipement a été impeccablement nettoyé dans les délais requis avec très peu de souci de le faire <span class=\"cangc3\">C</span><span class=\"cangc4\">LEAN</span> <span class=\"cangc3\">A</span><span class=\"cangc4\">S</span> <span class=\"cangc3\">N</span><span class=\"cangc4\">EW</span><sup>&reg;</sup>&rsquo;s process.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'fr', '2020-12-26 20:24:58', '2020-12-26 20:24:58');
INSERT INTO `reviews` VALUES ('172', 'RjY7hZ1Vqe', '22222222', 'Exxon Mobil', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Like \'new\' heat exchanger.\" data-src=\"/assets/img/feedback/Exxon-heat-exchanger.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">De Exxon Mobil, chef d\'équipe Heat:</p>\r\n<p class=\"quotation\">When we want our equipment &ldquo;really clean&rdquo; we send it to <span class=\"cangc3\">C</span><span class=\"cangc4\">LEAN</span> <span class=\"cangc3\">A</span><span class=\"cangc4\">S</span> <span class=\"cangc3\">N</span><span class=\"cangc4\">EW</span><sup>&reg;</sup>.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'fr', '2020-12-26 20:25:32', '2020-12-26 20:25:32');
INSERT INTO `reviews` VALUES ('173', '3IHSkFB29u', '22222222', 'Intégrité Versa', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Cleans hard to reach areas in a heat exchanger.\" data-src=\"/assets/img/feedback/Versa-integrity.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">De Versa Integrity, Operations Manager:</p>\r\n<p class=\"quotation\">L\'échangeur était si propre que nos gars ne pouvaient pas dire que l\'échangeur avait déjà été en service.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'fr', '2020-12-26 20:26:04', '2020-12-26 20:26:04');
INSERT INTO `reviews` VALUES ('174', 'Pk6FxVC5fj', '22222222', 'Dow Chemical', '<div class=\"col-4 float-left\"><img class=\"imimg-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean As New&reg; offers a faster cleaning, faster repair turnaround, and faster return to service.\" data-src=\"/assets/img/feedback/Dow.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">De Dow Chemical, Lead Turnaround Planner:</p>\r\n<p class=\"quotation\">Nous n\'avons entendu que de bonnes choses et nous sommes en train de planifier notre prochaine panne. Promettez si nous pouvons l\'envoyer là-bas pour être nettoyé, nous le ferons. Merci encore.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'fr', '2020-12-26 20:26:55', '2020-12-26 20:26:55');
INSERT INTO `reviews` VALUES ('175', '5ZaVWOyXNn', '22222222', 'Shell Sarnia', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean As New&reg; cleans compabloc heat exchangers in a fraction of the time required for hydroblasting.\" data-src=\"/assets/img/feedback/shell-sarina-compabloc.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Sarnia, Refinery Manager:</p>\r\n<p class=\"quotation\">The Refinery decided to test clean four Compabloc units using the new process proposed by Tech Sonic. With the clean Compablocs back in operation, the refinery client captured the throughput and heat transfer data rates to reveal an annual (bpd) recovery of US$4.27M.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'fr', '2020-12-26 20:27:22', '2020-12-26 20:27:22');
INSERT INTO `reviews` VALUES ('176', 'Tdsr8iqgOW', '22222222', 'Shell Scotford', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\" Cleans Tube Exchanger back to original specs.\" data-src=\"../assets/img/feedback/shell-scotford.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Scotford:</p>\r\n<p class=\"quotation\">We experienced excellent results with cleaning components for offsite repair/overhaul. 3rd party companies have never seen valves as clean as the ones coming from Scotford. Stainless steel bundles were cleaned better than expected and typically achieved IRIS quality. Bundles with other material composition were generally cleaned to Eddy Current quality. To achieve IRIS spec for these non-stainless items, some were rinsed with a 20Kpsi &amp; rotary lance.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'fr', '2020-12-26 20:27:53', '2020-12-26 20:27:53');
INSERT INTO `reviews` VALUES ('177', 'ku0J6orG1c', '22222222', 'Shell Moerdijk', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Like \'new\' rotating equipment.\" data-src=\"/assets/img/feedback/shell-moerdijk-rotator.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Moerdijk, Production Cleaning Manager:</p>\r\n<p class=\"quotation\">Tech Sonic Cleaning Systems are twice as fast at half the cost of traditional hydroblasting. We achieve thorough, complete cleaning that is non-destructive compared to conventional applications.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'fr', '2020-12-26 20:28:21', '2020-12-26 20:28:21');
INSERT INTO `reviews` VALUES ('178', 'VoiGD6RmEj', '22222222', 'Shell Global', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"A clean Shell Exchanger improves plant productivity.\" data-src=\"/assets/img/feedback/exchanger-bundles.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Global, Operations &amp; Technology Lead:</p>\r\n<p class=\"quotation\">This is a game changing technology in cleaning effectiveness. Exchanger bundles simply come out cleaner than when addressed with conventional technology. There is a better performance of the bundles on start-up, and these results are achieved in less cleaning time per bundle. This will allow us to be more aggressive in our turnaround schedules in the future. We typically see 70-80% recovery of performance with HP water jetting (caused by residual fouling on the shell side of the bundle). Now we see up to 100% of design heat transfer performance after start-up using the Tech Sonic applications.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'fr', '2020-12-26 20:29:49', '2020-12-26 20:29:49');
INSERT INTO `reviews` VALUES ('179', 'Ek36HWw0nK', '22222222', 'Suncor Énergie', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic technology is efficient and effective at cleaning dirty scaffolding.\" data-src=\"/assets/img/feedback/suncor-scaffolding.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Suncor Energy:</p>\r\n<p class=\"quotation\">For every dollar we spend with Tech Sonic, we save four dollars in operational and cost efficiencies. We used to have to throw out most of the components as they could not be put back into production. Our capital costs have reduced dramatically.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'fr', '2020-12-26 20:30:23', '2020-12-26 20:30:23');
INSERT INTO `reviews` VALUES ('180', 'kfvsjpY5DM', '22222222', 'CF Industrie', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic systems can clean most exchangers for less than half of replacement cost.\" data-src=\"/assets/img/feedback/cf-industries-shell-exchanger.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From CF Industries, Production Manager:</p>\r\n<p class=\"quotation\">We achieved a 27% increase in production of Ammonia after Tech Sonic cleaned our C-109 process train heat exchangers.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'fr', '2020-12-26 20:30:53', '2020-12-26 20:30:53');
INSERT INTO `reviews` VALUES ('181', '0gpYVfsHub', '22222222', 'Énergie Cenovus', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic systems can clean most packing materials for less than half of replacement cost. \" data-src=\"/assets/img/feedback/cenovus-energy-bucket-pool.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Cenovus Energy, Turnaround Manager:</p>\r\n<p class=\"quotation\">Our flow rates improved from 160 to 260 meters cubed per hour after using Tech Sonic&rsquo;s innovative cleaning technologies. Truly amazing.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'fr', '2020-12-26 20:31:20', '2020-12-26 20:31:20');
INSERT INTO `reviews` VALUES ('182', 'EWHeGkP4gy', '22222222', 'Nucor', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean heat exchangers require less energy and improves plant efficiencies.\" data-src=\"/assets/img/feedback/nucor.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Nucor:</p>\r\n<p class=\"quotation\">The heat exchanger that we cleaned Friday is operating very well. Overall heat transfer efficiency increased 50%.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'fr', '2020-12-26 20:31:54', '2020-12-26 20:31:54');
INSERT INTO `reviews` VALUES ('183', '8QhLp93xAn', '33333333', 'De la pratique à la théorie à la pratique: progrès dans le nettoyage des échangeurs de chaleur par ultrasons', '<h2>From Practice-To-Theory-To-Practice: Advances in the Cleaning of Heat Exchangers Using Ultrasound</h2>\r\n<p class=\"text-muted\">B. Kieser, A. Goode and R. Philion</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/51_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"A typical exchanger being removed from the ultrasonic bath for inspection and rinsing.\" data-src=\"https://cleanasnew.com/assets/img/tech-sonic-cleaning.jpg\" /></a></p>\r\n<p>The cleaning of heat exchangers, and other large process equipment, using large-scale ultrasonic systems has, in the past few years, progressed from a &ldquo;curiosity&rdquo; to a proven, practical tool for many clients.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/51_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'fr', '2020-12-26 20:38:46', '2020-12-26 20:38:46');
INSERT INTO `reviews` VALUES ('184', 'Rbw0omqBJ5', '33333333', 'Nettoyage par ultrasons des échangeurs Compabloc très encrassés', '<h2>Ultrasonic Cleaning of Badly Fouled Compabloc Exchangers</h2>\r\n<p class=\"text-muted\">R. Philion, B. Kieser and T. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/49_Kieser-PhilionSMCFL_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Compabloc exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-compablocs-cleaned.jpg\" /></a></p>\r\n<p>This paper describes the use of ultrasonic technologies to clean badly fouled Alfa Laval Compabloc&reg; 106 heat exchangers - which could not be effectively cleaned by conventional methods.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/49_Kieser-PhilionSMCFL_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'fr', '2020-12-26 20:40:06', '2020-12-26 20:40:06');
INSERT INTO `reviews` VALUES ('185', 'Wczml95w8J', '33333333', 'Application de peroxyde d\'hydrogène stabilisé pour l\'élimination des dépôts de polysulfure de fer', '<h2>Application of Stabilized Hydrogen Peroxide for the Removal of Iron Polysulfide Deposits</h2>\r\n<p class=\"text-muted\">T.R. McCartney and R. Shank</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/47_McCartney_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-exchangers-cleaned2.jpg\" /></a></p>\r\n<p>Hydrogen Peroxide is a strong oxidizing agent that could be used for the effective removal of the iron sulfide species.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/47_McCartney_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'fr', '2020-12-26 20:45:46', '2020-12-26 20:45:46');
INSERT INTO `reviews` VALUES ('186', 'ireQIuqZ6B', '33333333', 'Comprendre la valeur du nettoyage - Gains de l\'utilisation du nettoyage par ultrasons de gros équipements industriels', '<h2>Understanding the Value of Cleaning - Gains From the Use of Ultrasonic Cleaning of Large Industrial Equipment</h2>\r\n<p class=\"text-muted\">C. H. Schulz and B. Kieser</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2019/41_Schulz%20and%20Kieser.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" style=\"float: none;\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Example of soiling on Quench Oil Exchangers.\" data-src=\"https://cleanasnew.com/assets/img/dirty-heat-exchanger.jpg\" /></a></p>\r\n<p>We present the results of a study to determine the efficacy of large-scale ultrasonic cleaning of heat exchangers at Braskem.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2019/41_Schulz%20and%20Kieser.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'fr', '2020-12-26 20:46:29', '2020-12-26 20:46:29');
INSERT INTO `reviews` VALUES ('187', '4ZGDROmkBX', '33333333', 'L\'élimination efficace des dépôts Feco3 des échangeurs de chaleur de processus critiques utilisés dans la production d\'ammoniac à l\'aide d\'une méthode de nettoyage chimique par ultrasons exclusive', '<h2>The Effective Removal of Feco3 Deposits From Critical Process Heat Exchangers Used in Ammonia Production Using a Proprietary Ultrasonic Chemical Cleaning Method</h2>\r\n<p class=\"text-muted\">R. Philion and T.R. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/50_Philion_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-exchangers-cleaned3.jpg\" /></a></p>\r\n<p>This paper examines the problem of ferrous carbonate fouling and removal in lean - rich Diethanol amine exchangers at the CO2 extraction plants associated with ammonia manufacturing.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/50_Philion_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'fr', '2020-12-26 20:47:36', '2020-12-26 20:47:36');
INSERT INTO `reviews` VALUES ('196', '11111111', '11111111', 'Removal Of Iron Carbonate Deposits From Heat Exchangers Used In Ammonia Production Using Tech Sonic Cleaning', '<h2><img style=\"height: auto;\" src=\"https://cleanasnew.com/assets/img/fouling.jpg\" alt=\"A sample of the FeCO3fouling removed from the tube side of the heat exchanger.\" data-src=\"https://cleanasnew.com/assets/img/fouling.jpg\" /></h2>\r\n            <p>The use of ultrasonic technology for cleaning refinery and chemical plant components has been undertaken by Tech Sonic since 2003.</p>', '', null, 'Case Studies', 'pt-br', null, '2020-12-23 16:23:55');
INSERT INTO `reviews` VALUES ('197', '22222222', '22222222', 'Shell Deer Park', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../../assets/img/1px.png\" alt=\"Tech Sonic technology is efficient and effective at cleaning dirty heat exchanger. \" data-src=\"/assets/img/feedback/Shell-Deer-Park.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Deer Park, Turnaround Manager:</p>\r\n<p class=\"quotation\">I thought everything you guys did was awesome and have already been spreading recommendations to continue using you guys whenever we can!</p>\r\n</blockquote>\r\n</div>', '/assets/img/feedback/Shell-Deer-Park.jpg', 'Turnaround Manager', 'Feedback', 'pt-br', null, '2020-12-26 20:24:26');
INSERT INTO `reviews` VALUES ('198', '33333333', '33333333', 'The Application of Industrial Scale Ultrasonic Cleaning to Heat Exchangers', '<h2>The Application of Industrial Scale Ultrasonic Cleaning to Heat Exchangers</h2>\r\n<p class=\"text-muted\">B. Kieser, R. Phillion and T. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Tube Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" /></a></p>\r\n<p>The cleaning of fouled heat exchangers often presents a significant challenge to the maintenance and operation of chemical, petroleum and food processes.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', 'assets/img/tech-sonic-vessel-sm.jpg', null, 'Referred Journal Publications', 'pt-br', null, '2020-12-26 20:37:56');
INSERT INTO `reviews` VALUES ('199', 'o71Uy3sYSH', '11111111', 'Off-Line Ultrasonic Cleaning – Restoring As Built Performance During Turnarounds', '<p class=\"text-muted\" style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa; color: #6c757d !important;\">Gwen van de Bilt, Field Supervisor, Waste and Cleaning, Shell Moerdijk</p>\r\n<p><img class=\"img-fluid polaroid\" style=\"box-sizing: border-box; vertical-align: middle; border-style: none; max-width: 100%; height: auto; box-shadow: rgba(0, 0, 0, 0.2) 0px 4px 8px 0px, rgba(0, 0, 0, 0.19) 0px 6px 20px 0px; float: left; margin-bottom: 8.48438px; margin-left: 0px; font-family: Oswald, sans-serif; font-size: 16px; background-color: #f8f9fa;\" src=\"https://cleanasnew.com/img/before-and-after-filters-cleaned.jpg\" alt=\"Typical Wedge Wire filter for cleaning, multiple filters can be cleaned at the same time with only a low-pressure rinse.\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa;\">In April 2013 the Shell Moerdijk Chemicals plant conducted the biggest&ndash;ever TA event in the history of the site. One of the successes we had involved employing off-line ultrasonic cleaning.</p>', null, null, 'Case Studies', 'pt-br', '2020-12-26 14:23:34', '2020-12-26 14:23:34');
INSERT INTO `reviews` VALUES ('200', 'c48qWa5AoR', '11111111', 'Tech Sonic Cleaning System Helps ConocoPhillips Neutralize and Clean Valves', '<h2>Tech Sonic Cleaning System Helps ConocoPhillips Neutralize<br />and Clean Valve</h2>\r\n<p class=\"text-muted\">&nbsp;</p>\r\n<p><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Monel Plug valve after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-valves-cleaned.jpg\" /></p>\r\n<p>ConocoPhillips is one of the largest refiners in the United States. Its Ponca City, Oklahoma refiner , with a crude oil processing capacity of 187,000 per day, processes a mixture of light, medium...</p>\r\n<p><a class=\"btn btn-default\" href=\"https://cleanasnew.com/case-studies/ConocoPhllips-Valve-Service.pdf\" target=\"_blank\" rel=\"noopener\">Download Case Study </a></p>', null, null, 'Case Studies', 'pt-br', '2020-12-26 20:14:42', '2020-12-26 20:14:42');
INSERT INTO `reviews` VALUES ('201', 'GynfsMdkKN', '11111111', 'Upgrader Equipment Gets The Spa Treatment', '<h2>Upgrader Equipment Gets The Spa Treatment</h2>\r\n<p class=\"text-muted\">Tanya Ristoff, Communications Manager, Shell Scotford</p>\r\n<p><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Pictured here &ndash;an exchanger bundle is lowered into an ultrasonic bath by crane.\" data-src=\"https://cleanasnew.com/assets/img/scotford-upgrader-turnaround-case-study.jpg\" /></p>\r\n<p>At first glance, it looks like a big equipment soaker tub, but there&rsquo;s much more than meets the eye when it comes to Tech Sonic Services&rsquo; ultrasonic wash technology.</p>\r\n<p><a class=\"btn btn-default\" href=\"https://cleanasnew.com/case-studies/Scotford-Upgrader-Turnaround.pdf\" target=\"_blank\" rel=\"noopener\">Download Case Study </a></p>', null, null, 'Case Studies', 'pt-br', '2020-12-26 20:15:47', '2020-12-26 20:15:47');
INSERT INTO `reviews` VALUES ('202', 'ZdgtQSLRDz', '22222222', 'Alfa Laval', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\" Compabloc heat exchanger before and after cleaning.\" data-src=\"/assets/img/feedback/AlfaLaval.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Alfa Laval, Business Manager:</p>\r\n<p class=\"quotation\">As a test Ulrik had our small test decanter into their shop for a 24 hour turn around. The equipment was immaculately cleaned within the required time requested with very little concern to do so on <span class=\"cangc3\">C</span><span class=\"cangc4\">LEAN</span> <span class=\"cangc3\">A</span><span class=\"cangc4\">S</span> <span class=\"cangc3\">N</span><span class=\"cangc4\">EW</span><sup>&reg;</sup>&rsquo;s process.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'pt-br', '2020-12-26 20:24:58', '2020-12-26 20:24:58');
INSERT INTO `reviews` VALUES ('203', 'RjY7hZ1Vqe', '22222222', 'Exxon Mobil', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Like \'new\' heat exchanger.\" data-src=\"/assets/img/feedback/Exxon-heat-exchanger.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Exxon Mobil, Heat Team Lead:</p>\r\n<p class=\"quotation\">When we want our equipment &ldquo;really clean&rdquo; we send it to <span class=\"cangc3\">C</span><span class=\"cangc4\">LEAN</span> <span class=\"cangc3\">A</span><span class=\"cangc4\">S</span> <span class=\"cangc3\">N</span><span class=\"cangc4\">EW</span><sup>&reg;</sup>.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'pt-br', '2020-12-26 20:25:32', '2020-12-26 20:25:32');
INSERT INTO `reviews` VALUES ('204', '3IHSkFB29u', '22222222', 'Versa Integrity', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Cleans hard to reach areas in a heat exchanger.\" data-src=\"/assets/img/feedback/Versa-integrity.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Versa Integrity, Operations Manager:</p>\r\n<p class=\"quotation\">The exchanger was so clean our guys could not tell that the exchanger had ever been in service.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'pt-br', '2020-12-26 20:26:04', '2020-12-26 20:26:04');
INSERT INTO `reviews` VALUES ('205', 'Pk6FxVC5fj', '22222222', 'Dow Chemical', '<div class=\"col-4 float-left\"><img class=\"imimg-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean As New&reg; offers a faster cleaning, faster repair turnaround, and faster return to service.\" data-src=\"/assets/img/feedback/Dow.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Dow Chemical, Lead Turnaround Planner:</p>\r\n<p class=\"quotation\">Heard nothing but good things and we are in the process of planning our next outage. Promise if we can send it there to be cleaned, we will. Thanks again.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'pt-br', '2020-12-26 20:26:55', '2020-12-26 20:26:55');
INSERT INTO `reviews` VALUES ('206', '5ZaVWOyXNn', '22222222', 'Shell Sarnia', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean As New&reg; cleans compabloc heat exchangers in a fraction of the time required for hydroblasting.\" data-src=\"/assets/img/feedback/shell-sarina-compabloc.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Sarnia, Refinery Manager:</p>\r\n<p class=\"quotation\">The Refinery decided to test clean four Compabloc units using the new process proposed by Tech Sonic. With the clean Compablocs back in operation, the refinery client captured the throughput and heat transfer data rates to reveal an annual (bpd) recovery of US$4.27M.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'pt-br', '2020-12-26 20:27:22', '2020-12-26 20:27:22');
INSERT INTO `reviews` VALUES ('207', 'Tdsr8iqgOW', '22222222', 'Shell Scotford', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\" Cleans Tube Exchanger back to original specs.\" data-src=\"../assets/img/feedback/shell-scotford.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Scotford:</p>\r\n<p class=\"quotation\">We experienced excellent results with cleaning components for offsite repair/overhaul. 3rd party companies have never seen valves as clean as the ones coming from Scotford. Stainless steel bundles were cleaned better than expected and typically achieved IRIS quality. Bundles with other material composition were generally cleaned to Eddy Current quality. To achieve IRIS spec for these non-stainless items, some were rinsed with a 20Kpsi &amp; rotary lance.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'pt-br', '2020-12-26 20:27:53', '2020-12-26 20:27:53');
INSERT INTO `reviews` VALUES ('208', 'ku0J6orG1c', '22222222', 'Shell Moerdijk', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Like \'new\' rotating equipment.\" data-src=\"/assets/img/feedback/shell-moerdijk-rotator.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Moerdijk, Production Cleaning Manager:</p>\r\n<p class=\"quotation\">Tech Sonic Cleaning Systems are twice as fast at half the cost of traditional hydroblasting. We achieve thorough, complete cleaning that is non-destructive compared to conventional applications.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'pt-br', '2020-12-26 20:28:21', '2020-12-26 20:28:21');
INSERT INTO `reviews` VALUES ('209', 'VoiGD6RmEj', '22222222', 'Shell Global', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"A clean Shell Exchanger improves plant productivity.\" data-src=\"/assets/img/feedback/exchanger-bundles.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Global, Operations &amp; Technology Lead:</p>\r\n<p class=\"quotation\">This is a game changing technology in cleaning effectiveness. Exchanger bundles simply come out cleaner than when addressed with conventional technology. There is a better performance of the bundles on start-up, and these results are achieved in less cleaning time per bundle. This will allow us to be more aggressive in our turnaround schedules in the future. We typically see 70-80% recovery of performance with HP water jetting (caused by residual fouling on the shell side of the bundle). Now we see up to 100% of design heat transfer performance after start-up using the Tech Sonic applications.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'pt-br', '2020-12-26 20:29:49', '2020-12-26 20:29:49');
INSERT INTO `reviews` VALUES ('210', 'Ek36HWw0nK', '22222222', 'Suncor Energy', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic technology is efficient and effective at cleaning dirty scaffolding.\" data-src=\"/assets/img/feedback/suncor-scaffolding.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Suncor Energy:</p>\r\n<p class=\"quotation\">For every dollar we spend with Tech Sonic, we save four dollars in operational and cost efficiencies. We used to have to throw out most of the components as they could not be put back into production. Our capital costs have reduced dramatically.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'pt-br', '2020-12-26 20:30:23', '2020-12-26 20:30:23');
INSERT INTO `reviews` VALUES ('211', 'kfvsjpY5DM', '22222222', 'CF Industrie', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic systems can clean most exchangers for less than half of replacement cost.\" data-src=\"/assets/img/feedback/cf-industries-shell-exchanger.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From CF Industries, Production Manager:</p>\r\n<p class=\"quotation\">We achieved a 27% increase in production of Ammonia after Tech Sonic cleaned our C-109 process train heat exchangers.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'pt-br', '2020-12-26 20:30:53', '2020-12-26 20:30:53');
INSERT INTO `reviews` VALUES ('212', '0gpYVfsHub', '22222222', 'Cenovus Energy', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic systems can clean most packing materials for less than half of replacement cost. \" data-src=\"/assets/img/feedback/cenovus-energy-bucket-pool.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Cenovus Energy, Turnaround Manager:</p>\r\n<p class=\"quotation\">Our flow rates improved from 160 to 260 meters cubed per hour after using Tech Sonic&rsquo;s innovative cleaning technologies. Truly amazing.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'pt-br', '2020-12-26 20:31:20', '2020-12-26 20:31:20');
INSERT INTO `reviews` VALUES ('213', 'EWHeGkP4gy', '22222222', 'Nucor', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean heat exchangers require less energy and improves plant efficiencies.\" data-src=\"/assets/img/feedback/nucor.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Nucor:</p>\r\n<p class=\"quotation\">The heat exchanger that we cleaned Friday is operating very well. Overall heat transfer efficiency increased 50%.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'pt-br', '2020-12-26 20:31:54', '2020-12-26 20:31:54');
INSERT INTO `reviews` VALUES ('214', '8QhLp93xAn', '33333333', 'From Practice-To-Theory-To-Practice: Advances in the Cleaning of Heat Exchangers Using Ultrasound', '<h2>From Practice-To-Theory-To-Practice: Advances in the Cleaning of Heat Exchangers Using Ultrasound</h2>\r\n<p class=\"text-muted\">B. Kieser, A. Goode and R. Philion</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/51_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"A typical exchanger being removed from the ultrasonic bath for inspection and rinsing.\" data-src=\"https://cleanasnew.com/assets/img/tech-sonic-cleaning.jpg\" /></a></p>\r\n<p>The cleaning of heat exchangers, and other large process equipment, using large-scale ultrasonic systems has, in the past few years, progressed from a &ldquo;curiosity&rdquo; to a proven, practical tool for many clients.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/51_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'pt-br', '2020-12-26 20:38:46', '2020-12-26 20:38:46');
INSERT INTO `reviews` VALUES ('215', 'Rbw0omqBJ5', '33333333', 'Ultrasonic Cleaning of Badly Fouled Compabloc Exchangers', '<h2>Ultrasonic Cleaning of Badly Fouled Compabloc Exchangers</h2>\r\n<p class=\"text-muted\">R. Philion, B. Kieser and T. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/49_Kieser-PhilionSMCFL_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Compabloc exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-compablocs-cleaned.jpg\" /></a></p>\r\n<p>This paper describes the use of ultrasonic technologies to clean badly fouled Alfa Laval Compabloc&reg; 106 heat exchangers - which could not be effectively cleaned by conventional methods.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/49_Kieser-PhilionSMCFL_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'pt-br', '2020-12-26 20:40:06', '2020-12-26 20:40:06');
INSERT INTO `reviews` VALUES ('216', 'Wczml95w8J', '33333333', 'Application of Stabilized Hydrogen Peroxide for the Removal of Iron Polysulfide Deposits', '<h2>Application of Stabilized Hydrogen Peroxide for the Removal of Iron Polysulfide Deposits</h2>\r\n<p class=\"text-muted\">T.R. McCartney and R. Shank</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/47_McCartney_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-exchangers-cleaned2.jpg\" /></a></p>\r\n<p>Hydrogen Peroxide is a strong oxidizing agent that could be used for the effective removal of the iron sulfide species.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/47_McCartney_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'pt-br', '2020-12-26 20:45:46', '2020-12-26 20:45:46');
INSERT INTO `reviews` VALUES ('217', 'ireQIuqZ6B', '33333333', 'Understanding the Value of Cleaning - Gains From the Use of Ultrasonic Cleaning of Large Industrial Equipment', '<h2>Understanding the Value of Cleaning - Gains From the Use of Ultrasonic Cleaning of Large Industrial Equipment</h2>\r\n<p class=\"text-muted\">C. H. Schulz and B. Kieser</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2019/41_Schulz%20and%20Kieser.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" style=\"float: none;\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Example of soiling on Quench Oil Exchangers.\" data-src=\"https://cleanasnew.com/assets/img/dirty-heat-exchanger.jpg\" /></a></p>\r\n<p>We present the results of a study to determine the efficacy of large-scale ultrasonic cleaning of heat exchangers at Braskem.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2019/41_Schulz%20and%20Kieser.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'pt-br', '2020-12-26 20:46:29', '2020-12-26 20:46:29');
INSERT INTO `reviews` VALUES ('218', '4ZGDROmkBX', '33333333', 'The Effective Removal of Feco3 Deposits From Critical Process Heat Exchangers Used in Ammonia Production Using a Proprietary Ultrasonic Chemical Cleaning Method', '<h2>The Effective Removal of Feco3 Deposits From Critical Process Heat Exchangers Used in Ammonia Production Using a Proprietary Ultrasonic Chemical Cleaning Method</h2>\r\n<p class=\"text-muted\">R. Philion and T.R. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/50_Philion_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-exchangers-cleaned3.jpg\" /></a></p>\r\n<p>This paper examines the problem of ferrous carbonate fouling and removal in lean - rich Diethanol amine exchangers at the CO2 extraction plants associated with ammonia manufacturing.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/50_Philion_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'pt-br', '2020-12-26 20:47:36', '2020-12-26 20:47:36');
INSERT INTO `reviews` VALUES ('227', '11111111', '11111111', 'Removal Of Iron Carbonate Deposits From Heat Exchangers Used In Ammonia Production Using Tech Sonic Cleaning', '<h2><img style=\"height: auto;\" src=\"https://cleanasnew.com/assets/img/fouling.jpg\" alt=\"A sample of the FeCO3fouling removed from the tube side of the heat exchanger.\" data-src=\"https://cleanasnew.com/assets/img/fouling.jpg\" /></h2>\r\n            <p>The use of ultrasonic technology for cleaning refinery and chemical plant components has been undertaken by Tech Sonic since 2003.</p>', '', null, 'Case Studies', 'de', null, '2020-12-23 16:23:55');
INSERT INTO `reviews` VALUES ('228', '22222222', '22222222', 'Shell Deer Park', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../../assets/img/1px.png\" alt=\"Tech Sonic technology is efficient and effective at cleaning dirty heat exchanger. \" data-src=\"/assets/img/feedback/Shell-Deer-Park.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Deer Park, Turnaround Manager:</p>\r\n<p class=\"quotation\">I thought everything you guys did was awesome and have already been spreading recommendations to continue using you guys whenever we can!</p>\r\n</blockquote>\r\n</div>', '/assets/img/feedback/Shell-Deer-Park.jpg', 'Turnaround Manager', 'Feedback', 'de', null, '2020-12-26 20:24:26');
INSERT INTO `reviews` VALUES ('229', '33333333', '33333333', 'The Application of Industrial Scale Ultrasonic Cleaning to Heat Exchangers', '<h2>The Application of Industrial Scale Ultrasonic Cleaning to Heat Exchangers</h2>\r\n<p class=\"text-muted\">B. Kieser, R. Phillion and T. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Tube Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" /></a></p>\r\n<p>The cleaning of fouled heat exchangers often presents a significant challenge to the maintenance and operation of chemical, petroleum and food processes.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', 'assets/img/tech-sonic-vessel-sm.jpg', null, 'Referred Journal Publications', 'de', null, '2020-12-26 20:37:56');
INSERT INTO `reviews` VALUES ('230', 'o71Uy3sYSH', '11111111', 'Off-Line Ultrasonic Cleaning – Restoring As Built Performance During Turnarounds', '<p class=\"text-muted\" style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa; color: #6c757d !important;\">Gwen van de Bilt, Field Supervisor, Waste and Cleaning, Shell Moerdijk</p>\r\n<p><img class=\"img-fluid polaroid\" style=\"box-sizing: border-box; vertical-align: middle; border-style: none; max-width: 100%; height: auto; box-shadow: rgba(0, 0, 0, 0.2) 0px 4px 8px 0px, rgba(0, 0, 0, 0.19) 0px 6px 20px 0px; float: left; margin-bottom: 8.48438px; margin-left: 0px; font-family: Oswald, sans-serif; font-size: 16px; background-color: #f8f9fa;\" src=\"https://cleanasnew.com/img/before-and-after-filters-cleaned.jpg\" alt=\"Typical Wedge Wire filter for cleaning, multiple filters can be cleaned at the same time with only a low-pressure rinse.\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa;\">In April 2013 the Shell Moerdijk Chemicals plant conducted the biggest&ndash;ever TA event in the history of the site. One of the successes we had involved employing off-line ultrasonic cleaning.</p>', null, null, 'Case Studies', 'de', '2020-12-26 14:23:34', '2020-12-26 14:23:34');
INSERT INTO `reviews` VALUES ('231', 'c48qWa5AoR', '11111111', 'Tech Sonic Cleaning System Helps ConocoPhillips Neutralize and Clean Valves', '<h2>Tech Sonic Cleaning System Helps ConocoPhillips Neutralize<br />and Clean Valve</h2>\r\n<p class=\"text-muted\">&nbsp;</p>\r\n<p><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Monel Plug valve after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-valves-cleaned.jpg\" /></p>\r\n<p>ConocoPhillips is one of the largest refiners in the United States. Its Ponca City, Oklahoma refiner , with a crude oil processing capacity of 187,000 per day, processes a mixture of light, medium...</p>\r\n<p><a class=\"btn btn-default\" href=\"https://cleanasnew.com/case-studies/ConocoPhllips-Valve-Service.pdf\" target=\"_blank\" rel=\"noopener\">Download Case Study </a></p>', null, null, 'Case Studies', 'de', '2020-12-26 20:14:42', '2020-12-26 20:14:42');
INSERT INTO `reviews` VALUES ('232', 'GynfsMdkKN', '11111111', 'Upgrader Equipment Gets The Spa Treatment', '<h2>Upgrader Equipment Gets The Spa Treatment</h2>\r\n<p class=\"text-muted\">Tanya Ristoff, Communications Manager, Shell Scotford</p>\r\n<p><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Pictured here &ndash;an exchanger bundle is lowered into an ultrasonic bath by crane.\" data-src=\"https://cleanasnew.com/assets/img/scotford-upgrader-turnaround-case-study.jpg\" /></p>\r\n<p>At first glance, it looks like a big equipment soaker tub, but there&rsquo;s much more than meets the eye when it comes to Tech Sonic Services&rsquo; ultrasonic wash technology.</p>\r\n<p><a class=\"btn btn-default\" href=\"https://cleanasnew.com/case-studies/Scotford-Upgrader-Turnaround.pdf\" target=\"_blank\" rel=\"noopener\">Download Case Study </a></p>', null, null, 'Case Studies', 'de', '2020-12-26 20:15:47', '2020-12-26 20:15:47');
INSERT INTO `reviews` VALUES ('233', 'ZdgtQSLRDz', '22222222', 'Alfa Laval', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\" Compabloc heat exchanger before and after cleaning.\" data-src=\"/assets/img/feedback/AlfaLaval.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Alfa Laval, Business Manager:</p>\r\n<p class=\"quotation\">As a test Ulrik had our small test decanter into their shop for a 24 hour turn around. The equipment was immaculately cleaned within the required time requested with very little concern to do so on <span class=\"cangc3\">C</span><span class=\"cangc4\">LEAN</span> <span class=\"cangc3\">A</span><span class=\"cangc4\">S</span> <span class=\"cangc3\">N</span><span class=\"cangc4\">EW</span><sup>&reg;</sup>&rsquo;s process.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'de', '2020-12-26 20:24:58', '2020-12-26 20:24:58');
INSERT INTO `reviews` VALUES ('234', 'RjY7hZ1Vqe', '22222222', 'Exxon Mobil', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Like \'new\' heat exchanger.\" data-src=\"/assets/img/feedback/Exxon-heat-exchanger.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Exxon Mobil, Heat Team Lead:</p>\r\n<p class=\"quotation\">When we want our equipment &ldquo;really clean&rdquo; we send it to <span class=\"cangc3\">C</span><span class=\"cangc4\">LEAN</span> <span class=\"cangc3\">A</span><span class=\"cangc4\">S</span> <span class=\"cangc3\">N</span><span class=\"cangc4\">EW</span><sup>&reg;</sup>.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'de', '2020-12-26 20:25:32', '2020-12-26 20:25:32');
INSERT INTO `reviews` VALUES ('235', '3IHSkFB29u', '22222222', 'Versa Integrity', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Cleans hard to reach areas in a heat exchanger.\" data-src=\"/assets/img/feedback/Versa-integrity.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Versa Integrity, Operations Manager:</p>\r\n<p class=\"quotation\">The exchanger was so clean our guys could not tell that the exchanger had ever been in service.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'de', '2020-12-26 20:26:04', '2020-12-26 20:26:04');
INSERT INTO `reviews` VALUES ('236', 'Pk6FxVC5fj', '22222222', 'Dow Chemical', '<div class=\"col-4 float-left\"><img class=\"imimg-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean As New&reg; offers a faster cleaning, faster repair turnaround, and faster return to service.\" data-src=\"/assets/img/feedback/Dow.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Dow Chemical, Lead Turnaround Planner:</p>\r\n<p class=\"quotation\">Heard nothing but good things and we are in the process of planning our next outage. Promise if we can send it there to be cleaned, we will. Thanks again.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'de', '2020-12-26 20:26:55', '2020-12-26 20:26:55');
INSERT INTO `reviews` VALUES ('237', '5ZaVWOyXNn', '22222222', 'Shell Sarnia', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean As New&reg; cleans compabloc heat exchangers in a fraction of the time required for hydroblasting.\" data-src=\"/assets/img/feedback/shell-sarina-compabloc.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Sarnia, Refinery Manager:</p>\r\n<p class=\"quotation\">The Refinery decided to test clean four Compabloc units using the new process proposed by Tech Sonic. With the clean Compablocs back in operation, the refinery client captured the throughput and heat transfer data rates to reveal an annual (bpd) recovery of US$4.27M.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'de', '2020-12-26 20:27:22', '2020-12-26 20:27:22');
INSERT INTO `reviews` VALUES ('238', 'Tdsr8iqgOW', '22222222', 'Shell Scotford', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\" Cleans Tube Exchanger back to original specs.\" data-src=\"../assets/img/feedback/shell-scotford.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Scotford:</p>\r\n<p class=\"quotation\">We experienced excellent results with cleaning components for offsite repair/overhaul. 3rd party companies have never seen valves as clean as the ones coming from Scotford. Stainless steel bundles were cleaned better than expected and typically achieved IRIS quality. Bundles with other material composition were generally cleaned to Eddy Current quality. To achieve IRIS spec for these non-stainless items, some were rinsed with a 20Kpsi &amp; rotary lance.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'de', '2020-12-26 20:27:53', '2020-12-26 20:27:53');
INSERT INTO `reviews` VALUES ('239', 'ku0J6orG1c', '22222222', 'Shell Moerdijk', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Like \'new\' rotating equipment.\" data-src=\"/assets/img/feedback/shell-moerdijk-rotator.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Moerdijk, Production Cleaning Manager:</p>\r\n<p class=\"quotation\">Tech Sonic Cleaning Systems are twice as fast at half the cost of traditional hydroblasting. We achieve thorough, complete cleaning that is non-destructive compared to conventional applications.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'de', '2020-12-26 20:28:21', '2020-12-26 20:28:21');
INSERT INTO `reviews` VALUES ('240', 'VoiGD6RmEj', '22222222', 'Shell Global', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"A clean Shell Exchanger improves plant productivity.\" data-src=\"/assets/img/feedback/exchanger-bundles.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Global, Operations &amp; Technology Lead:</p>\r\n<p class=\"quotation\">This is a game changing technology in cleaning effectiveness. Exchanger bundles simply come out cleaner than when addressed with conventional technology. There is a better performance of the bundles on start-up, and these results are achieved in less cleaning time per bundle. This will allow us to be more aggressive in our turnaround schedules in the future. We typically see 70-80% recovery of performance with HP water jetting (caused by residual fouling on the shell side of the bundle). Now we see up to 100% of design heat transfer performance after start-up using the Tech Sonic applications.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'de', '2020-12-26 20:29:49', '2020-12-26 20:29:49');
INSERT INTO `reviews` VALUES ('241', 'Ek36HWw0nK', '22222222', 'Suncor Energy', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic technology is efficient and effective at cleaning dirty scaffolding.\" data-src=\"/assets/img/feedback/suncor-scaffolding.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Suncor Energy:</p>\r\n<p class=\"quotation\">For every dollar we spend with Tech Sonic, we save four dollars in operational and cost efficiencies. We used to have to throw out most of the components as they could not be put back into production. Our capital costs have reduced dramatically.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'de', '2020-12-26 20:30:23', '2020-12-26 20:30:23');
INSERT INTO `reviews` VALUES ('242', 'kfvsjpY5DM', '22222222', 'CF Industrie', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic systems can clean most exchangers for less than half of replacement cost.\" data-src=\"/assets/img/feedback/cf-industries-shell-exchanger.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From CF Industries, Production Manager:</p>\r\n<p class=\"quotation\">We achieved a 27% increase in production of Ammonia after Tech Sonic cleaned our C-109 process train heat exchangers.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'de', '2020-12-26 20:30:53', '2020-12-26 20:30:53');
INSERT INTO `reviews` VALUES ('243', '0gpYVfsHub', '22222222', 'Cenovus Energy', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic systems can clean most packing materials for less than half of replacement cost. \" data-src=\"/assets/img/feedback/cenovus-energy-bucket-pool.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Cenovus Energy, Turnaround Manager:</p>\r\n<p class=\"quotation\">Our flow rates improved from 160 to 260 meters cubed per hour after using Tech Sonic&rsquo;s innovative cleaning technologies. Truly amazing.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'de', '2020-12-26 20:31:20', '2020-12-26 20:31:20');
INSERT INTO `reviews` VALUES ('244', 'EWHeGkP4gy', '22222222', 'Nucor', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean heat exchangers require less energy and improves plant efficiencies.\" data-src=\"/assets/img/feedback/nucor.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Nucor:</p>\r\n<p class=\"quotation\">The heat exchanger that we cleaned Friday is operating very well. Overall heat transfer efficiency increased 50%.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'de', '2020-12-26 20:31:54', '2020-12-26 20:31:54');
INSERT INTO `reviews` VALUES ('245', '8QhLp93xAn', '33333333', 'From Practice-To-Theory-To-Practice: Advances in the Cleaning of Heat Exchangers Using Ultrasound', '<h2>From Practice-To-Theory-To-Practice: Advances in the Cleaning of Heat Exchangers Using Ultrasound</h2>\r\n<p class=\"text-muted\">B. Kieser, A. Goode and R. Philion</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/51_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"A typical exchanger being removed from the ultrasonic bath for inspection and rinsing.\" data-src=\"https://cleanasnew.com/assets/img/tech-sonic-cleaning.jpg\" /></a></p>\r\n<p>The cleaning of heat exchangers, and other large process equipment, using large-scale ultrasonic systems has, in the past few years, progressed from a &ldquo;curiosity&rdquo; to a proven, practical tool for many clients.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/51_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'de', '2020-12-26 20:38:46', '2020-12-26 20:38:46');
INSERT INTO `reviews` VALUES ('246', 'Rbw0omqBJ5', '33333333', 'Ultrasonic Cleaning of Badly Fouled Compabloc Exchangers', '<h2>Ultrasonic Cleaning of Badly Fouled Compabloc Exchangers</h2>\r\n<p class=\"text-muted\">R. Philion, B. Kieser and T. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/49_Kieser-PhilionSMCFL_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Compabloc exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-compablocs-cleaned.jpg\" /></a></p>\r\n<p>This paper describes the use of ultrasonic technologies to clean badly fouled Alfa Laval Compabloc&reg; 106 heat exchangers - which could not be effectively cleaned by conventional methods.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/49_Kieser-PhilionSMCFL_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'de', '2020-12-26 20:40:06', '2020-12-26 20:40:06');
INSERT INTO `reviews` VALUES ('247', 'Wczml95w8J', '33333333', 'Application of Stabilized Hydrogen Peroxide for the Removal of Iron Polysulfide Deposits', '<h2>Application of Stabilized Hydrogen Peroxide for the Removal of Iron Polysulfide Deposits</h2>\r\n<p class=\"text-muted\">T.R. McCartney and R. Shank</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/47_McCartney_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-exchangers-cleaned2.jpg\" /></a></p>\r\n<p>Hydrogen Peroxide is a strong oxidizing agent that could be used for the effective removal of the iron sulfide species.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/47_McCartney_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'de', '2020-12-26 20:45:46', '2020-12-26 20:45:46');
INSERT INTO `reviews` VALUES ('248', 'ireQIuqZ6B', '33333333', 'Understanding the Value of Cleaning - Gains From the Use of Ultrasonic Cleaning of Large Industrial Equipment', '<h2>Understanding the Value of Cleaning - Gains From the Use of Ultrasonic Cleaning of Large Industrial Equipment</h2>\r\n<p class=\"text-muted\">C. H. Schulz and B. Kieser</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2019/41_Schulz%20and%20Kieser.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" style=\"float: none;\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Example of soiling on Quench Oil Exchangers.\" data-src=\"https://cleanasnew.com/assets/img/dirty-heat-exchanger.jpg\" /></a></p>\r\n<p>We present the results of a study to determine the efficacy of large-scale ultrasonic cleaning of heat exchangers at Braskem.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2019/41_Schulz%20and%20Kieser.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'de', '2020-12-26 20:46:29', '2020-12-26 20:46:29');
INSERT INTO `reviews` VALUES ('249', '4ZGDROmkBX', '33333333', 'The Effective Removal of Feco3 Deposits From Critical Process Heat Exchangers Used in Ammonia Production Using a Proprietary Ultrasonic Chemical Cleaning Method', '<h2>The Effective Removal of Feco3 Deposits From Critical Process Heat Exchangers Used in Ammonia Production Using a Proprietary Ultrasonic Chemical Cleaning Method</h2>\r\n<p class=\"text-muted\">R. Philion and T.R. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/50_Philion_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-exchangers-cleaned3.jpg\" /></a></p>\r\n<p>This paper examines the problem of ferrous carbonate fouling and removal in lean - rich Diethanol amine exchangers at the CO2 extraction plants associated with ammonia manufacturing.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/50_Philion_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'de', '2020-12-26 20:47:36', '2020-12-26 20:47:36');
INSERT INTO `reviews` VALUES ('258', '11111111', '11111111', 'Removal Of Iron Carbonate Deposits From Heat Exchangers Used In Ammonia Production Using Tech Sonic Cleaning', '<h2><img style=\"height: auto;\" src=\"https://cleanasnew.com/assets/img/fouling.jpg\" alt=\"A sample of the FeCO3fouling removed from the tube side of the heat exchanger.\" data-src=\"https://cleanasnew.com/assets/img/fouling.jpg\" /></h2>\r\n            <p>The use of ultrasonic technology for cleaning refinery and chemical plant components has been undertaken by Tech Sonic since 2003.</p>', '', null, 'Case Studies', 'ru', null, '2020-12-23 16:23:55');
INSERT INTO `reviews` VALUES ('259', '22222222', '22222222', 'Shell Deer Park', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../../assets/img/1px.png\" alt=\"Tech Sonic technology is efficient and effective at cleaning dirty heat exchanger. \" data-src=\"/assets/img/feedback/Shell-Deer-Park.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Deer Park, Turnaround Manager:</p>\r\n<p class=\"quotation\">I thought everything you guys did was awesome and have already been spreading recommendations to continue using you guys whenever we can!</p>\r\n</blockquote>\r\n</div>', '/assets/img/feedback/Shell-Deer-Park.jpg', 'Turnaround Manager', 'Feedback', 'ru', null, '2020-12-26 20:24:26');
INSERT INTO `reviews` VALUES ('260', '33333333', '33333333', 'The Application of Industrial Scale Ultrasonic Cleaning to Heat Exchangers', '<h2>The Application of Industrial Scale Ultrasonic Cleaning to Heat Exchangers</h2>\r\n<p class=\"text-muted\">B. Kieser, R. Phillion and T. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Tube Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" /></a></p>\r\n<p>The cleaning of fouled heat exchangers often presents a significant challenge to the maintenance and operation of chemical, petroleum and food processes.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', 'assets/img/tech-sonic-vessel-sm.jpg', null, 'Referred Journal Publications', 'ru', null, '2020-12-26 20:37:56');
INSERT INTO `reviews` VALUES ('261', 'o71Uy3sYSH', '11111111', 'Off-Line Ultrasonic Cleaning – Restoring As Built Performance During Turnarounds', '<p class=\"text-muted\" style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa; color: #6c757d !important;\">Gwen van de Bilt, Field Supervisor, Waste and Cleaning, Shell Moerdijk</p>\r\n<p><img class=\"img-fluid polaroid\" style=\"box-sizing: border-box; vertical-align: middle; border-style: none; max-width: 100%; height: auto; box-shadow: rgba(0, 0, 0, 0.2) 0px 4px 8px 0px, rgba(0, 0, 0, 0.19) 0px 6px 20px 0px; float: left; margin-bottom: 8.48438px; margin-left: 0px; font-family: Oswald, sans-serif; font-size: 16px; background-color: #f8f9fa;\" src=\"https://cleanasnew.com/img/before-and-after-filters-cleaned.jpg\" alt=\"Typical Wedge Wire filter for cleaning, multiple filters can be cleaned at the same time with only a low-pressure rinse.\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa;\">In April 2013 the Shell Moerdijk Chemicals plant conducted the biggest&ndash;ever TA event in the history of the site. One of the successes we had involved employing off-line ultrasonic cleaning.</p>', null, null, 'Case Studies', 'ru', '2020-12-26 14:23:34', '2020-12-26 14:23:34');
INSERT INTO `reviews` VALUES ('262', 'c48qWa5AoR', '11111111', 'Tech Sonic Cleaning System Helps ConocoPhillips Neutralize and Clean Valves', '<h2>Tech Sonic Cleaning System Helps ConocoPhillips Neutralize<br />and Clean Valve</h2>\r\n<p class=\"text-muted\">&nbsp;</p>\r\n<p><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Monel Plug valve after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-valves-cleaned.jpg\" /></p>\r\n<p>ConocoPhillips is one of the largest refiners in the United States. Its Ponca City, Oklahoma refiner , with a crude oil processing capacity of 187,000 per day, processes a mixture of light, medium...</p>\r\n<p><a class=\"btn btn-default\" href=\"https://cleanasnew.com/case-studies/ConocoPhllips-Valve-Service.pdf\" target=\"_blank\" rel=\"noopener\">Download Case Study </a></p>', null, null, 'Case Studies', 'ru', '2020-12-26 20:14:42', '2020-12-26 20:14:42');
INSERT INTO `reviews` VALUES ('263', 'GynfsMdkKN', '11111111', 'Upgrader Equipment Gets The Spa Treatment', '<h2>Upgrader Equipment Gets The Spa Treatment</h2>\r\n<p class=\"text-muted\">Tanya Ristoff, Communications Manager, Shell Scotford</p>\r\n<p><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Pictured here &ndash;an exchanger bundle is lowered into an ultrasonic bath by crane.\" data-src=\"https://cleanasnew.com/assets/img/scotford-upgrader-turnaround-case-study.jpg\" /></p>\r\n<p>At first glance, it looks like a big equipment soaker tub, but there&rsquo;s much more than meets the eye when it comes to Tech Sonic Services&rsquo; ultrasonic wash technology.</p>\r\n<p><a class=\"btn btn-default\" href=\"https://cleanasnew.com/case-studies/Scotford-Upgrader-Turnaround.pdf\" target=\"_blank\" rel=\"noopener\">Download Case Study </a></p>', null, null, 'Case Studies', 'ru', '2020-12-26 20:15:47', '2020-12-26 20:15:47');
INSERT INTO `reviews` VALUES ('264', 'ZdgtQSLRDz', '22222222', 'Alfa Laval', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\" Compabloc heat exchanger before and after cleaning.\" data-src=\"/assets/img/feedback/AlfaLaval.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Alfa Laval, Business Manager:</p>\r\n<p class=\"quotation\">As a test Ulrik had our small test decanter into their shop for a 24 hour turn around. The equipment was immaculately cleaned within the required time requested with very little concern to do so on <span class=\"cangc3\">C</span><span class=\"cangc4\">LEAN</span> <span class=\"cangc3\">A</span><span class=\"cangc4\">S</span> <span class=\"cangc3\">N</span><span class=\"cangc4\">EW</span><sup>&reg;</sup>&rsquo;s process.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'ru', '2020-12-26 20:24:58', '2020-12-26 20:24:58');
INSERT INTO `reviews` VALUES ('265', 'RjY7hZ1Vqe', '22222222', 'Exxon Mobil', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Like \'new\' heat exchanger.\" data-src=\"/assets/img/feedback/Exxon-heat-exchanger.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Exxon Mobil, Heat Team Lead:</p>\r\n<p class=\"quotation\">When we want our equipment &ldquo;really clean&rdquo; we send it to <span class=\"cangc3\">C</span><span class=\"cangc4\">LEAN</span> <span class=\"cangc3\">A</span><span class=\"cangc4\">S</span> <span class=\"cangc3\">N</span><span class=\"cangc4\">EW</span><sup>&reg;</sup>.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'ru', '2020-12-26 20:25:32', '2020-12-26 20:25:32');
INSERT INTO `reviews` VALUES ('266', '3IHSkFB29u', '22222222', 'Versa Integrity', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Cleans hard to reach areas in a heat exchanger.\" data-src=\"/assets/img/feedback/Versa-integrity.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Versa Integrity, Operations Manager:</p>\r\n<p class=\"quotation\">The exchanger was so clean our guys could not tell that the exchanger had ever been in service.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'ru', '2020-12-26 20:26:04', '2020-12-26 20:26:04');
INSERT INTO `reviews` VALUES ('267', 'Pk6FxVC5fj', '22222222', 'Dow Chemical', '<div class=\"col-4 float-left\"><img class=\"imimg-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean As New&reg; offers a faster cleaning, faster repair turnaround, and faster return to service.\" data-src=\"/assets/img/feedback/Dow.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Dow Chemical, Lead Turnaround Planner:</p>\r\n<p class=\"quotation\">Heard nothing but good things and we are in the process of planning our next outage. Promise if we can send it there to be cleaned, we will. Thanks again.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'ru', '2020-12-26 20:26:55', '2020-12-26 20:26:55');
INSERT INTO `reviews` VALUES ('268', '5ZaVWOyXNn', '22222222', 'Shell Sarnia', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean As New&reg; cleans compabloc heat exchangers in a fraction of the time required for hydroblasting.\" data-src=\"/assets/img/feedback/shell-sarina-compabloc.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Sarnia, Refinery Manager:</p>\r\n<p class=\"quotation\">The Refinery decided to test clean four Compabloc units using the new process proposed by Tech Sonic. With the clean Compablocs back in operation, the refinery client captured the throughput and heat transfer data rates to reveal an annual (bpd) recovery of US$4.27M.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'ru', '2020-12-26 20:27:22', '2020-12-26 20:27:22');
INSERT INTO `reviews` VALUES ('269', 'Tdsr8iqgOW', '22222222', 'Shell Scotford', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\" Cleans Tube Exchanger back to original specs.\" data-src=\"../assets/img/feedback/shell-scotford.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Scotford:</p>\r\n<p class=\"quotation\">We experienced excellent results with cleaning components for offsite repair/overhaul. 3rd party companies have never seen valves as clean as the ones coming from Scotford. Stainless steel bundles were cleaned better than expected and typically achieved IRIS quality. Bundles with other material composition were generally cleaned to Eddy Current quality. To achieve IRIS spec for these non-stainless items, some were rinsed with a 20Kpsi &amp; rotary lance.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'ru', '2020-12-26 20:27:53', '2020-12-26 20:27:53');
INSERT INTO `reviews` VALUES ('270', 'ku0J6orG1c', '22222222', 'Shell Moerdijk', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Like \'new\' rotating equipment.\" data-src=\"/assets/img/feedback/shell-moerdijk-rotator.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Moerdijk, Production Cleaning Manager:</p>\r\n<p class=\"quotation\">Tech Sonic Cleaning Systems are twice as fast at half the cost of traditional hydroblasting. We achieve thorough, complete cleaning that is non-destructive compared to conventional applications.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'ru', '2020-12-26 20:28:21', '2020-12-26 20:28:21');
INSERT INTO `reviews` VALUES ('271', 'VoiGD6RmEj', '22222222', 'Shell Global', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"A clean Shell Exchanger improves plant productivity.\" data-src=\"/assets/img/feedback/exchanger-bundles.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Global, Operations &amp; Technology Lead:</p>\r\n<p class=\"quotation\">This is a game changing technology in cleaning effectiveness. Exchanger bundles simply come out cleaner than when addressed with conventional technology. There is a better performance of the bundles on start-up, and these results are achieved in less cleaning time per bundle. This will allow us to be more aggressive in our turnaround schedules in the future. We typically see 70-80% recovery of performance with HP water jetting (caused by residual fouling on the shell side of the bundle). Now we see up to 100% of design heat transfer performance after start-up using the Tech Sonic applications.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'ru', '2020-12-26 20:29:49', '2020-12-26 20:29:49');
INSERT INTO `reviews` VALUES ('272', 'Ek36HWw0nK', '22222222', 'Suncor Energy', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic technology is efficient and effective at cleaning dirty scaffolding.\" data-src=\"/assets/img/feedback/suncor-scaffolding.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Suncor Energy:</p>\r\n<p class=\"quotation\">For every dollar we spend with Tech Sonic, we save four dollars in operational and cost efficiencies. We used to have to throw out most of the components as they could not be put back into production. Our capital costs have reduced dramatically.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'ru', '2020-12-26 20:30:23', '2020-12-26 20:30:23');
INSERT INTO `reviews` VALUES ('273', 'kfvsjpY5DM', '22222222', 'CF Industrie', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic systems can clean most exchangers for less than half of replacement cost.\" data-src=\"/assets/img/feedback/cf-industries-shell-exchanger.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From CF Industries, Production Manager:</p>\r\n<p class=\"quotation\">We achieved a 27% increase in production of Ammonia after Tech Sonic cleaned our C-109 process train heat exchangers.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'ru', '2020-12-26 20:30:53', '2020-12-26 20:30:53');
INSERT INTO `reviews` VALUES ('274', '0gpYVfsHub', '22222222', 'Cenovus Energy', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic systems can clean most packing materials for less than half of replacement cost. \" data-src=\"/assets/img/feedback/cenovus-energy-bucket-pool.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Cenovus Energy, Turnaround Manager:</p>\r\n<p class=\"quotation\">Our flow rates improved from 160 to 260 meters cubed per hour after using Tech Sonic&rsquo;s innovative cleaning technologies. Truly amazing.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'ru', '2020-12-26 20:31:20', '2020-12-26 20:31:20');
INSERT INTO `reviews` VALUES ('275', 'EWHeGkP4gy', '22222222', 'Nucor', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean heat exchangers require less energy and improves plant efficiencies.\" data-src=\"/assets/img/feedback/nucor.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Nucor:</p>\r\n<p class=\"quotation\">The heat exchanger that we cleaned Friday is operating very well. Overall heat transfer efficiency increased 50%.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'ru', '2020-12-26 20:31:54', '2020-12-26 20:31:54');
INSERT INTO `reviews` VALUES ('276', '8QhLp93xAn', '33333333', 'From Practice-To-Theory-To-Practice: Advances in the Cleaning of Heat Exchangers Using Ultrasound', '<h2>From Practice-To-Theory-To-Practice: Advances in the Cleaning of Heat Exchangers Using Ultrasound</h2>\r\n<p class=\"text-muted\">B. Kieser, A. Goode and R. Philion</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/51_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"A typical exchanger being removed from the ultrasonic bath for inspection and rinsing.\" data-src=\"https://cleanasnew.com/assets/img/tech-sonic-cleaning.jpg\" /></a></p>\r\n<p>The cleaning of heat exchangers, and other large process equipment, using large-scale ultrasonic systems has, in the past few years, progressed from a &ldquo;curiosity&rdquo; to a proven, practical tool for many clients.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/51_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'ru', '2020-12-26 20:38:46', '2020-12-26 20:38:46');
INSERT INTO `reviews` VALUES ('277', 'Rbw0omqBJ5', '33333333', 'Ultrasonic Cleaning of Badly Fouled Compabloc Exchangers', '<h2>Ultrasonic Cleaning of Badly Fouled Compabloc Exchangers</h2>\r\n<p class=\"text-muted\">R. Philion, B. Kieser and T. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/49_Kieser-PhilionSMCFL_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Compabloc exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-compablocs-cleaned.jpg\" /></a></p>\r\n<p>This paper describes the use of ultrasonic technologies to clean badly fouled Alfa Laval Compabloc&reg; 106 heat exchangers - which could not be effectively cleaned by conventional methods.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/49_Kieser-PhilionSMCFL_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'ru', '2020-12-26 20:40:06', '2020-12-26 20:40:06');
INSERT INTO `reviews` VALUES ('278', 'Wczml95w8J', '33333333', 'Application of Stabilized Hydrogen Peroxide for the Removal of Iron Polysulfide Deposits', '<h2>Application of Stabilized Hydrogen Peroxide for the Removal of Iron Polysulfide Deposits</h2>\r\n<p class=\"text-muted\">T.R. McCartney and R. Shank</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/47_McCartney_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-exchangers-cleaned2.jpg\" /></a></p>\r\n<p>Hydrogen Peroxide is a strong oxidizing agent that could be used for the effective removal of the iron sulfide species.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/47_McCartney_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'ru', '2020-12-26 20:45:46', '2020-12-26 20:45:46');
INSERT INTO `reviews` VALUES ('279', 'ireQIuqZ6B', '33333333', 'Understanding the Value of Cleaning - Gains From the Use of Ultrasonic Cleaning of Large Industrial Equipment', '<h2>Understanding the Value of Cleaning - Gains From the Use of Ultrasonic Cleaning of Large Industrial Equipment</h2>\r\n<p class=\"text-muted\">C. H. Schulz and B. Kieser</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2019/41_Schulz%20and%20Kieser.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" style=\"float: none;\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Example of soiling on Quench Oil Exchangers.\" data-src=\"https://cleanasnew.com/assets/img/dirty-heat-exchanger.jpg\" /></a></p>\r\n<p>We present the results of a study to determine the efficacy of large-scale ultrasonic cleaning of heat exchangers at Braskem.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2019/41_Schulz%20and%20Kieser.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'ru', '2020-12-26 20:46:29', '2020-12-26 20:46:29');
INSERT INTO `reviews` VALUES ('280', '4ZGDROmkBX', '33333333', 'The Effective Removal of Feco3 Deposits From Critical Process Heat Exchangers Used in Ammonia Production Using a Proprietary Ultrasonic Chemical Cleaning Method', '<h2>The Effective Removal of Feco3 Deposits From Critical Process Heat Exchangers Used in Ammonia Production Using a Proprietary Ultrasonic Chemical Cleaning Method</h2>\r\n<p class=\"text-muted\">R. Philion and T.R. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/50_Philion_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-exchangers-cleaned3.jpg\" /></a></p>\r\n<p>This paper examines the problem of ferrous carbonate fouling and removal in lean - rich Diethanol amine exchangers at the CO2 extraction plants associated with ammonia manufacturing.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/50_Philion_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'ru', '2020-12-26 20:47:36', '2020-12-26 20:47:36');
INSERT INTO `reviews` VALUES ('289', '11111111', '11111111', 'Removal Of Iron Carbonate Deposits From Heat Exchangers Used In Ammonia Production Using Tech Sonic Cleaning', '<h2><img style=\"height: auto;\" src=\"https://cleanasnew.com/assets/img/fouling.jpg\" alt=\"A sample of the FeCO3fouling removed from the tube side of the heat exchanger.\" data-src=\"https://cleanasnew.com/assets/img/fouling.jpg\" /></h2>\r\n            <p>The use of ultrasonic technology for cleaning refinery and chemical plant components has been undertaken by Tech Sonic since 2003.</p>', '', null, 'Case Studies', 'hi-in', null, '2020-12-23 16:23:55');
INSERT INTO `reviews` VALUES ('290', '22222222', '22222222', 'Shell Deer Park', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../../assets/img/1px.png\" alt=\"Tech Sonic technology is efficient and effective at cleaning dirty heat exchanger. \" data-src=\"/assets/img/feedback/Shell-Deer-Park.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Deer Park, Turnaround Manager:</p>\r\n<p class=\"quotation\">I thought everything you guys did was awesome and have already been spreading recommendations to continue using you guys whenever we can!</p>\r\n</blockquote>\r\n</div>', '/assets/img/feedback/Shell-Deer-Park.jpg', 'Turnaround Manager', 'Feedback', 'hi-in', null, '2020-12-26 20:24:26');
INSERT INTO `reviews` VALUES ('291', '33333333', '33333333', 'The Application of Industrial Scale Ultrasonic Cleaning to Heat Exchangers', '<h2>The Application of Industrial Scale Ultrasonic Cleaning to Heat Exchangers</h2>\r\n<p class=\"text-muted\">B. Kieser, R. Phillion and T. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Tube Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" /></a></p>\r\n<p>The cleaning of fouled heat exchangers often presents a significant challenge to the maintenance and operation of chemical, petroleum and food processes.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', 'assets/img/tech-sonic-vessel-sm.jpg', null, 'Referred Journal Publications', 'hi-in', null, '2020-12-26 20:37:56');
INSERT INTO `reviews` VALUES ('292', 'o71Uy3sYSH', '11111111', 'Off-Line Ultrasonic Cleaning – Restoring As Built Performance During Turnarounds', '<p class=\"text-muted\" style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa; color: #6c757d !important;\">Gwen van de Bilt, Field Supervisor, Waste and Cleaning, Shell Moerdijk</p>\r\n<p><img class=\"img-fluid polaroid\" style=\"box-sizing: border-box; vertical-align: middle; border-style: none; max-width: 100%; height: auto; box-shadow: rgba(0, 0, 0, 0.2) 0px 4px 8px 0px, rgba(0, 0, 0, 0.19) 0px 6px 20px 0px; float: left; margin-bottom: 8.48438px; margin-left: 0px; font-family: Oswald, sans-serif; font-size: 16px; background-color: #f8f9fa;\" src=\"https://cleanasnew.com/img/before-and-after-filters-cleaned.jpg\" alt=\"Typical Wedge Wire filter for cleaning, multiple filters can be cleaned at the same time with only a low-pressure rinse.\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa;\">In April 2013 the Shell Moerdijk Chemicals plant conducted the biggest&ndash;ever TA event in the history of the site. One of the successes we had involved employing off-line ultrasonic cleaning.</p>', null, null, 'Case Studies', 'hi-in', '2020-12-26 14:23:34', '2020-12-26 14:23:34');
INSERT INTO `reviews` VALUES ('293', 'c48qWa5AoR', '11111111', 'Tech Sonic Cleaning System Helps ConocoPhillips Neutralize and Clean Valves', '<h2>Tech Sonic Cleaning System Helps ConocoPhillips Neutralize<br />and Clean Valve</h2>\r\n<p class=\"text-muted\">&nbsp;</p>\r\n<p><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Monel Plug valve after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-valves-cleaned.jpg\" /></p>\r\n<p>ConocoPhillips is one of the largest refiners in the United States. Its Ponca City, Oklahoma refiner , with a crude oil processing capacity of 187,000 per day, processes a mixture of light, medium...</p>\r\n<p><a class=\"btn btn-default\" href=\"https://cleanasnew.com/case-studies/ConocoPhllips-Valve-Service.pdf\" target=\"_blank\" rel=\"noopener\">Download Case Study </a></p>', null, null, 'Case Studies', 'hi-in', '2020-12-26 20:14:42', '2020-12-26 20:14:42');
INSERT INTO `reviews` VALUES ('294', 'GynfsMdkKN', '11111111', 'Upgrader Equipment Gets The Spa Treatment', '<h2>Upgrader Equipment Gets The Spa Treatment</h2>\r\n<p class=\"text-muted\">Tanya Ristoff, Communications Manager, Shell Scotford</p>\r\n<p><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Pictured here &ndash;an exchanger bundle is lowered into an ultrasonic bath by crane.\" data-src=\"https://cleanasnew.com/assets/img/scotford-upgrader-turnaround-case-study.jpg\" /></p>\r\n<p>At first glance, it looks like a big equipment soaker tub, but there&rsquo;s much more than meets the eye when it comes to Tech Sonic Services&rsquo; ultrasonic wash technology.</p>\r\n<p><a class=\"btn btn-default\" href=\"https://cleanasnew.com/case-studies/Scotford-Upgrader-Turnaround.pdf\" target=\"_blank\" rel=\"noopener\">Download Case Study </a></p>', null, null, 'Case Studies', 'hi-in', '2020-12-26 20:15:47', '2020-12-26 20:15:47');
INSERT INTO `reviews` VALUES ('295', 'ZdgtQSLRDz', '22222222', 'Alfa Laval', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\" Compabloc heat exchanger before and after cleaning.\" data-src=\"/assets/img/feedback/AlfaLaval.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Alfa Laval, Business Manager:</p>\r\n<p class=\"quotation\">As a test Ulrik had our small test decanter into their shop for a 24 hour turn around. The equipment was immaculately cleaned within the required time requested with very little concern to do so on <span class=\"cangc3\">C</span><span class=\"cangc4\">LEAN</span> <span class=\"cangc3\">A</span><span class=\"cangc4\">S</span> <span class=\"cangc3\">N</span><span class=\"cangc4\">EW</span><sup>&reg;</sup>&rsquo;s process.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'hi-in', '2020-12-26 20:24:58', '2020-12-26 20:24:58');
INSERT INTO `reviews` VALUES ('296', 'RjY7hZ1Vqe', '22222222', 'Exxon Mobil', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Like \'new\' heat exchanger.\" data-src=\"/assets/img/feedback/Exxon-heat-exchanger.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Exxon Mobil, Heat Team Lead:</p>\r\n<p class=\"quotation\">When we want our equipment &ldquo;really clean&rdquo; we send it to <span class=\"cangc3\">C</span><span class=\"cangc4\">LEAN</span> <span class=\"cangc3\">A</span><span class=\"cangc4\">S</span> <span class=\"cangc3\">N</span><span class=\"cangc4\">EW</span><sup>&reg;</sup>.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'hi-in', '2020-12-26 20:25:32', '2020-12-26 20:25:32');
INSERT INTO `reviews` VALUES ('297', '3IHSkFB29u', '22222222', 'Versa Integrity', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Cleans hard to reach areas in a heat exchanger.\" data-src=\"/assets/img/feedback/Versa-integrity.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Versa Integrity, Operations Manager:</p>\r\n<p class=\"quotation\">The exchanger was so clean our guys could not tell that the exchanger had ever been in service.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'hi-in', '2020-12-26 20:26:04', '2020-12-26 20:26:04');
INSERT INTO `reviews` VALUES ('298', 'Pk6FxVC5fj', '22222222', 'Dow Chemical', '<div class=\"col-4 float-left\"><img class=\"imimg-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean As New&reg; offers a faster cleaning, faster repair turnaround, and faster return to service.\" data-src=\"/assets/img/feedback/Dow.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Dow Chemical, Lead Turnaround Planner:</p>\r\n<p class=\"quotation\">Heard nothing but good things and we are in the process of planning our next outage. Promise if we can send it there to be cleaned, we will. Thanks again.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'hi-in', '2020-12-26 20:26:55', '2020-12-26 20:26:55');
INSERT INTO `reviews` VALUES ('299', '5ZaVWOyXNn', '22222222', 'Shell Sarnia', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean As New&reg; cleans compabloc heat exchangers in a fraction of the time required for hydroblasting.\" data-src=\"/assets/img/feedback/shell-sarina-compabloc.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Sarnia, Refinery Manager:</p>\r\n<p class=\"quotation\">The Refinery decided to test clean four Compabloc units using the new process proposed by Tech Sonic. With the clean Compablocs back in operation, the refinery client captured the throughput and heat transfer data rates to reveal an annual (bpd) recovery of US$4.27M.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'hi-in', '2020-12-26 20:27:22', '2020-12-26 20:27:22');
INSERT INTO `reviews` VALUES ('300', 'Tdsr8iqgOW', '22222222', 'Shell Scotford', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\" Cleans Tube Exchanger back to original specs.\" data-src=\"../assets/img/feedback/shell-scotford.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Scotford:</p>\r\n<p class=\"quotation\">We experienced excellent results with cleaning components for offsite repair/overhaul. 3rd party companies have never seen valves as clean as the ones coming from Scotford. Stainless steel bundles were cleaned better than expected and typically achieved IRIS quality. Bundles with other material composition were generally cleaned to Eddy Current quality. To achieve IRIS spec for these non-stainless items, some were rinsed with a 20Kpsi &amp; rotary lance.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'hi-in', '2020-12-26 20:27:53', '2020-12-26 20:27:53');
INSERT INTO `reviews` VALUES ('301', 'ku0J6orG1c', '22222222', 'Shell Moerdijk', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Like \'new\' rotating equipment.\" data-src=\"/assets/img/feedback/shell-moerdijk-rotator.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Moerdijk, Production Cleaning Manager:</p>\r\n<p class=\"quotation\">Tech Sonic Cleaning Systems are twice as fast at half the cost of traditional hydroblasting. We achieve thorough, complete cleaning that is non-destructive compared to conventional applications.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'hi-in', '2020-12-26 20:28:21', '2020-12-26 20:28:21');
INSERT INTO `reviews` VALUES ('302', 'VoiGD6RmEj', '22222222', 'Shell Global', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"A clean Shell Exchanger improves plant productivity.\" data-src=\"/assets/img/feedback/exchanger-bundles.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Global, Operations &amp; Technology Lead:</p>\r\n<p class=\"quotation\">This is a game changing technology in cleaning effectiveness. Exchanger bundles simply come out cleaner than when addressed with conventional technology. There is a better performance of the bundles on start-up, and these results are achieved in less cleaning time per bundle. This will allow us to be more aggressive in our turnaround schedules in the future. We typically see 70-80% recovery of performance with HP water jetting (caused by residual fouling on the shell side of the bundle). Now we see up to 100% of design heat transfer performance after start-up using the Tech Sonic applications.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'hi-in', '2020-12-26 20:29:49', '2020-12-26 20:29:49');
INSERT INTO `reviews` VALUES ('303', 'Ek36HWw0nK', '22222222', 'Suncor Energy', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic technology is efficient and effective at cleaning dirty scaffolding.\" data-src=\"/assets/img/feedback/suncor-scaffolding.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Suncor Energy:</p>\r\n<p class=\"quotation\">For every dollar we spend with Tech Sonic, we save four dollars in operational and cost efficiencies. We used to have to throw out most of the components as they could not be put back into production. Our capital costs have reduced dramatically.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'hi-in', '2020-12-26 20:30:23', '2020-12-26 20:30:23');
INSERT INTO `reviews` VALUES ('304', 'kfvsjpY5DM', '22222222', 'CF Industrie', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic systems can clean most exchangers for less than half of replacement cost.\" data-src=\"/assets/img/feedback/cf-industries-shell-exchanger.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From CF Industries, Production Manager:</p>\r\n<p class=\"quotation\">We achieved a 27% increase in production of Ammonia after Tech Sonic cleaned our C-109 process train heat exchangers.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'hi-in', '2020-12-26 20:30:53', '2020-12-26 20:30:53');
INSERT INTO `reviews` VALUES ('305', '0gpYVfsHub', '22222222', 'Cenovus Energy', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic systems can clean most packing materials for less than half of replacement cost. \" data-src=\"/assets/img/feedback/cenovus-energy-bucket-pool.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Cenovus Energy, Turnaround Manager:</p>\r\n<p class=\"quotation\">Our flow rates improved from 160 to 260 meters cubed per hour after using Tech Sonic&rsquo;s innovative cleaning technologies. Truly amazing.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'hi-in', '2020-12-26 20:31:20', '2020-12-26 20:31:20');
INSERT INTO `reviews` VALUES ('306', 'EWHeGkP4gy', '22222222', 'Nucor', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean heat exchangers require less energy and improves plant efficiencies.\" data-src=\"/assets/img/feedback/nucor.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Nucor:</p>\r\n<p class=\"quotation\">The heat exchanger that we cleaned Friday is operating very well. Overall heat transfer efficiency increased 50%.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'hi-in', '2020-12-26 20:31:54', '2020-12-26 20:31:54');
INSERT INTO `reviews` VALUES ('307', '8QhLp93xAn', '33333333', 'From Practice-To-Theory-To-Practice: Advances in the Cleaning of Heat Exchangers Using Ultrasound', '<h2>From Practice-To-Theory-To-Practice: Advances in the Cleaning of Heat Exchangers Using Ultrasound</h2>\r\n<p class=\"text-muted\">B. Kieser, A. Goode and R. Philion</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/51_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"A typical exchanger being removed from the ultrasonic bath for inspection and rinsing.\" data-src=\"https://cleanasnew.com/assets/img/tech-sonic-cleaning.jpg\" /></a></p>\r\n<p>The cleaning of heat exchangers, and other large process equipment, using large-scale ultrasonic systems has, in the past few years, progressed from a &ldquo;curiosity&rdquo; to a proven, practical tool for many clients.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/51_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'hi-in', '2020-12-26 20:38:46', '2020-12-26 20:38:46');
INSERT INTO `reviews` VALUES ('308', 'Rbw0omqBJ5', '33333333', 'Ultrasonic Cleaning of Badly Fouled Compabloc Exchangers', '<h2>Ultrasonic Cleaning of Badly Fouled Compabloc Exchangers</h2>\r\n<p class=\"text-muted\">R. Philion, B. Kieser and T. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/49_Kieser-PhilionSMCFL_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Compabloc exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-compablocs-cleaned.jpg\" /></a></p>\r\n<p>This paper describes the use of ultrasonic technologies to clean badly fouled Alfa Laval Compabloc&reg; 106 heat exchangers - which could not be effectively cleaned by conventional methods.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/49_Kieser-PhilionSMCFL_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'hi-in', '2020-12-26 20:40:06', '2020-12-26 20:40:06');
INSERT INTO `reviews` VALUES ('309', 'Wczml95w8J', '33333333', 'Application of Stabilized Hydrogen Peroxide for the Removal of Iron Polysulfide Deposits', '<h2>Application of Stabilized Hydrogen Peroxide for the Removal of Iron Polysulfide Deposits</h2>\r\n<p class=\"text-muted\">T.R. McCartney and R. Shank</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/47_McCartney_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-exchangers-cleaned2.jpg\" /></a></p>\r\n<p>Hydrogen Peroxide is a strong oxidizing agent that could be used for the effective removal of the iron sulfide species.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/47_McCartney_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'hi-in', '2020-12-26 20:45:46', '2020-12-26 20:45:46');
INSERT INTO `reviews` VALUES ('310', 'ireQIuqZ6B', '33333333', 'Understanding the Value of Cleaning - Gains From the Use of Ultrasonic Cleaning of Large Industrial Equipment', '<h2>Understanding the Value of Cleaning - Gains From the Use of Ultrasonic Cleaning of Large Industrial Equipment</h2>\r\n<p class=\"text-muted\">C. H. Schulz and B. Kieser</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2019/41_Schulz%20and%20Kieser.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" style=\"float: none;\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Example of soiling on Quench Oil Exchangers.\" data-src=\"https://cleanasnew.com/assets/img/dirty-heat-exchanger.jpg\" /></a></p>\r\n<p>We present the results of a study to determine the efficacy of large-scale ultrasonic cleaning of heat exchangers at Braskem.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2019/41_Schulz%20and%20Kieser.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'hi-in', '2020-12-26 20:46:29', '2020-12-26 20:46:29');
INSERT INTO `reviews` VALUES ('311', '4ZGDROmkBX', '33333333', 'The Effective Removal of Feco3 Deposits From Critical Process Heat Exchangers Used in Ammonia Production Using a Proprietary Ultrasonic Chemical Cleaning Method', '<h2>The Effective Removal of Feco3 Deposits From Critical Process Heat Exchangers Used in Ammonia Production Using a Proprietary Ultrasonic Chemical Cleaning Method</h2>\r\n<p class=\"text-muted\">R. Philion and T.R. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/50_Philion_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-exchangers-cleaned3.jpg\" /></a></p>\r\n<p>This paper examines the problem of ferrous carbonate fouling and removal in lean - rich Diethanol amine exchangers at the CO2 extraction plants associated with ammonia manufacturing.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/50_Philion_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'hi-in', '2020-12-26 20:47:36', '2020-12-26 20:47:36');
INSERT INTO `reviews` VALUES ('320', '11111111', '11111111', 'Removal Of Iron Carbonate Deposits From Heat Exchangers Used In Ammonia Production Using Tech Sonic Cleaning', '<h2><img style=\"height: auto;\" src=\"https://cleanasnew.com/assets/img/fouling.jpg\" alt=\"A sample of the FeCO3fouling removed from the tube side of the heat exchanger.\" data-src=\"https://cleanasnew.com/assets/img/fouling.jpg\" /></h2>\r\n            <p>The use of ultrasonic technology for cleaning refinery and chemical plant components has been undertaken by Tech Sonic since 2003.</p>', '', null, 'Case Studies', 'pa-pk', null, '2020-12-23 16:23:55');
INSERT INTO `reviews` VALUES ('321', '22222222', '22222222', 'Shell Deer Park', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../../assets/img/1px.png\" alt=\"Tech Sonic technology is efficient and effective at cleaning dirty heat exchanger. \" data-src=\"/assets/img/feedback/Shell-Deer-Park.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Deer Park, Turnaround Manager:</p>\r\n<p class=\"quotation\">I thought everything you guys did was awesome and have already been spreading recommendations to continue using you guys whenever we can!</p>\r\n</blockquote>\r\n</div>', '/assets/img/feedback/Shell-Deer-Park.jpg', 'Turnaround Manager', 'Feedback', 'pa-pk', null, '2020-12-26 20:24:26');
INSERT INTO `reviews` VALUES ('322', '33333333', '33333333', 'The Application of Industrial Scale Ultrasonic Cleaning to Heat Exchangers', '<h2>The Application of Industrial Scale Ultrasonic Cleaning to Heat Exchangers</h2>\r\n<p class=\"text-muted\">B. Kieser, R. Phillion and T. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Tube Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" /></a></p>\r\n<p>The cleaning of fouled heat exchangers often presents a significant challenge to the maintenance and operation of chemical, petroleum and food processes.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', 'assets/img/tech-sonic-vessel-sm.jpg', null, 'Referred Journal Publications', 'pa-pk', null, '2020-12-26 20:37:56');
INSERT INTO `reviews` VALUES ('323', 'o71Uy3sYSH', '11111111', 'Off-Line Ultrasonic Cleaning – Restoring As Built Performance During Turnarounds', '<p class=\"text-muted\" style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa; color: #6c757d !important;\">Gwen van de Bilt, Field Supervisor, Waste and Cleaning, Shell Moerdijk</p>\r\n<p><img class=\"img-fluid polaroid\" style=\"box-sizing: border-box; vertical-align: middle; border-style: none; max-width: 100%; height: auto; box-shadow: rgba(0, 0, 0, 0.2) 0px 4px 8px 0px, rgba(0, 0, 0, 0.19) 0px 6px 20px 0px; float: left; margin-bottom: 8.48438px; margin-left: 0px; font-family: Oswald, sans-serif; font-size: 16px; background-color: #f8f9fa;\" src=\"https://cleanasnew.com/img/before-and-after-filters-cleaned.jpg\" alt=\"Typical Wedge Wire filter for cleaning, multiple filters can be cleaned at the same time with only a low-pressure rinse.\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa;\">In April 2013 the Shell Moerdijk Chemicals plant conducted the biggest&ndash;ever TA event in the history of the site. One of the successes we had involved employing off-line ultrasonic cleaning.</p>', null, null, 'Case Studies', 'pa-pk', '2020-12-26 14:23:34', '2020-12-26 14:23:34');
INSERT INTO `reviews` VALUES ('324', 'c48qWa5AoR', '11111111', 'Tech Sonic Cleaning System Helps ConocoPhillips Neutralize and Clean Valves', '<h2>Tech Sonic Cleaning System Helps ConocoPhillips Neutralize<br />and Clean Valve</h2>\r\n<p class=\"text-muted\">&nbsp;</p>\r\n<p><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Monel Plug valve after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-valves-cleaned.jpg\" /></p>\r\n<p>ConocoPhillips is one of the largest refiners in the United States. Its Ponca City, Oklahoma refiner , with a crude oil processing capacity of 187,000 per day, processes a mixture of light, medium...</p>\r\n<p><a class=\"btn btn-default\" href=\"https://cleanasnew.com/case-studies/ConocoPhllips-Valve-Service.pdf\" target=\"_blank\" rel=\"noopener\">Download Case Study </a></p>', null, null, 'Case Studies', 'pa-pk', '2020-12-26 20:14:42', '2020-12-26 20:14:42');
INSERT INTO `reviews` VALUES ('325', 'GynfsMdkKN', '11111111', 'Upgrader Equipment Gets The Spa Treatment', '<h2>Upgrader Equipment Gets The Spa Treatment</h2>\r\n<p class=\"text-muted\">Tanya Ristoff, Communications Manager, Shell Scotford</p>\r\n<p><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Pictured here &ndash;an exchanger bundle is lowered into an ultrasonic bath by crane.\" data-src=\"https://cleanasnew.com/assets/img/scotford-upgrader-turnaround-case-study.jpg\" /></p>\r\n<p>At first glance, it looks like a big equipment soaker tub, but there&rsquo;s much more than meets the eye when it comes to Tech Sonic Services&rsquo; ultrasonic wash technology.</p>\r\n<p><a class=\"btn btn-default\" href=\"https://cleanasnew.com/case-studies/Scotford-Upgrader-Turnaround.pdf\" target=\"_blank\" rel=\"noopener\">Download Case Study </a></p>', null, null, 'Case Studies', 'pa-pk', '2020-12-26 20:15:47', '2020-12-26 20:15:47');
INSERT INTO `reviews` VALUES ('326', 'ZdgtQSLRDz', '22222222', 'Alfa Laval', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\" Compabloc heat exchanger before and after cleaning.\" data-src=\"/assets/img/feedback/AlfaLaval.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Alfa Laval, Business Manager:</p>\r\n<p class=\"quotation\">As a test Ulrik had our small test decanter into their shop for a 24 hour turn around. The equipment was immaculately cleaned within the required time requested with very little concern to do so on <span class=\"cangc3\">C</span><span class=\"cangc4\">LEAN</span> <span class=\"cangc3\">A</span><span class=\"cangc4\">S</span> <span class=\"cangc3\">N</span><span class=\"cangc4\">EW</span><sup>&reg;</sup>&rsquo;s process.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'pa-pk', '2020-12-26 20:24:58', '2020-12-26 20:24:58');
INSERT INTO `reviews` VALUES ('327', 'RjY7hZ1Vqe', '22222222', 'Exxon Mobil', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Like \'new\' heat exchanger.\" data-src=\"/assets/img/feedback/Exxon-heat-exchanger.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Exxon Mobil, Heat Team Lead:</p>\r\n<p class=\"quotation\">When we want our equipment &ldquo;really clean&rdquo; we send it to <span class=\"cangc3\">C</span><span class=\"cangc4\">LEAN</span> <span class=\"cangc3\">A</span><span class=\"cangc4\">S</span> <span class=\"cangc3\">N</span><span class=\"cangc4\">EW</span><sup>&reg;</sup>.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'pa-pk', '2020-12-26 20:25:32', '2020-12-26 20:25:32');
INSERT INTO `reviews` VALUES ('328', '3IHSkFB29u', '22222222', 'Versa Integrity', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Cleans hard to reach areas in a heat exchanger.\" data-src=\"/assets/img/feedback/Versa-integrity.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Versa Integrity, Operations Manager:</p>\r\n<p class=\"quotation\">The exchanger was so clean our guys could not tell that the exchanger had ever been in service.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'pa-pk', '2020-12-26 20:26:04', '2020-12-26 20:26:04');
INSERT INTO `reviews` VALUES ('329', 'Pk6FxVC5fj', '22222222', 'Dow Chemical', '<div class=\"col-4 float-left\"><img class=\"imimg-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean As New&reg; offers a faster cleaning, faster repair turnaround, and faster return to service.\" data-src=\"/assets/img/feedback/Dow.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Dow Chemical, Lead Turnaround Planner:</p>\r\n<p class=\"quotation\">Heard nothing but good things and we are in the process of planning our next outage. Promise if we can send it there to be cleaned, we will. Thanks again.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'pa-pk', '2020-12-26 20:26:55', '2020-12-26 20:26:55');
INSERT INTO `reviews` VALUES ('330', '5ZaVWOyXNn', '22222222', 'Shell Sarnia', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean As New&reg; cleans compabloc heat exchangers in a fraction of the time required for hydroblasting.\" data-src=\"/assets/img/feedback/shell-sarina-compabloc.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Sarnia, Refinery Manager:</p>\r\n<p class=\"quotation\">The Refinery decided to test clean four Compabloc units using the new process proposed by Tech Sonic. With the clean Compablocs back in operation, the refinery client captured the throughput and heat transfer data rates to reveal an annual (bpd) recovery of US$4.27M.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'pa-pk', '2020-12-26 20:27:22', '2020-12-26 20:27:22');
INSERT INTO `reviews` VALUES ('331', 'Tdsr8iqgOW', '22222222', 'Shell Scotford', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\" Cleans Tube Exchanger back to original specs.\" data-src=\"../assets/img/feedback/shell-scotford.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Scotford:</p>\r\n<p class=\"quotation\">We experienced excellent results with cleaning components for offsite repair/overhaul. 3rd party companies have never seen valves as clean as the ones coming from Scotford. Stainless steel bundles were cleaned better than expected and typically achieved IRIS quality. Bundles with other material composition were generally cleaned to Eddy Current quality. To achieve IRIS spec for these non-stainless items, some were rinsed with a 20Kpsi &amp; rotary lance.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'pa-pk', '2020-12-26 20:27:53', '2020-12-26 20:27:53');
INSERT INTO `reviews` VALUES ('332', 'ku0J6orG1c', '22222222', 'Shell Moerdijk', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Like \'new\' rotating equipment.\" data-src=\"/assets/img/feedback/shell-moerdijk-rotator.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Moerdijk, Production Cleaning Manager:</p>\r\n<p class=\"quotation\">Tech Sonic Cleaning Systems are twice as fast at half the cost of traditional hydroblasting. We achieve thorough, complete cleaning that is non-destructive compared to conventional applications.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'pa-pk', '2020-12-26 20:28:21', '2020-12-26 20:28:21');
INSERT INTO `reviews` VALUES ('333', 'VoiGD6RmEj', '22222222', 'Shell Global', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"A clean Shell Exchanger improves plant productivity.\" data-src=\"/assets/img/feedback/exchanger-bundles.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Global, Operations &amp; Technology Lead:</p>\r\n<p class=\"quotation\">This is a game changing technology in cleaning effectiveness. Exchanger bundles simply come out cleaner than when addressed with conventional technology. There is a better performance of the bundles on start-up, and these results are achieved in less cleaning time per bundle. This will allow us to be more aggressive in our turnaround schedules in the future. We typically see 70-80% recovery of performance with HP water jetting (caused by residual fouling on the shell side of the bundle). Now we see up to 100% of design heat transfer performance after start-up using the Tech Sonic applications.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'pa-pk', '2020-12-26 20:29:49', '2020-12-26 20:29:49');
INSERT INTO `reviews` VALUES ('334', 'Ek36HWw0nK', '22222222', 'Suncor Energy', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic technology is efficient and effective at cleaning dirty scaffolding.\" data-src=\"/assets/img/feedback/suncor-scaffolding.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Suncor Energy:</p>\r\n<p class=\"quotation\">For every dollar we spend with Tech Sonic, we save four dollars in operational and cost efficiencies. We used to have to throw out most of the components as they could not be put back into production. Our capital costs have reduced dramatically.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'pa-pk', '2020-12-26 20:30:23', '2020-12-26 20:30:23');
INSERT INTO `reviews` VALUES ('335', 'kfvsjpY5DM', '22222222', 'CF Industrie', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic systems can clean most exchangers for less than half of replacement cost.\" data-src=\"/assets/img/feedback/cf-industries-shell-exchanger.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From CF Industries, Production Manager:</p>\r\n<p class=\"quotation\">We achieved a 27% increase in production of Ammonia after Tech Sonic cleaned our C-109 process train heat exchangers.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'pa-pk', '2020-12-26 20:30:53', '2020-12-26 20:30:53');
INSERT INTO `reviews` VALUES ('336', '0gpYVfsHub', '22222222', 'Cenovus Energy', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic systems can clean most packing materials for less than half of replacement cost. \" data-src=\"/assets/img/feedback/cenovus-energy-bucket-pool.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Cenovus Energy, Turnaround Manager:</p>\r\n<p class=\"quotation\">Our flow rates improved from 160 to 260 meters cubed per hour after using Tech Sonic&rsquo;s innovative cleaning technologies. Truly amazing.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'pa-pk', '2020-12-26 20:31:20', '2020-12-26 20:31:20');
INSERT INTO `reviews` VALUES ('337', 'EWHeGkP4gy', '22222222', 'Nucor', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean heat exchangers require less energy and improves plant efficiencies.\" data-src=\"/assets/img/feedback/nucor.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Nucor:</p>\r\n<p class=\"quotation\">The heat exchanger that we cleaned Friday is operating very well. Overall heat transfer efficiency increased 50%.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'pa-pk', '2020-12-26 20:31:54', '2020-12-26 20:31:54');
INSERT INTO `reviews` VALUES ('338', '8QhLp93xAn', '33333333', 'From Practice-To-Theory-To-Practice: Advances in the Cleaning of Heat Exchangers Using Ultrasound', '<h2>From Practice-To-Theory-To-Practice: Advances in the Cleaning of Heat Exchangers Using Ultrasound</h2>\r\n<p class=\"text-muted\">B. Kieser, A. Goode and R. Philion</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/51_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"A typical exchanger being removed from the ultrasonic bath for inspection and rinsing.\" data-src=\"https://cleanasnew.com/assets/img/tech-sonic-cleaning.jpg\" /></a></p>\r\n<p>The cleaning of heat exchangers, and other large process equipment, using large-scale ultrasonic systems has, in the past few years, progressed from a &ldquo;curiosity&rdquo; to a proven, practical tool for many clients.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/51_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'pa-pk', '2020-12-26 20:38:46', '2020-12-26 20:38:46');
INSERT INTO `reviews` VALUES ('339', 'Rbw0omqBJ5', '33333333', 'Ultrasonic Cleaning of Badly Fouled Compabloc Exchangers', '<h2>Ultrasonic Cleaning of Badly Fouled Compabloc Exchangers</h2>\r\n<p class=\"text-muted\">R. Philion, B. Kieser and T. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/49_Kieser-PhilionSMCFL_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Compabloc exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-compablocs-cleaned.jpg\" /></a></p>\r\n<p>This paper describes the use of ultrasonic technologies to clean badly fouled Alfa Laval Compabloc&reg; 106 heat exchangers - which could not be effectively cleaned by conventional methods.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/49_Kieser-PhilionSMCFL_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'pa-pk', '2020-12-26 20:40:06', '2020-12-26 20:40:06');
INSERT INTO `reviews` VALUES ('340', 'Wczml95w8J', '33333333', 'Application of Stabilized Hydrogen Peroxide for the Removal of Iron Polysulfide Deposits', '<h2>Application of Stabilized Hydrogen Peroxide for the Removal of Iron Polysulfide Deposits</h2>\r\n<p class=\"text-muted\">T.R. McCartney and R. Shank</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/47_McCartney_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-exchangers-cleaned2.jpg\" /></a></p>\r\n<p>Hydrogen Peroxide is a strong oxidizing agent that could be used for the effective removal of the iron sulfide species.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/47_McCartney_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'pa-pk', '2020-12-26 20:45:46', '2020-12-26 20:45:46');
INSERT INTO `reviews` VALUES ('341', 'ireQIuqZ6B', '33333333', 'Understanding the Value of Cleaning - Gains From the Use of Ultrasonic Cleaning of Large Industrial Equipment', '<h2>Understanding the Value of Cleaning - Gains From the Use of Ultrasonic Cleaning of Large Industrial Equipment</h2>\r\n<p class=\"text-muted\">C. H. Schulz and B. Kieser</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2019/41_Schulz%20and%20Kieser.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" style=\"float: none;\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Example of soiling on Quench Oil Exchangers.\" data-src=\"https://cleanasnew.com/assets/img/dirty-heat-exchanger.jpg\" /></a></p>\r\n<p>We present the results of a study to determine the efficacy of large-scale ultrasonic cleaning of heat exchangers at Braskem.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2019/41_Schulz%20and%20Kieser.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'pa-pk', '2020-12-26 20:46:29', '2020-12-26 20:46:29');
INSERT INTO `reviews` VALUES ('342', '4ZGDROmkBX', '33333333', 'The Effective Removal of Feco3 Deposits From Critical Process Heat Exchangers Used in Ammonia Production Using a Proprietary Ultrasonic Chemical Cleaning Method', '<h2>The Effective Removal of Feco3 Deposits From Critical Process Heat Exchangers Used in Ammonia Production Using a Proprietary Ultrasonic Chemical Cleaning Method</h2>\r\n<p class=\"text-muted\">R. Philion and T.R. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/50_Philion_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-exchangers-cleaned3.jpg\" /></a></p>\r\n<p>This paper examines the problem of ferrous carbonate fouling and removal in lean - rich Diethanol amine exchangers at the CO2 extraction plants associated with ammonia manufacturing.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/50_Philion_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'pa-pk', '2020-12-26 20:47:36', '2020-12-26 20:47:36');
INSERT INTO `reviews` VALUES ('351', '11111111', '11111111', 'Removal Of Iron Carbonate Deposits From Heat Exchangers Used In Ammonia Production Using Tech Sonic Cleaning', '<h2><img style=\"height: auto;\" src=\"https://cleanasnew.com/assets/img/fouling.jpg\" alt=\"A sample of the FeCO3fouling removed from the tube side of the heat exchanger.\" data-src=\"https://cleanasnew.com/assets/img/fouling.jpg\" /></h2>\r\n            <p>The use of ultrasonic technology for cleaning refinery and chemical plant components has been undertaken by Tech Sonic since 2003.</p>', '', null, 'Case Studies', 'cn', null, '2020-12-23 16:23:55');
INSERT INTO `reviews` VALUES ('352', '22222222', '22222222', 'Shell Deer Park', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../../assets/img/1px.png\" alt=\"Tech Sonic technology is efficient and effective at cleaning dirty heat exchanger. \" data-src=\"/assets/img/feedback/Shell-Deer-Park.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Deer Park, Turnaround Manager:</p>\r\n<p class=\"quotation\">I thought everything you guys did was awesome and have already been spreading recommendations to continue using you guys whenever we can!</p>\r\n</blockquote>\r\n</div>', '/assets/img/feedback/Shell-Deer-Park.jpg', 'Turnaround Manager', 'Feedback', 'cn', null, '2020-12-26 20:24:26');
INSERT INTO `reviews` VALUES ('353', '33333333', '33333333', 'The Application of Industrial Scale Ultrasonic Cleaning to Heat Exchangers', '<h2>The Application of Industrial Scale Ultrasonic Cleaning to Heat Exchangers</h2>\r\n<p class=\"text-muted\">B. Kieser, R. Phillion and T. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Tube Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" /></a></p>\r\n<p>The cleaning of fouled heat exchangers often presents a significant challenge to the maintenance and operation of chemical, petroleum and food processes.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', 'assets/img/tech-sonic-vessel-sm.jpg', null, 'Referred Journal Publications', 'cn', null, '2020-12-26 20:37:56');
INSERT INTO `reviews` VALUES ('354', 'o71Uy3sYSH', '11111111', 'Off-Line Ultrasonic Cleaning – Restoring As Built Performance During Turnarounds', '<p class=\"text-muted\" style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa; color: #6c757d !important;\">Gwen van de Bilt, Field Supervisor, Waste and Cleaning, Shell Moerdijk</p>\r\n<p><img class=\"img-fluid polaroid\" style=\"box-sizing: border-box; vertical-align: middle; border-style: none; max-width: 100%; height: auto; box-shadow: rgba(0, 0, 0, 0.2) 0px 4px 8px 0px, rgba(0, 0, 0, 0.19) 0px 6px 20px 0px; float: left; margin-bottom: 8.48438px; margin-left: 0px; font-family: Oswald, sans-serif; font-size: 16px; background-color: #f8f9fa;\" src=\"https://cleanasnew.com/img/before-and-after-filters-cleaned.jpg\" alt=\"Typical Wedge Wire filter for cleaning, multiple filters can be cleaned at the same time with only a low-pressure rinse.\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa;\">In April 2013 the Shell Moerdijk Chemicals plant conducted the biggest&ndash;ever TA event in the history of the site. One of the successes we had involved employing off-line ultrasonic cleaning.</p>', null, null, 'Case Studies', 'cn', '2020-12-26 14:23:34', '2020-12-26 14:23:34');
INSERT INTO `reviews` VALUES ('355', 'c48qWa5AoR', '11111111', 'Tech Sonic Cleaning System Helps ConocoPhillips Neutralize and Clean Valves', '<h2>Tech Sonic Cleaning System Helps ConocoPhillips Neutralize<br />and Clean Valve</h2>\r\n<p class=\"text-muted\">&nbsp;</p>\r\n<p><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Monel Plug valve after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-valves-cleaned.jpg\" /></p>\r\n<p>ConocoPhillips is one of the largest refiners in the United States. Its Ponca City, Oklahoma refiner , with a crude oil processing capacity of 187,000 per day, processes a mixture of light, medium...</p>\r\n<p><a class=\"btn btn-default\" href=\"https://cleanasnew.com/case-studies/ConocoPhllips-Valve-Service.pdf\" target=\"_blank\" rel=\"noopener\">Download Case Study </a></p>', null, null, 'Case Studies', 'cn', '2020-12-26 20:14:42', '2020-12-26 20:14:42');
INSERT INTO `reviews` VALUES ('356', 'GynfsMdkKN', '11111111', 'Upgrader Equipment Gets The Spa Treatment', '<h2>Upgrader Equipment Gets The Spa Treatment</h2>\r\n<p class=\"text-muted\">Tanya Ristoff, Communications Manager, Shell Scotford</p>\r\n<p><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Pictured here &ndash;an exchanger bundle is lowered into an ultrasonic bath by crane.\" data-src=\"https://cleanasnew.com/assets/img/scotford-upgrader-turnaround-case-study.jpg\" /></p>\r\n<p>At first glance, it looks like a big equipment soaker tub, but there&rsquo;s much more than meets the eye when it comes to Tech Sonic Services&rsquo; ultrasonic wash technology.</p>\r\n<p><a class=\"btn btn-default\" href=\"https://cleanasnew.com/case-studies/Scotford-Upgrader-Turnaround.pdf\" target=\"_blank\" rel=\"noopener\">Download Case Study </a></p>', null, null, 'Case Studies', 'cn', '2020-12-26 20:15:47', '2020-12-26 20:15:47');
INSERT INTO `reviews` VALUES ('357', 'ZdgtQSLRDz', '22222222', 'Alfa Laval', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\" Compabloc heat exchanger before and after cleaning.\" data-src=\"/assets/img/feedback/AlfaLaval.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Alfa Laval, Business Manager:</p>\r\n<p class=\"quotation\">As a test Ulrik had our small test decanter into their shop for a 24 hour turn around. The equipment was immaculately cleaned within the required time requested with very little concern to do so on <span class=\"cangc3\">C</span><span class=\"cangc4\">LEAN</span> <span class=\"cangc3\">A</span><span class=\"cangc4\">S</span> <span class=\"cangc3\">N</span><span class=\"cangc4\">EW</span><sup>&reg;</sup>&rsquo;s process.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'cn', '2020-12-26 20:24:58', '2020-12-26 20:24:58');
INSERT INTO `reviews` VALUES ('358', 'RjY7hZ1Vqe', '22222222', 'Exxon Mobil', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Like \'new\' heat exchanger.\" data-src=\"/assets/img/feedback/Exxon-heat-exchanger.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Exxon Mobil, Heat Team Lead:</p>\r\n<p class=\"quotation\">When we want our equipment &ldquo;really clean&rdquo; we send it to <span class=\"cangc3\">C</span><span class=\"cangc4\">LEAN</span> <span class=\"cangc3\">A</span><span class=\"cangc4\">S</span> <span class=\"cangc3\">N</span><span class=\"cangc4\">EW</span><sup>&reg;</sup>.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'cn', '2020-12-26 20:25:32', '2020-12-26 20:25:32');
INSERT INTO `reviews` VALUES ('359', '3IHSkFB29u', '22222222', 'Versa Integrity', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Cleans hard to reach areas in a heat exchanger.\" data-src=\"/assets/img/feedback/Versa-integrity.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Versa Integrity, Operations Manager:</p>\r\n<p class=\"quotation\">The exchanger was so clean our guys could not tell that the exchanger had ever been in service.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'cn', '2020-12-26 20:26:04', '2020-12-26 20:26:04');
INSERT INTO `reviews` VALUES ('360', 'Pk6FxVC5fj', '22222222', 'Dow Chemical', '<div class=\"col-4 float-left\"><img class=\"imimg-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean As New&reg; offers a faster cleaning, faster repair turnaround, and faster return to service.\" data-src=\"/assets/img/feedback/Dow.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Dow Chemical, Lead Turnaround Planner:</p>\r\n<p class=\"quotation\">Heard nothing but good things and we are in the process of planning our next outage. Promise if we can send it there to be cleaned, we will. Thanks again.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'cn', '2020-12-26 20:26:55', '2020-12-26 20:26:55');
INSERT INTO `reviews` VALUES ('361', '5ZaVWOyXNn', '22222222', 'Shell Sarnia', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean As New&reg; cleans compabloc heat exchangers in a fraction of the time required for hydroblasting.\" data-src=\"/assets/img/feedback/shell-sarina-compabloc.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Sarnia, Refinery Manager:</p>\r\n<p class=\"quotation\">The Refinery decided to test clean four Compabloc units using the new process proposed by Tech Sonic. With the clean Compablocs back in operation, the refinery client captured the throughput and heat transfer data rates to reveal an annual (bpd) recovery of US$4.27M.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'cn', '2020-12-26 20:27:22', '2020-12-26 20:27:22');
INSERT INTO `reviews` VALUES ('362', 'Tdsr8iqgOW', '22222222', 'Shell Scotford', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\" Cleans Tube Exchanger back to original specs.\" data-src=\"../assets/img/feedback/shell-scotford.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Scotford:</p>\r\n<p class=\"quotation\">We experienced excellent results with cleaning components for offsite repair/overhaul. 3rd party companies have never seen valves as clean as the ones coming from Scotford. Stainless steel bundles were cleaned better than expected and typically achieved IRIS quality. Bundles with other material composition were generally cleaned to Eddy Current quality. To achieve IRIS spec for these non-stainless items, some were rinsed with a 20Kpsi &amp; rotary lance.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'cn', '2020-12-26 20:27:53', '2020-12-26 20:27:53');
INSERT INTO `reviews` VALUES ('363', 'ku0J6orG1c', '22222222', 'Shell Moerdijk', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Like \'new\' rotating equipment.\" data-src=\"/assets/img/feedback/shell-moerdijk-rotator.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Moerdijk, Production Cleaning Manager:</p>\r\n<p class=\"quotation\">Tech Sonic Cleaning Systems are twice as fast at half the cost of traditional hydroblasting. We achieve thorough, complete cleaning that is non-destructive compared to conventional applications.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'cn', '2020-12-26 20:28:21', '2020-12-26 20:28:21');
INSERT INTO `reviews` VALUES ('364', 'VoiGD6RmEj', '22222222', 'Shell Global', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"A clean Shell Exchanger improves plant productivity.\" data-src=\"/assets/img/feedback/exchanger-bundles.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Global, Operations &amp; Technology Lead:</p>\r\n<p class=\"quotation\">This is a game changing technology in cleaning effectiveness. Exchanger bundles simply come out cleaner than when addressed with conventional technology. There is a better performance of the bundles on start-up, and these results are achieved in less cleaning time per bundle. This will allow us to be more aggressive in our turnaround schedules in the future. We typically see 70-80% recovery of performance with HP water jetting (caused by residual fouling on the shell side of the bundle). Now we see up to 100% of design heat transfer performance after start-up using the Tech Sonic applications.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'cn', '2020-12-26 20:29:49', '2020-12-26 20:29:49');
INSERT INTO `reviews` VALUES ('365', 'Ek36HWw0nK', '22222222', 'Suncor Energy', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic technology is efficient and effective at cleaning dirty scaffolding.\" data-src=\"/assets/img/feedback/suncor-scaffolding.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Suncor Energy:</p>\r\n<p class=\"quotation\">For every dollar we spend with Tech Sonic, we save four dollars in operational and cost efficiencies. We used to have to throw out most of the components as they could not be put back into production. Our capital costs have reduced dramatically.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'cn', '2020-12-26 20:30:23', '2020-12-26 20:30:23');
INSERT INTO `reviews` VALUES ('366', 'kfvsjpY5DM', '22222222', 'CF Industrie', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic systems can clean most exchangers for less than half of replacement cost.\" data-src=\"/assets/img/feedback/cf-industries-shell-exchanger.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From CF Industries, Production Manager:</p>\r\n<p class=\"quotation\">We achieved a 27% increase in production of Ammonia after Tech Sonic cleaned our C-109 process train heat exchangers.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'cn', '2020-12-26 20:30:53', '2020-12-26 20:30:53');
INSERT INTO `reviews` VALUES ('367', '0gpYVfsHub', '22222222', 'Cenovus Energy', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic systems can clean most packing materials for less than half of replacement cost. \" data-src=\"/assets/img/feedback/cenovus-energy-bucket-pool.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Cenovus Energy, Turnaround Manager:</p>\r\n<p class=\"quotation\">Our flow rates improved from 160 to 260 meters cubed per hour after using Tech Sonic&rsquo;s innovative cleaning technologies. Truly amazing.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'cn', '2020-12-26 20:31:20', '2020-12-26 20:31:20');
INSERT INTO `reviews` VALUES ('368', 'EWHeGkP4gy', '22222222', 'Nucor', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean heat exchangers require less energy and improves plant efficiencies.\" data-src=\"/assets/img/feedback/nucor.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Nucor:</p>\r\n<p class=\"quotation\">The heat exchanger that we cleaned Friday is operating very well. Overall heat transfer efficiency increased 50%.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'cn', '2020-12-26 20:31:54', '2020-12-26 20:31:54');
INSERT INTO `reviews` VALUES ('369', '8QhLp93xAn', '33333333', 'From Practice-To-Theory-To-Practice: Advances in the Cleaning of Heat Exchangers Using Ultrasound', '<h2>From Practice-To-Theory-To-Practice: Advances in the Cleaning of Heat Exchangers Using Ultrasound</h2>\r\n<p class=\"text-muted\">B. Kieser, A. Goode and R. Philion</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/51_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"A typical exchanger being removed from the ultrasonic bath for inspection and rinsing.\" data-src=\"https://cleanasnew.com/assets/img/tech-sonic-cleaning.jpg\" /></a></p>\r\n<p>The cleaning of heat exchangers, and other large process equipment, using large-scale ultrasonic systems has, in the past few years, progressed from a &ldquo;curiosity&rdquo; to a proven, practical tool for many clients.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/51_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'cn', '2020-12-26 20:38:46', '2020-12-26 20:38:46');
INSERT INTO `reviews` VALUES ('370', 'Rbw0omqBJ5', '33333333', 'Ultrasonic Cleaning of Badly Fouled Compabloc Exchangers', '<h2>Ultrasonic Cleaning of Badly Fouled Compabloc Exchangers</h2>\r\n<p class=\"text-muted\">R. Philion, B. Kieser and T. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/49_Kieser-PhilionSMCFL_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Compabloc exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-compablocs-cleaned.jpg\" /></a></p>\r\n<p>This paper describes the use of ultrasonic technologies to clean badly fouled Alfa Laval Compabloc&reg; 106 heat exchangers - which could not be effectively cleaned by conventional methods.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/49_Kieser-PhilionSMCFL_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'cn', '2020-12-26 20:40:06', '2020-12-26 20:40:06');
INSERT INTO `reviews` VALUES ('371', 'Wczml95w8J', '33333333', 'Application of Stabilized Hydrogen Peroxide for the Removal of Iron Polysulfide Deposits', '<h2>Application of Stabilized Hydrogen Peroxide for the Removal of Iron Polysulfide Deposits</h2>\r\n<p class=\"text-muted\">T.R. McCartney and R. Shank</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/47_McCartney_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-exchangers-cleaned2.jpg\" /></a></p>\r\n<p>Hydrogen Peroxide is a strong oxidizing agent that could be used for the effective removal of the iron sulfide species.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/47_McCartney_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'cn', '2020-12-26 20:45:46', '2020-12-26 20:45:46');
INSERT INTO `reviews` VALUES ('372', 'ireQIuqZ6B', '33333333', 'Understanding the Value of Cleaning - Gains From the Use of Ultrasonic Cleaning of Large Industrial Equipment', '<h2>Understanding the Value of Cleaning - Gains From the Use of Ultrasonic Cleaning of Large Industrial Equipment</h2>\r\n<p class=\"text-muted\">C. H. Schulz and B. Kieser</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2019/41_Schulz%20and%20Kieser.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" style=\"float: none;\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Example of soiling on Quench Oil Exchangers.\" data-src=\"https://cleanasnew.com/assets/img/dirty-heat-exchanger.jpg\" /></a></p>\r\n<p>We present the results of a study to determine the efficacy of large-scale ultrasonic cleaning of heat exchangers at Braskem.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2019/41_Schulz%20and%20Kieser.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'cn', '2020-12-26 20:46:29', '2020-12-26 20:46:29');
INSERT INTO `reviews` VALUES ('373', '4ZGDROmkBX', '33333333', 'The Effective Removal of Feco3 Deposits From Critical Process Heat Exchangers Used in Ammonia Production Using a Proprietary Ultrasonic Chemical Cleaning Method', '<h2>The Effective Removal of Feco3 Deposits From Critical Process Heat Exchangers Used in Ammonia Production Using a Proprietary Ultrasonic Chemical Cleaning Method</h2>\r\n<p class=\"text-muted\">R. Philion and T.R. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/50_Philion_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-exchangers-cleaned3.jpg\" /></a></p>\r\n<p>This paper examines the problem of ferrous carbonate fouling and removal in lean - rich Diethanol amine exchangers at the CO2 extraction plants associated with ammonia manufacturing.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/50_Philion_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'cn', '2020-12-26 20:47:36', '2020-12-26 20:47:36');
INSERT INTO `reviews` VALUES ('382', '11111111', '11111111', 'Removal Of Iron Carbonate Deposits From Heat Exchangers Used In Ammonia Production Using Tech Sonic Cleaning', '<h2><img style=\"height: auto;\" src=\"https://cleanasnew.com/assets/img/fouling.jpg\" alt=\"A sample of the FeCO3fouling removed from the tube side of the heat exchanger.\" data-src=\"https://cleanasnew.com/assets/img/fouling.jpg\" /></h2>\r\n            <p>The use of ultrasonic technology for cleaning refinery and chemical plant components has been undertaken by Tech Sonic since 2003.</p>', '', null, 'Case Studies', 'jp', null, '2020-12-23 16:23:55');
INSERT INTO `reviews` VALUES ('383', '22222222', '22222222', 'Shell Deer Park', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../../assets/img/1px.png\" alt=\"Tech Sonic technology is efficient and effective at cleaning dirty heat exchanger. \" data-src=\"/assets/img/feedback/Shell-Deer-Park.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Deer Park, Turnaround Manager:</p>\r\n<p class=\"quotation\">I thought everything you guys did was awesome and have already been spreading recommendations to continue using you guys whenever we can!</p>\r\n</blockquote>\r\n</div>', '/assets/img/feedback/Shell-Deer-Park.jpg', 'Turnaround Manager', 'Feedback', 'jp', null, '2020-12-26 20:24:26');
INSERT INTO `reviews` VALUES ('384', '33333333', '33333333', 'The Application of Industrial Scale Ultrasonic Cleaning to Heat Exchangers', '<h2>The Application of Industrial Scale Ultrasonic Cleaning to Heat Exchangers</h2>\r\n<p class=\"text-muted\">B. Kieser, R. Phillion and T. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Tube Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" /></a></p>\r\n<p>The cleaning of fouled heat exchangers often presents a significant challenge to the maintenance and operation of chemical, petroleum and food processes.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', 'assets/img/tech-sonic-vessel-sm.jpg', null, 'Referred Journal Publications', 'jp', null, '2020-12-26 20:37:56');
INSERT INTO `reviews` VALUES ('385', 'o71Uy3sYSH', '11111111', 'Off-Line Ultrasonic Cleaning – Restoring As Built Performance During Turnarounds', '<p class=\"text-muted\" style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa; color: #6c757d !important;\">Gwen van de Bilt, Field Supervisor, Waste and Cleaning, Shell Moerdijk</p>\r\n<p><img class=\"img-fluid polaroid\" style=\"box-sizing: border-box; vertical-align: middle; border-style: none; max-width: 100%; height: auto; box-shadow: rgba(0, 0, 0, 0.2) 0px 4px 8px 0px, rgba(0, 0, 0, 0.19) 0px 6px 20px 0px; float: left; margin-bottom: 8.48438px; margin-left: 0px; font-family: Oswald, sans-serif; font-size: 16px; background-color: #f8f9fa;\" src=\"https://cleanasnew.com/img/before-and-after-filters-cleaned.jpg\" alt=\"Typical Wedge Wire filter for cleaning, multiple filters can be cleaned at the same time with only a low-pressure rinse.\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa;\">In April 2013 the Shell Moerdijk Chemicals plant conducted the biggest&ndash;ever TA event in the history of the site. One of the successes we had involved employing off-line ultrasonic cleaning.</p>', null, null, 'Case Studies', 'jp', '2020-12-26 14:23:34', '2020-12-26 14:23:34');
INSERT INTO `reviews` VALUES ('386', 'c48qWa5AoR', '11111111', 'Tech Sonic Cleaning System Helps ConocoPhillips Neutralize and Clean Valves', '<h2>Tech Sonic Cleaning System Helps ConocoPhillips Neutralize<br />and Clean Valve</h2>\r\n<p class=\"text-muted\">&nbsp;</p>\r\n<p><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Monel Plug valve after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-valves-cleaned.jpg\" /></p>\r\n<p>ConocoPhillips is one of the largest refiners in the United States. Its Ponca City, Oklahoma refiner , with a crude oil processing capacity of 187,000 per day, processes a mixture of light, medium...</p>\r\n<p><a class=\"btn btn-default\" href=\"https://cleanasnew.com/case-studies/ConocoPhllips-Valve-Service.pdf\" target=\"_blank\" rel=\"noopener\">Download Case Study </a></p>', null, null, 'Case Studies', 'jp', '2020-12-26 20:14:42', '2020-12-26 20:14:42');
INSERT INTO `reviews` VALUES ('387', 'GynfsMdkKN', '11111111', 'Upgrader Equipment Gets The Spa Treatment', '<h2>Upgrader Equipment Gets The Spa Treatment</h2>\r\n<p class=\"text-muted\">Tanya Ristoff, Communications Manager, Shell Scotford</p>\r\n<p><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Pictured here &ndash;an exchanger bundle is lowered into an ultrasonic bath by crane.\" data-src=\"https://cleanasnew.com/assets/img/scotford-upgrader-turnaround-case-study.jpg\" /></p>\r\n<p>At first glance, it looks like a big equipment soaker tub, but there&rsquo;s much more than meets the eye when it comes to Tech Sonic Services&rsquo; ultrasonic wash technology.</p>\r\n<p><a class=\"btn btn-default\" href=\"https://cleanasnew.com/case-studies/Scotford-Upgrader-Turnaround.pdf\" target=\"_blank\" rel=\"noopener\">Download Case Study </a></p>', null, null, 'Case Studies', 'jp', '2020-12-26 20:15:47', '2020-12-26 20:15:47');
INSERT INTO `reviews` VALUES ('388', 'ZdgtQSLRDz', '22222222', 'Alfa Laval', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\" Compabloc heat exchanger before and after cleaning.\" data-src=\"/assets/img/feedback/AlfaLaval.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Alfa Laval, Business Manager:</p>\r\n<p class=\"quotation\">As a test Ulrik had our small test decanter into their shop for a 24 hour turn around. The equipment was immaculately cleaned within the required time requested with very little concern to do so on <span class=\"cangc3\">C</span><span class=\"cangc4\">LEAN</span> <span class=\"cangc3\">A</span><span class=\"cangc4\">S</span> <span class=\"cangc3\">N</span><span class=\"cangc4\">EW</span><sup>&reg;</sup>&rsquo;s process.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'jp', '2020-12-26 20:24:58', '2020-12-26 20:24:58');
INSERT INTO `reviews` VALUES ('389', 'RjY7hZ1Vqe', '22222222', 'Exxon Mobil', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Like \'new\' heat exchanger.\" data-src=\"/assets/img/feedback/Exxon-heat-exchanger.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Exxon Mobil, Heat Team Lead:</p>\r\n<p class=\"quotation\">When we want our equipment &ldquo;really clean&rdquo; we send it to <span class=\"cangc3\">C</span><span class=\"cangc4\">LEAN</span> <span class=\"cangc3\">A</span><span class=\"cangc4\">S</span> <span class=\"cangc3\">N</span><span class=\"cangc4\">EW</span><sup>&reg;</sup>.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'jp', '2020-12-26 20:25:32', '2020-12-26 20:25:32');
INSERT INTO `reviews` VALUES ('390', '3IHSkFB29u', '22222222', 'Versa Integrity', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Cleans hard to reach areas in a heat exchanger.\" data-src=\"/assets/img/feedback/Versa-integrity.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Versa Integrity, Operations Manager:</p>\r\n<p class=\"quotation\">The exchanger was so clean our guys could not tell that the exchanger had ever been in service.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'jp', '2020-12-26 20:26:04', '2020-12-26 20:26:04');
INSERT INTO `reviews` VALUES ('391', 'Pk6FxVC5fj', '22222222', 'Dow Chemical', '<div class=\"col-4 float-left\"><img class=\"imimg-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean As New&reg; offers a faster cleaning, faster repair turnaround, and faster return to service.\" data-src=\"/assets/img/feedback/Dow.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Dow Chemical, Lead Turnaround Planner:</p>\r\n<p class=\"quotation\">Heard nothing but good things and we are in the process of planning our next outage. Promise if we can send it there to be cleaned, we will. Thanks again.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'jp', '2020-12-26 20:26:55', '2020-12-26 20:26:55');
INSERT INTO `reviews` VALUES ('392', '5ZaVWOyXNn', '22222222', 'Shell Sarnia', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean As New&reg; cleans compabloc heat exchangers in a fraction of the time required for hydroblasting.\" data-src=\"/assets/img/feedback/shell-sarina-compabloc.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Sarnia, Refinery Manager:</p>\r\n<p class=\"quotation\">The Refinery decided to test clean four Compabloc units using the new process proposed by Tech Sonic. With the clean Compablocs back in operation, the refinery client captured the throughput and heat transfer data rates to reveal an annual (bpd) recovery of US$4.27M.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'jp', '2020-12-26 20:27:22', '2020-12-26 20:27:22');
INSERT INTO `reviews` VALUES ('393', 'Tdsr8iqgOW', '22222222', 'Shell Scotford', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\" Cleans Tube Exchanger back to original specs.\" data-src=\"../assets/img/feedback/shell-scotford.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Scotford:</p>\r\n<p class=\"quotation\">We experienced excellent results with cleaning components for offsite repair/overhaul. 3rd party companies have never seen valves as clean as the ones coming from Scotford. Stainless steel bundles were cleaned better than expected and typically achieved IRIS quality. Bundles with other material composition were generally cleaned to Eddy Current quality. To achieve IRIS spec for these non-stainless items, some were rinsed with a 20Kpsi &amp; rotary lance.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'jp', '2020-12-26 20:27:53', '2020-12-26 20:27:53');
INSERT INTO `reviews` VALUES ('394', 'ku0J6orG1c', '22222222', 'Shell Moerdijk', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Like \'new\' rotating equipment.\" data-src=\"/assets/img/feedback/shell-moerdijk-rotator.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Moerdijk, Production Cleaning Manager:</p>\r\n<p class=\"quotation\">Tech Sonic Cleaning Systems are twice as fast at half the cost of traditional hydroblasting. We achieve thorough, complete cleaning that is non-destructive compared to conventional applications.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'jp', '2020-12-26 20:28:21', '2020-12-26 20:28:21');
INSERT INTO `reviews` VALUES ('395', 'VoiGD6RmEj', '22222222', 'Shell Global', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"A clean Shell Exchanger improves plant productivity.\" data-src=\"/assets/img/feedback/exchanger-bundles.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Global, Operations &amp; Technology Lead:</p>\r\n<p class=\"quotation\">This is a game changing technology in cleaning effectiveness. Exchanger bundles simply come out cleaner than when addressed with conventional technology. There is a better performance of the bundles on start-up, and these results are achieved in less cleaning time per bundle. This will allow us to be more aggressive in our turnaround schedules in the future. We typically see 70-80% recovery of performance with HP water jetting (caused by residual fouling on the shell side of the bundle). Now we see up to 100% of design heat transfer performance after start-up using the Tech Sonic applications.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'jp', '2020-12-26 20:29:49', '2020-12-26 20:29:49');
INSERT INTO `reviews` VALUES ('396', 'Ek36HWw0nK', '22222222', 'Suncor Energy', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic technology is efficient and effective at cleaning dirty scaffolding.\" data-src=\"/assets/img/feedback/suncor-scaffolding.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Suncor Energy:</p>\r\n<p class=\"quotation\">For every dollar we spend with Tech Sonic, we save four dollars in operational and cost efficiencies. We used to have to throw out most of the components as they could not be put back into production. Our capital costs have reduced dramatically.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'jp', '2020-12-26 20:30:23', '2020-12-26 20:30:23');
INSERT INTO `reviews` VALUES ('397', 'kfvsjpY5DM', '22222222', 'CF Industrie', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic systems can clean most exchangers for less than half of replacement cost.\" data-src=\"/assets/img/feedback/cf-industries-shell-exchanger.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From CF Industries, Production Manager:</p>\r\n<p class=\"quotation\">We achieved a 27% increase in production of Ammonia after Tech Sonic cleaned our C-109 process train heat exchangers.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'jp', '2020-12-26 20:30:53', '2020-12-26 20:30:53');
INSERT INTO `reviews` VALUES ('398', '0gpYVfsHub', '22222222', 'Cenovus Energy', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic systems can clean most packing materials for less than half of replacement cost. \" data-src=\"/assets/img/feedback/cenovus-energy-bucket-pool.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Cenovus Energy, Turnaround Manager:</p>\r\n<p class=\"quotation\">Our flow rates improved from 160 to 260 meters cubed per hour after using Tech Sonic&rsquo;s innovative cleaning technologies. Truly amazing.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'jp', '2020-12-26 20:31:20', '2020-12-26 20:31:20');
INSERT INTO `reviews` VALUES ('399', 'EWHeGkP4gy', '22222222', 'Nucor', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean heat exchangers require less energy and improves plant efficiencies.\" data-src=\"/assets/img/feedback/nucor.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Nucor:</p>\r\n<p class=\"quotation\">The heat exchanger that we cleaned Friday is operating very well. Overall heat transfer efficiency increased 50%.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'jp', '2020-12-26 20:31:54', '2020-12-26 20:31:54');
INSERT INTO `reviews` VALUES ('400', '8QhLp93xAn', '33333333', 'From Practice-To-Theory-To-Practice: Advances in the Cleaning of Heat Exchangers Using Ultrasound', '<h2>From Practice-To-Theory-To-Practice: Advances in the Cleaning of Heat Exchangers Using Ultrasound</h2>\r\n<p class=\"text-muted\">B. Kieser, A. Goode and R. Philion</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/51_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"A typical exchanger being removed from the ultrasonic bath for inspection and rinsing.\" data-src=\"https://cleanasnew.com/assets/img/tech-sonic-cleaning.jpg\" /></a></p>\r\n<p>The cleaning of heat exchangers, and other large process equipment, using large-scale ultrasonic systems has, in the past few years, progressed from a &ldquo;curiosity&rdquo; to a proven, practical tool for many clients.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/51_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'jp', '2020-12-26 20:38:46', '2020-12-26 20:38:46');
INSERT INTO `reviews` VALUES ('401', 'Rbw0omqBJ5', '33333333', 'Ultrasonic Cleaning of Badly Fouled Compabloc Exchangers', '<h2>Ultrasonic Cleaning of Badly Fouled Compabloc Exchangers</h2>\r\n<p class=\"text-muted\">R. Philion, B. Kieser and T. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/49_Kieser-PhilionSMCFL_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Compabloc exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-compablocs-cleaned.jpg\" /></a></p>\r\n<p>This paper describes the use of ultrasonic technologies to clean badly fouled Alfa Laval Compabloc&reg; 106 heat exchangers - which could not be effectively cleaned by conventional methods.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/49_Kieser-PhilionSMCFL_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'jp', '2020-12-26 20:40:06', '2020-12-26 20:40:06');
INSERT INTO `reviews` VALUES ('402', 'Wczml95w8J', '33333333', 'Application of Stabilized Hydrogen Peroxide for the Removal of Iron Polysulfide Deposits', '<h2>Application of Stabilized Hydrogen Peroxide for the Removal of Iron Polysulfide Deposits</h2>\r\n<p class=\"text-muted\">T.R. McCartney and R. Shank</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/47_McCartney_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-exchangers-cleaned2.jpg\" /></a></p>\r\n<p>Hydrogen Peroxide is a strong oxidizing agent that could be used for the effective removal of the iron sulfide species.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/47_McCartney_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'jp', '2020-12-26 20:45:46', '2020-12-26 20:45:46');
INSERT INTO `reviews` VALUES ('403', 'ireQIuqZ6B', '33333333', 'Understanding the Value of Cleaning - Gains From the Use of Ultrasonic Cleaning of Large Industrial Equipment', '<h2>Understanding the Value of Cleaning - Gains From the Use of Ultrasonic Cleaning of Large Industrial Equipment</h2>\r\n<p class=\"text-muted\">C. H. Schulz and B. Kieser</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2019/41_Schulz%20and%20Kieser.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" style=\"float: none;\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Example of soiling on Quench Oil Exchangers.\" data-src=\"https://cleanasnew.com/assets/img/dirty-heat-exchanger.jpg\" /></a></p>\r\n<p>We present the results of a study to determine the efficacy of large-scale ultrasonic cleaning of heat exchangers at Braskem.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2019/41_Schulz%20and%20Kieser.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'jp', '2020-12-26 20:46:29', '2020-12-26 20:46:29');
INSERT INTO `reviews` VALUES ('404', '4ZGDROmkBX', '33333333', 'The Effective Removal of Feco3 Deposits From Critical Process Heat Exchangers Used in Ammonia Production Using a Proprietary Ultrasonic Chemical Cleaning Method', '<h2>The Effective Removal of Feco3 Deposits From Critical Process Heat Exchangers Used in Ammonia Production Using a Proprietary Ultrasonic Chemical Cleaning Method</h2>\r\n<p class=\"text-muted\">R. Philion and T.R. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/50_Philion_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-exchangers-cleaned3.jpg\" /></a></p>\r\n<p>This paper examines the problem of ferrous carbonate fouling and removal in lean - rich Diethanol amine exchangers at the CO2 extraction plants associated with ammonia manufacturing.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/50_Philion_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'jp', '2020-12-26 20:47:36', '2020-12-26 20:47:36');
INSERT INTO `reviews` VALUES ('413', '11111111', '11111111', 'Removal Of Iron Carbonate Deposits From Heat Exchangers Used In Ammonia Production Using Tech Sonic Cleaning', '<h2><img style=\"height: auto;\" src=\"https://cleanasnew.com/assets/img/fouling.jpg\" alt=\"A sample of the FeCO3fouling removed from the tube side of the heat exchanger.\" data-src=\"https://cleanasnew.com/assets/img/fouling.jpg\" /></h2>\r\n            <p>The use of ultrasonic technology for cleaning refinery and chemical plant components has been undertaken by Tech Sonic since 2003.</p>', '', null, 'Case Studies', 'th', null, '2020-12-23 16:23:55');
INSERT INTO `reviews` VALUES ('414', '22222222', '22222222', 'Shell Deer Park', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../../assets/img/1px.png\" alt=\"Tech Sonic technology is efficient and effective at cleaning dirty heat exchanger. \" data-src=\"/assets/img/feedback/Shell-Deer-Park.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Deer Park, Turnaround Manager:</p>\r\n<p class=\"quotation\">I thought everything you guys did was awesome and have already been spreading recommendations to continue using you guys whenever we can!</p>\r\n</blockquote>\r\n</div>', '/assets/img/feedback/Shell-Deer-Park.jpg', 'Turnaround Manager', 'Feedback', 'th', null, '2020-12-26 20:24:26');
INSERT INTO `reviews` VALUES ('415', '33333333', '33333333', 'The Application of Industrial Scale Ultrasonic Cleaning to Heat Exchangers', '<h2>The Application of Industrial Scale Ultrasonic Cleaning to Heat Exchangers</h2>\r\n<p class=\"text-muted\">B. Kieser, R. Phillion and T. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Tube Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" /></a></p>\r\n<p>The cleaning of fouled heat exchangers often presents a significant challenge to the maintenance and operation of chemical, petroleum and food processes.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', 'assets/img/tech-sonic-vessel-sm.jpg', null, 'Referred Journal Publications', 'th', null, '2020-12-26 20:37:56');
INSERT INTO `reviews` VALUES ('416', 'o71Uy3sYSH', '11111111', 'Off-Line Ultrasonic Cleaning – Restoring As Built Performance During Turnarounds', '<p class=\"text-muted\" style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa; color: #6c757d !important;\">Gwen van de Bilt, Field Supervisor, Waste and Cleaning, Shell Moerdijk</p>\r\n<p><img class=\"img-fluid polaroid\" style=\"box-sizing: border-box; vertical-align: middle; border-style: none; max-width: 100%; height: auto; box-shadow: rgba(0, 0, 0, 0.2) 0px 4px 8px 0px, rgba(0, 0, 0, 0.19) 0px 6px 20px 0px; float: left; margin-bottom: 8.48438px; margin-left: 0px; font-family: Oswald, sans-serif; font-size: 16px; background-color: #f8f9fa;\" src=\"https://cleanasnew.com/img/before-and-after-filters-cleaned.jpg\" alt=\"Typical Wedge Wire filter for cleaning, multiple filters can be cleaned at the same time with only a low-pressure rinse.\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa;\">In April 2013 the Shell Moerdijk Chemicals plant conducted the biggest&ndash;ever TA event in the history of the site. One of the successes we had involved employing off-line ultrasonic cleaning.</p>', null, null, 'Case Studies', 'th', '2020-12-26 14:23:34', '2020-12-26 14:23:34');
INSERT INTO `reviews` VALUES ('417', 'c48qWa5AoR', '11111111', 'Tech Sonic Cleaning System Helps ConocoPhillips Neutralize and Clean Valves', '<h2>Tech Sonic Cleaning System Helps ConocoPhillips Neutralize<br />and Clean Valve</h2>\r\n<p class=\"text-muted\">&nbsp;</p>\r\n<p><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Monel Plug valve after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-valves-cleaned.jpg\" /></p>\r\n<p>ConocoPhillips is one of the largest refiners in the United States. Its Ponca City, Oklahoma refiner , with a crude oil processing capacity of 187,000 per day, processes a mixture of light, medium...</p>\r\n<p><a class=\"btn btn-default\" href=\"https://cleanasnew.com/case-studies/ConocoPhllips-Valve-Service.pdf\" target=\"_blank\" rel=\"noopener\">Download Case Study </a></p>', null, null, 'Case Studies', 'th', '2020-12-26 20:14:42', '2020-12-26 20:14:42');
INSERT INTO `reviews` VALUES ('418', 'GynfsMdkKN', '11111111', 'Upgrader Equipment Gets The Spa Treatment', '<h2>Upgrader Equipment Gets The Spa Treatment</h2>\r\n<p class=\"text-muted\">Tanya Ristoff, Communications Manager, Shell Scotford</p>\r\n<p><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Pictured here &ndash;an exchanger bundle is lowered into an ultrasonic bath by crane.\" data-src=\"https://cleanasnew.com/assets/img/scotford-upgrader-turnaround-case-study.jpg\" /></p>\r\n<p>At first glance, it looks like a big equipment soaker tub, but there&rsquo;s much more than meets the eye when it comes to Tech Sonic Services&rsquo; ultrasonic wash technology.</p>\r\n<p><a class=\"btn btn-default\" href=\"https://cleanasnew.com/case-studies/Scotford-Upgrader-Turnaround.pdf\" target=\"_blank\" rel=\"noopener\">Download Case Study </a></p>', null, null, 'Case Studies', 'th', '2020-12-26 20:15:47', '2020-12-26 20:15:47');
INSERT INTO `reviews` VALUES ('419', 'ZdgtQSLRDz', '22222222', 'Alfa Laval', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\" Compabloc heat exchanger before and after cleaning.\" data-src=\"/assets/img/feedback/AlfaLaval.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Alfa Laval, Business Manager:</p>\r\n<p class=\"quotation\">As a test Ulrik had our small test decanter into their shop for a 24 hour turn around. The equipment was immaculately cleaned within the required time requested with very little concern to do so on <span class=\"cangc3\">C</span><span class=\"cangc4\">LEAN</span> <span class=\"cangc3\">A</span><span class=\"cangc4\">S</span> <span class=\"cangc3\">N</span><span class=\"cangc4\">EW</span><sup>&reg;</sup>&rsquo;s process.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'th', '2020-12-26 20:24:58', '2020-12-26 20:24:58');
INSERT INTO `reviews` VALUES ('420', 'RjY7hZ1Vqe', '22222222', 'Exxon Mobil', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Like \'new\' heat exchanger.\" data-src=\"/assets/img/feedback/Exxon-heat-exchanger.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Exxon Mobil, Heat Team Lead:</p>\r\n<p class=\"quotation\">When we want our equipment &ldquo;really clean&rdquo; we send it to <span class=\"cangc3\">C</span><span class=\"cangc4\">LEAN</span> <span class=\"cangc3\">A</span><span class=\"cangc4\">S</span> <span class=\"cangc3\">N</span><span class=\"cangc4\">EW</span><sup>&reg;</sup>.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'th', '2020-12-26 20:25:32', '2020-12-26 20:25:32');
INSERT INTO `reviews` VALUES ('421', '3IHSkFB29u', '22222222', 'Versa Integrity', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Cleans hard to reach areas in a heat exchanger.\" data-src=\"/assets/img/feedback/Versa-integrity.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Versa Integrity, Operations Manager:</p>\r\n<p class=\"quotation\">The exchanger was so clean our guys could not tell that the exchanger had ever been in service.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'th', '2020-12-26 20:26:04', '2020-12-26 20:26:04');
INSERT INTO `reviews` VALUES ('422', 'Pk6FxVC5fj', '22222222', 'Dow Chemical', '<div class=\"col-4 float-left\"><img class=\"imimg-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean As New&reg; offers a faster cleaning, faster repair turnaround, and faster return to service.\" data-src=\"/assets/img/feedback/Dow.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Dow Chemical, Lead Turnaround Planner:</p>\r\n<p class=\"quotation\">Heard nothing but good things and we are in the process of planning our next outage. Promise if we can send it there to be cleaned, we will. Thanks again.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'th', '2020-12-26 20:26:55', '2020-12-26 20:26:55');
INSERT INTO `reviews` VALUES ('423', '5ZaVWOyXNn', '22222222', 'Shell Sarnia', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean As New&reg; cleans compabloc heat exchangers in a fraction of the time required for hydroblasting.\" data-src=\"/assets/img/feedback/shell-sarina-compabloc.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Sarnia, Refinery Manager:</p>\r\n<p class=\"quotation\">The Refinery decided to test clean four Compabloc units using the new process proposed by Tech Sonic. With the clean Compablocs back in operation, the refinery client captured the throughput and heat transfer data rates to reveal an annual (bpd) recovery of US$4.27M.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'th', '2020-12-26 20:27:22', '2020-12-26 20:27:22');
INSERT INTO `reviews` VALUES ('424', 'Tdsr8iqgOW', '22222222', 'Shell Scotford', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\" Cleans Tube Exchanger back to original specs.\" data-src=\"../assets/img/feedback/shell-scotford.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Scotford:</p>\r\n<p class=\"quotation\">We experienced excellent results with cleaning components for offsite repair/overhaul. 3rd party companies have never seen valves as clean as the ones coming from Scotford. Stainless steel bundles were cleaned better than expected and typically achieved IRIS quality. Bundles with other material composition were generally cleaned to Eddy Current quality. To achieve IRIS spec for these non-stainless items, some were rinsed with a 20Kpsi &amp; rotary lance.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'th', '2020-12-26 20:27:53', '2020-12-26 20:27:53');
INSERT INTO `reviews` VALUES ('425', 'ku0J6orG1c', '22222222', 'Shell Moerdijk', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Like \'new\' rotating equipment.\" data-src=\"/assets/img/feedback/shell-moerdijk-rotator.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Moerdijk, Production Cleaning Manager:</p>\r\n<p class=\"quotation\">Tech Sonic Cleaning Systems are twice as fast at half the cost of traditional hydroblasting. We achieve thorough, complete cleaning that is non-destructive compared to conventional applications.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'th', '2020-12-26 20:28:21', '2020-12-26 20:28:21');
INSERT INTO `reviews` VALUES ('426', 'VoiGD6RmEj', '22222222', 'Shell Global', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"A clean Shell Exchanger improves plant productivity.\" data-src=\"/assets/img/feedback/exchanger-bundles.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Global, Operations &amp; Technology Lead:</p>\r\n<p class=\"quotation\">This is a game changing technology in cleaning effectiveness. Exchanger bundles simply come out cleaner than when addressed with conventional technology. There is a better performance of the bundles on start-up, and these results are achieved in less cleaning time per bundle. This will allow us to be more aggressive in our turnaround schedules in the future. We typically see 70-80% recovery of performance with HP water jetting (caused by residual fouling on the shell side of the bundle). Now we see up to 100% of design heat transfer performance after start-up using the Tech Sonic applications.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'th', '2020-12-26 20:29:49', '2020-12-26 20:29:49');
INSERT INTO `reviews` VALUES ('427', 'Ek36HWw0nK', '22222222', 'Suncor Energy', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic technology is efficient and effective at cleaning dirty scaffolding.\" data-src=\"/assets/img/feedback/suncor-scaffolding.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Suncor Energy:</p>\r\n<p class=\"quotation\">For every dollar we spend with Tech Sonic, we save four dollars in operational and cost efficiencies. We used to have to throw out most of the components as they could not be put back into production. Our capital costs have reduced dramatically.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'th', '2020-12-26 20:30:23', '2020-12-26 20:30:23');
INSERT INTO `reviews` VALUES ('428', 'kfvsjpY5DM', '22222222', 'CF Industrie', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic systems can clean most exchangers for less than half of replacement cost.\" data-src=\"/assets/img/feedback/cf-industries-shell-exchanger.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From CF Industries, Production Manager:</p>\r\n<p class=\"quotation\">We achieved a 27% increase in production of Ammonia after Tech Sonic cleaned our C-109 process train heat exchangers.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'th', '2020-12-26 20:30:53', '2020-12-26 20:30:53');
INSERT INTO `reviews` VALUES ('429', '0gpYVfsHub', '22222222', 'Cenovus Energy', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic systems can clean most packing materials for less than half of replacement cost. \" data-src=\"/assets/img/feedback/cenovus-energy-bucket-pool.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Cenovus Energy, Turnaround Manager:</p>\r\n<p class=\"quotation\">Our flow rates improved from 160 to 260 meters cubed per hour after using Tech Sonic&rsquo;s innovative cleaning technologies. Truly amazing.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'th', '2020-12-26 20:31:20', '2020-12-26 20:31:20');
INSERT INTO `reviews` VALUES ('430', 'EWHeGkP4gy', '22222222', 'Nucor', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean heat exchangers require less energy and improves plant efficiencies.\" data-src=\"/assets/img/feedback/nucor.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Nucor:</p>\r\n<p class=\"quotation\">The heat exchanger that we cleaned Friday is operating very well. Overall heat transfer efficiency increased 50%.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'th', '2020-12-26 20:31:54', '2020-12-26 20:31:54');
INSERT INTO `reviews` VALUES ('431', '8QhLp93xAn', '33333333', 'From Practice-To-Theory-To-Practice: Advances in the Cleaning of Heat Exchangers Using Ultrasound', '<h2>From Practice-To-Theory-To-Practice: Advances in the Cleaning of Heat Exchangers Using Ultrasound</h2>\r\n<p class=\"text-muted\">B. Kieser, A. Goode and R. Philion</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/51_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"A typical exchanger being removed from the ultrasonic bath for inspection and rinsing.\" data-src=\"https://cleanasnew.com/assets/img/tech-sonic-cleaning.jpg\" /></a></p>\r\n<p>The cleaning of heat exchangers, and other large process equipment, using large-scale ultrasonic systems has, in the past few years, progressed from a &ldquo;curiosity&rdquo; to a proven, practical tool for many clients.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/51_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'th', '2020-12-26 20:38:46', '2020-12-26 20:38:46');
INSERT INTO `reviews` VALUES ('432', 'Rbw0omqBJ5', '33333333', 'Ultrasonic Cleaning of Badly Fouled Compabloc Exchangers', '<h2>Ultrasonic Cleaning of Badly Fouled Compabloc Exchangers</h2>\r\n<p class=\"text-muted\">R. Philion, B. Kieser and T. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/49_Kieser-PhilionSMCFL_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Compabloc exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-compablocs-cleaned.jpg\" /></a></p>\r\n<p>This paper describes the use of ultrasonic technologies to clean badly fouled Alfa Laval Compabloc&reg; 106 heat exchangers - which could not be effectively cleaned by conventional methods.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/49_Kieser-PhilionSMCFL_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'th', '2020-12-26 20:40:06', '2020-12-26 20:40:06');
INSERT INTO `reviews` VALUES ('433', 'Wczml95w8J', '33333333', 'Application of Stabilized Hydrogen Peroxide for the Removal of Iron Polysulfide Deposits', '<h2>Application of Stabilized Hydrogen Peroxide for the Removal of Iron Polysulfide Deposits</h2>\r\n<p class=\"text-muted\">T.R. McCartney and R. Shank</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/47_McCartney_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-exchangers-cleaned2.jpg\" /></a></p>\r\n<p>Hydrogen Peroxide is a strong oxidizing agent that could be used for the effective removal of the iron sulfide species.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/47_McCartney_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'th', '2020-12-26 20:45:46', '2020-12-26 20:45:46');
INSERT INTO `reviews` VALUES ('434', 'ireQIuqZ6B', '33333333', 'Understanding the Value of Cleaning - Gains From the Use of Ultrasonic Cleaning of Large Industrial Equipment', '<h2>Understanding the Value of Cleaning - Gains From the Use of Ultrasonic Cleaning of Large Industrial Equipment</h2>\r\n<p class=\"text-muted\">C. H. Schulz and B. Kieser</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2019/41_Schulz%20and%20Kieser.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" style=\"float: none;\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Example of soiling on Quench Oil Exchangers.\" data-src=\"https://cleanasnew.com/assets/img/dirty-heat-exchanger.jpg\" /></a></p>\r\n<p>We present the results of a study to determine the efficacy of large-scale ultrasonic cleaning of heat exchangers at Braskem.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2019/41_Schulz%20and%20Kieser.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'th', '2020-12-26 20:46:29', '2020-12-26 20:46:29');
INSERT INTO `reviews` VALUES ('435', '4ZGDROmkBX', '33333333', 'The Effective Removal of Feco3 Deposits From Critical Process Heat Exchangers Used in Ammonia Production Using a Proprietary Ultrasonic Chemical Cleaning Method', '<h2>The Effective Removal of Feco3 Deposits From Critical Process Heat Exchangers Used in Ammonia Production Using a Proprietary Ultrasonic Chemical Cleaning Method</h2>\r\n<p class=\"text-muted\">R. Philion and T.R. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/50_Philion_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-exchangers-cleaned3.jpg\" /></a></p>\r\n<p>This paper examines the problem of ferrous carbonate fouling and removal in lean - rich Diethanol amine exchangers at the CO2 extraction plants associated with ammonia manufacturing.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/50_Philion_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'th', '2020-12-26 20:47:36', '2020-12-26 20:47:36');
INSERT INTO `reviews` VALUES ('436', '22222222', '22222222', 'Shell Deer Park', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../../assets/img/1px.png\" alt=\"Tech Sonic technology is efficient and effective at cleaning dirty heat exchanger. \" data-src=\"/assets/img/feedback/Shell-Deer-Park.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">De Shell Deer Park, responsable du redressement:</p>\r\n<p class=\"quotation\">Je pensais que tout ce que vous faisiez était génial et j\'ai déjà diffusé des recommandations pour continuer à vous utiliser chaque fois que nous le pouvons!</p>\r\n</blockquote>\r\n</div>', '/assets/img/feedback/Shell-Deer-Park.jpg', 'Turnaround Manager', 'Feedback', 'fr', '0000-00-00 00:00:00', '0000-00-00 00:00:00');
INSERT INTO `reviews` VALUES ('444', '11111111', '11111111', 'Removal Of Iron Carbonate Deposits From Heat Exchangers Used In Ammonia Production Using Tech Sonic Cleaning', '<h2><img style=\"height: auto;\" src=\"https://cleanasnew.com/assets/img/fouling.jpg\" alt=\"A sample of the FeCO3fouling removed from the tube side of the heat exchanger.\" data-src=\"https://cleanasnew.com/assets/img/fouling.jpg\" /></h2>\r\n            <p>The use of ultrasonic technology for cleaning refinery and chemical plant components has been undertaken by Tech Sonic since 2003.</p>', '', null, 'Case Studies', 'ml', null, '2020-12-23 16:23:55');
INSERT INTO `reviews` VALUES ('445', '22222222', '22222222', 'Shell Deer Park', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../../assets/img/1px.png\" alt=\"Tech Sonic technology is efficient and effective at cleaning dirty heat exchanger. \" data-src=\"/assets/img/feedback/Shell-Deer-Park.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Deer Park, Turnaround Manager:</p>\r\n<p class=\"quotation\">I thought everything you guys did was awesome and have already been spreading recommendations to continue using you guys whenever we can!</p>\r\n</blockquote>\r\n</div>', '/assets/img/feedback/Shell-Deer-Park.jpg', 'Turnaround Manager', 'Feedback', 'ml', null, '2020-12-26 20:24:26');
INSERT INTO `reviews` VALUES ('446', '33333333', '33333333', 'The Application of Industrial Scale Ultrasonic Cleaning to Heat Exchangers', '<h2>The Application of Industrial Scale Ultrasonic Cleaning to Heat Exchangers</h2>\r\n<p class=\"text-muted\">B. Kieser, R. Phillion and T. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Tube Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-bundle-tubs-cleaned.jpg\" /></a></p>\r\n<p>The cleaning of fouled heat exchangers often presents a significant challenge to the maintenance and operation of chemical, petroleum and food processes.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/48_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', 'assets/img/tech-sonic-vessel-sm.jpg', null, 'Referred Journal Publications', 'ml', null, '2020-12-26 20:37:56');
INSERT INTO `reviews` VALUES ('447', 'o71Uy3sYSH', '11111111', 'Off-Line Ultrasonic Cleaning – Restoring As Built Performance During Turnarounds', '<p class=\"text-muted\" style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa; color: #6c757d !important;\">Gwen van de Bilt, Field Supervisor, Waste and Cleaning, Shell Moerdijk</p>\r\n<p><img class=\"img-fluid polaroid\" style=\"box-sizing: border-box; vertical-align: middle; border-style: none; max-width: 100%; height: auto; box-shadow: rgba(0, 0, 0, 0.2) 0px 4px 8px 0px, rgba(0, 0, 0, 0.19) 0px 6px 20px 0px; float: left; margin-bottom: 8.48438px; margin-left: 0px; font-family: Oswald, sans-serif; font-size: 16px; background-color: #f8f9fa;\" src=\"https://cleanasnew.com/img/before-and-after-filters-cleaned.jpg\" alt=\"Typical Wedge Wire filter for cleaning, multiple filters can be cleaned at the same time with only a low-pressure rinse.\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 0.8em; font-size: 20px; font-family: Oswald, sans-serif; background-color: #f8f9fa;\">In April 2013 the Shell Moerdijk Chemicals plant conducted the biggest&ndash;ever TA event in the history of the site. One of the successes we had involved employing off-line ultrasonic cleaning.</p>', null, null, 'Case Studies', 'ml', '2020-12-26 14:23:34', '2020-12-26 14:23:34');
INSERT INTO `reviews` VALUES ('448', 'c48qWa5AoR', '11111111', 'Tech Sonic Cleaning System Helps ConocoPhillips Neutralize and Clean Valves', '<h2>Tech Sonic Cleaning System Helps ConocoPhillips Neutralize<br />and Clean Valve</h2>\r\n<p class=\"text-muted\">&nbsp;</p>\r\n<p><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Monel Plug valve after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-valves-cleaned.jpg\" /></p>\r\n<p>ConocoPhillips is one of the largest refiners in the United States. Its Ponca City, Oklahoma refiner , with a crude oil processing capacity of 187,000 per day, processes a mixture of light, medium...</p>\r\n<p><a class=\"btn btn-default\" href=\"https://cleanasnew.com/case-studies/ConocoPhllips-Valve-Service.pdf\" target=\"_blank\" rel=\"noopener\">Download Case Study </a></p>', null, null, 'Case Studies', 'ml', '2020-12-26 20:14:42', '2020-12-26 20:14:42');
INSERT INTO `reviews` VALUES ('449', 'GynfsMdkKN', '11111111', 'Upgrader Equipment Gets The Spa Treatment', '<h2>Upgrader Equipment Gets The Spa Treatment</h2>\r\n<p class=\"text-muted\">Tanya Ristoff, Communications Manager, Shell Scotford</p>\r\n<p><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Pictured here &ndash;an exchanger bundle is lowered into an ultrasonic bath by crane.\" data-src=\"https://cleanasnew.com/assets/img/scotford-upgrader-turnaround-case-study.jpg\" /></p>\r\n<p>At first glance, it looks like a big equipment soaker tub, but there&rsquo;s much more than meets the eye when it comes to Tech Sonic Services&rsquo; ultrasonic wash technology.</p>\r\n<p><a class=\"btn btn-default\" href=\"https://cleanasnew.com/case-studies/Scotford-Upgrader-Turnaround.pdf\" target=\"_blank\" rel=\"noopener\">Download Case Study </a></p>', null, null, 'Case Studies', 'ml', '2020-12-26 20:15:47', '2020-12-26 20:15:47');
INSERT INTO `reviews` VALUES ('450', 'ZdgtQSLRDz', '22222222', 'Alfa Laval', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\" Compabloc heat exchanger before and after cleaning.\" data-src=\"/assets/img/feedback/AlfaLaval.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Alfa Laval, Business Manager:</p>\r\n<p class=\"quotation\">As a test Ulrik had our small test decanter into their shop for a 24 hour turn around. The equipment was immaculately cleaned within the required time requested with very little concern to do so on <span class=\"cangc3\">C</span><span class=\"cangc4\">LEAN</span> <span class=\"cangc3\">A</span><span class=\"cangc4\">S</span> <span class=\"cangc3\">N</span><span class=\"cangc4\">EW</span><sup>&reg;</sup>&rsquo;s process.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'ml', '2020-12-26 20:24:58', '2020-12-26 20:24:58');
INSERT INTO `reviews` VALUES ('451', 'RjY7hZ1Vqe', '22222222', 'Exxon Mobil', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Like \'new\' heat exchanger.\" data-src=\"/assets/img/feedback/Exxon-heat-exchanger.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Exxon Mobil, Heat Team Lead:</p>\r\n<p class=\"quotation\">When we want our equipment &ldquo;really clean&rdquo; we send it to <span class=\"cangc3\">C</span><span class=\"cangc4\">LEAN</span> <span class=\"cangc3\">A</span><span class=\"cangc4\">S</span> <span class=\"cangc3\">N</span><span class=\"cangc4\">EW</span><sup>&reg;</sup>.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'ml', '2020-12-26 20:25:32', '2020-12-26 20:25:32');
INSERT INTO `reviews` VALUES ('452', '3IHSkFB29u', '22222222', 'Versa Integrity', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Cleans hard to reach areas in a heat exchanger.\" data-src=\"/assets/img/feedback/Versa-integrity.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Versa Integrity, Operations Manager:</p>\r\n<p class=\"quotation\">The exchanger was so clean our guys could not tell that the exchanger had ever been in service.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'ml', '2020-12-26 20:26:04', '2020-12-26 20:26:04');
INSERT INTO `reviews` VALUES ('453', 'Pk6FxVC5fj', '22222222', 'Dow Chemical', '<div class=\"col-4 float-left\"><img class=\"imimg-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean As New&reg; offers a faster cleaning, faster repair turnaround, and faster return to service.\" data-src=\"/assets/img/feedback/Dow.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Dow Chemical, Lead Turnaround Planner:</p>\r\n<p class=\"quotation\">Heard nothing but good things and we are in the process of planning our next outage. Promise if we can send it there to be cleaned, we will. Thanks again.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'ml', '2020-12-26 20:26:55', '2020-12-26 20:26:55');
INSERT INTO `reviews` VALUES ('454', '5ZaVWOyXNn', '22222222', 'Shell Sarnia', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean As New&reg; cleans compabloc heat exchangers in a fraction of the time required for hydroblasting.\" data-src=\"/assets/img/feedback/shell-sarina-compabloc.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Sarnia, Refinery Manager:</p>\r\n<p class=\"quotation\">The Refinery decided to test clean four Compabloc units using the new process proposed by Tech Sonic. With the clean Compablocs back in operation, the refinery client captured the throughput and heat transfer data rates to reveal an annual (bpd) recovery of US$4.27M.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'ml', '2020-12-26 20:27:22', '2020-12-26 20:27:22');
INSERT INTO `reviews` VALUES ('455', 'Tdsr8iqgOW', '22222222', 'Shell Scotford', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\" Cleans Tube Exchanger back to original specs.\" data-src=\"../assets/img/feedback/shell-scotford.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Scotford:</p>\r\n<p class=\"quotation\">We experienced excellent results with cleaning components for offsite repair/overhaul. 3rd party companies have never seen valves as clean as the ones coming from Scotford. Stainless steel bundles were cleaned better than expected and typically achieved IRIS quality. Bundles with other material composition were generally cleaned to Eddy Current quality. To achieve IRIS spec for these non-stainless items, some were rinsed with a 20Kpsi &amp; rotary lance.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'ml', '2020-12-26 20:27:53', '2020-12-26 20:27:53');
INSERT INTO `reviews` VALUES ('456', 'ku0J6orG1c', '22222222', 'Shell Moerdijk', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Like \'new\' rotating equipment.\" data-src=\"/assets/img/feedback/shell-moerdijk-rotator.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Moerdijk, Production Cleaning Manager:</p>\r\n<p class=\"quotation\">Tech Sonic Cleaning Systems are twice as fast at half the cost of traditional hydroblasting. We achieve thorough, complete cleaning that is non-destructive compared to conventional applications.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'ml', '2020-12-26 20:28:21', '2020-12-26 20:28:21');
INSERT INTO `reviews` VALUES ('457', 'VoiGD6RmEj', '22222222', 'Shell Global', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"A clean Shell Exchanger improves plant productivity.\" data-src=\"/assets/img/feedback/exchanger-bundles.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Shell Global, Operations &amp; Technology Lead:</p>\r\n<p class=\"quotation\">This is a game changing technology in cleaning effectiveness. Exchanger bundles simply come out cleaner than when addressed with conventional technology. There is a better performance of the bundles on start-up, and these results are achieved in less cleaning time per bundle. This will allow us to be more aggressive in our turnaround schedules in the future. We typically see 70-80% recovery of performance with HP water jetting (caused by residual fouling on the shell side of the bundle). Now we see up to 100% of design heat transfer performance after start-up using the Tech Sonic applications.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'ml', '2020-12-26 20:29:49', '2020-12-26 20:29:49');
INSERT INTO `reviews` VALUES ('458', 'Ek36HWw0nK', '22222222', 'Suncor Energy', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic technology is efficient and effective at cleaning dirty scaffolding.\" data-src=\"/assets/img/feedback/suncor-scaffolding.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Suncor Energy:</p>\r\n<p class=\"quotation\">For every dollar we spend with Tech Sonic, we save four dollars in operational and cost efficiencies. We used to have to throw out most of the components as they could not be put back into production. Our capital costs have reduced dramatically.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'ml', '2020-12-26 20:30:23', '2020-12-26 20:30:23');
INSERT INTO `reviews` VALUES ('459', 'kfvsjpY5DM', '22222222', 'CF Industrie', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic systems can clean most exchangers for less than half of replacement cost.\" data-src=\"/assets/img/feedback/cf-industries-shell-exchanger.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From CF Industries, Production Manager:</p>\r\n<p class=\"quotation\">We achieved a 27% increase in production of Ammonia after Tech Sonic cleaned our C-109 process train heat exchangers.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'ml', '2020-12-26 20:30:53', '2020-12-26 20:30:53');
INSERT INTO `reviews` VALUES ('460', '0gpYVfsHub', '22222222', 'Cenovus Energy', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Tech Sonic systems can clean most packing materials for less than half of replacement cost. \" data-src=\"/assets/img/feedback/cenovus-energy-bucket-pool.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Cenovus Energy, Turnaround Manager:</p>\r\n<p class=\"quotation\">Our flow rates improved from 160 to 260 meters cubed per hour after using Tech Sonic&rsquo;s innovative cleaning technologies. Truly amazing.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'ml', '2020-12-26 20:31:20', '2020-12-26 20:31:20');
INSERT INTO `reviews` VALUES ('461', 'EWHeGkP4gy', '22222222', 'Nucor', '<div class=\"col-4 float-left\"><img class=\"img-fluid largImg lazyload\" src=\"../../assets/img/1px.png\" alt=\"Clean heat exchangers require less energy and improves plant efficiencies.\" data-src=\"/assets/img/feedback/nucor.jpg\" /></div>\r\n<div class=\"col-8 float-left\">\r\n<blockquote>\r\n<p class=\"text-md-center text-muted\">From Nucor:</p>\r\n<p class=\"quotation\">The heat exchanger that we cleaned Friday is operating very well. Overall heat transfer efficiency increased 50%.</p>\r\n</blockquote>\r\n</div>', null, null, 'Feedback', 'ml', '2020-12-26 20:31:54', '2020-12-26 20:31:54');
INSERT INTO `reviews` VALUES ('462', '8QhLp93xAn', '33333333', 'From Practice-To-Theory-To-Practice: Advances in the Cleaning of Heat Exchangers Using Ultrasound', '<h2>From Practice-To-Theory-To-Practice: Advances in the Cleaning of Heat Exchangers Using Ultrasound</h2>\r\n<p class=\"text-muted\">B. Kieser, A. Goode and R. Philion</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/51_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"A typical exchanger being removed from the ultrasonic bath for inspection and rinsing.\" data-src=\"https://cleanasnew.com/assets/img/tech-sonic-cleaning.jpg\" /></a></p>\r\n<p>The cleaning of heat exchangers, and other large process equipment, using large-scale ultrasonic systems has, in the past few years, progressed from a &ldquo;curiosity&rdquo; to a proven, practical tool for many clients.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/51_Kieser_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'ml', '2020-12-26 20:38:46', '2020-12-26 20:38:46');
INSERT INTO `reviews` VALUES ('463', 'Rbw0omqBJ5', '33333333', 'Ultrasonic Cleaning of Badly Fouled Compabloc Exchangers', '<h2>Ultrasonic Cleaning of Badly Fouled Compabloc Exchangers</h2>\r\n<p class=\"text-muted\">R. Philion, B. Kieser and T. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/49_Kieser-PhilionSMCFL_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Compabloc exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-compablocs-cleaned.jpg\" /></a></p>\r\n<p>This paper describes the use of ultrasonic technologies to clean badly fouled Alfa Laval Compabloc&reg; 106 heat exchangers - which could not be effectively cleaned by conventional methods.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2011/49_Kieser-PhilionSMCFL_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'ml', '2020-12-26 20:40:06', '2020-12-26 20:40:06');
INSERT INTO `reviews` VALUES ('464', 'Wczml95w8J', '33333333', 'Application of Stabilized Hydrogen Peroxide for the Removal of Iron Polysulfide Deposits', '<h2>Application of Stabilized Hydrogen Peroxide for the Removal of Iron Polysulfide Deposits</h2>\r\n<p class=\"text-muted\">T.R. McCartney and R. Shank</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/47_McCartney_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-exchangers-cleaned2.jpg\" /></a></p>\r\n<p>Hydrogen Peroxide is a strong oxidizing agent that could be used for the effective removal of the iron sulfide species.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/47_McCartney_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'ml', '2020-12-26 20:45:46', '2020-12-26 20:45:46');
INSERT INTO `reviews` VALUES ('465', 'ireQIuqZ6B', '33333333', 'Understanding the Value of Cleaning - Gains From the Use of Ultrasonic Cleaning of Large Industrial Equipment', '<h2>Understanding the Value of Cleaning - Gains From the Use of Ultrasonic Cleaning of Large Industrial Equipment</h2>\r\n<p class=\"text-muted\">C. H. Schulz and B. Kieser</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2019/41_Schulz%20and%20Kieser.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"> <img class=\"img-fluid polaroid lazyload\" style=\"float: none;\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Example of soiling on Quench Oil Exchangers.\" data-src=\"https://cleanasnew.com/assets/img/dirty-heat-exchanger.jpg\" /></a></p>\r\n<p>We present the results of a study to determine the efficacy of large-scale ultrasonic cleaning of heat exchangers at Braskem.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2019/41_Schulz%20and%20Kieser.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'ml', '2020-12-26 20:46:29', '2020-12-26 20:46:29');
INSERT INTO `reviews` VALUES ('466', '4ZGDROmkBX', '33333333', 'The Effective Removal of Feco3 Deposits From Critical Process Heat Exchangers Used in Ammonia Production Using a Proprietary Ultrasonic Chemical Cleaning Method', '<h2>The Effective Removal of Feco3 Deposits From Critical Process Heat Exchangers Used in Ammonia Production Using a Proprietary Ultrasonic Chemical Cleaning Method</h2>\r\n<p class=\"text-muted\">R. Philion and T.R. McCartney</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/50_Philion_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\"><img class=\"img-fluid polaroid lazyload\" src=\"https://cleanasnew.com/assets/img/1px.png\" alt=\"Exchanger before and after cleaning.\" data-src=\"https://cleanasnew.com/assets/img/before-and-after-exchangers-cleaned3.jpg\" /></a></p>\r\n<p>This paper examines the problem of ferrous carbonate fouling and removal in lean - rich Diethanol amine exchangers at the CO2 extraction plants associated with ammonia manufacturing.</p>\r\n<p><a href=\"http://www.heatexchanger-fouling.com/papers/papers2013/50_Philion_F.pdf\" target=\"_blank\" rel=\"noopener noreferrer nofollow\">Read more &rarr;</a></p>', null, null, 'Referred Journal Publications', 'ml', '2020-12-26 20:47:36', '2020-12-26 20:47:36');
